import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";

export default function Express() {
  const history = useHistory();
  const API_URL = "http://localhost:5000/api/express";
  const USER_API_URL = "http://localhost:5000/api/users";

  const [searchTerm, setSearchTerm] = useState("");
  const [expressList, setExpressList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchExpressForUser();
  }, []);

  const fetchExpressForUser = async () => {
    try {
      const userId = localStorage.getItem("userId");
      if (!userId) {
        setError("User not logged in.");
        setLoading(false);
        return;
      }

      // fetch user data
      const userResponse = await fetch(`${USER_API_URL}/${userId}`);
      if (!userResponse.ok) throw new Error("Failed to fetch user data");

      const userData = await userResponse.json();

      // fetch only the express for that user
      const response = await fetch(`${API_URL}/${userData.expressId}`);
      if (!response.ok) throw new Error("Failed to fetch express data");

      const expressData = await response.json();
      setExpressList([expressData]); // single express
    } catch (error) {
      console.error("Error fetching express:", error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleAddExpress = () => {
    history.push("/admin/express/add");
  };

  const filteredExpresses = expressList.filter((express) =>
    express.expressName.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <>
      <div className="flex justify-between items-center mb-6">
        <input
          type="text"
          placeholder="Search express..."
          className="border rounded-lg px-3 py-2 w-1/3"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button
          onClick={handleAddExpress}
          className="bg-blue-600 text-white px-4 py-2 rounded-lg"
        >
          Add Express
        </button>
      </div>

      {loading ? (
        <p>Loading express...</p>
      ) : error ? (
        <p className="text-red-500">{error}</p>
      ) : filteredExpresses.length === 0 ? (
        <p>No express found</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredExpresses.map((express) => (
            <div
              key={express.id}
              className="border rounded-lg shadow-md p-4 flex items-center gap-4"
            >
              <img
                src={`http://localhost:5000/uploads/${express.expressLogo}`}
                alt={express.expressName}
                className="h-12 w-12 object-cover rounded"
              />
              <div>
                <h2 className="font-semibold">{express.expressName}</h2>
                <p className="text-gray-600">{express.description}</p>
              </div>
            </div>
          ))}
        </div>
      )}
    </>
  );
}
