import React from "react";
import Chart from "chart.js";

export default function CardLineChart({ chartData, filters }) {
  const [isLoading, setIsLoading] = React.useState(false);
  const [error, setError] = React.useState(null);

  React.useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);
      setError(null);

      try {
        let url = '/api/reports/date-range';
        const params = new URLSearchParams();

        if (filters?.startDate) {
          params.append('start', filters.startDate.getTime());
        }

        if (filters?.endDate) {
          params.append('end', filters.endDate.getTime());
        }

        if (filters?.destination) {
          params.append('destination', filters.destination);
        }

        if (params.toString()) {
          url += `?${params.toString()}`;
        }

        const response = await fetch(url, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`,
            'Content-Type': 'application/json'
          }
        });

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const tickets = await response.json();

        // Process data for line chart (revenue)
        const revenueByDay = processRevenueData(tickets);

        const dataToUse = {
          labels: revenueByDay.labels,
          datasets: [
            {
              label: "Revenue (RWF)",
              backgroundColor: "rgba(59,130,246,0.1)",
              borderColor: "rgb(59,130,246)",
              data: revenueByDay.data,
              fill: true,
              tension: 0.4,
            },
          ],
        };

        renderChart(dataToUse);
      } catch (err) {
        console.error("Error fetching chart data:", err);
        setError(err.message);
        renderChart(getDefaultData());
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, [filters]);

  const processRevenueData = (tickets) => {
    // Group tickets by day and calculate total revenue
    const revenueByDay = {};

    tickets.forEach(ticket => {
      if (ticket.paid) {
        const date = new Date(ticket.createdAt).toLocaleDateString();
        if (!revenueByDay[date]) {
          revenueByDay[date] = 0;
        }
        revenueByDay[date] += ticket.amount || 0;
      }
    });

    // Convert to arrays for chart
    const labels = Object.keys(revenueByDay);
    const data = Object.values(revenueByDay);

    return { labels, data };
  };

  const getDefaultData = () => {
    return {
      labels: ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"],
      datasets: [
        {
          label: "Revenue (RWF)",
          backgroundColor: "rgba(59,130,246,0.1)",
          borderColor: "rgb(59,130,246)",
          data: [3000, 4500, 2500, 5000, 4000, 6000, 7000],
          fill: true,
          tension: 0.4,
        },
      ],
    };
  };

  const renderChart = (data) => {
    var config = {
      type: "line",
      data: data,
      options: {
        maintainAspectRatio: false,
        responsive: true,
        title: {
          display: false,
          text: "Revenue Overview",
          fontColor: "white",
        },
        legend: {
          labels: {
            fontColor: "white",
          },
          align: "end",
          position: "bottom",
        },
        tooltips: {
          mode: "index",
          intersect: false,
        },
        hover: {
          mode: "nearest",
          intersect: true,
        },
        scales: {
          xAxes: [
            {
              ticks: {
                fontColor: "rgba(255,255,255,.7)",
              },
              display: true,
              scaleLabel: {
                display: false,
                labelString: "Date",
                fontColor: "white",
              },
              gridLines: {
                display: false,
                borderDash: [2],
                borderDashOffset: [2],
                color: "rgba(33, 37, 41, 0.3)",
                zeroLineColor: "rgba(0, 0, 0, 0)",
                zeroLineBorderDash: [2],
                zeroLineBorderDashOffset: [2],
              },
            },
          ],
          yAxes: [
            {
              ticks: {
                fontColor: "rgba(255,255,255,.7)",
                beginAtZero: true,
                precision: 0
              },
              display: true,
              scaleLabel: {
                display: false,
                labelString: "Revenue (RWF)",
                fontColor: "white",
              },
              gridLines: {
                borderDash: [3],
                borderDashOffset: [3],
                drawBorder: false,
                color: "rgba(255, 255, 255, 0.15)",
                zeroLineColor: "rgba(33, 37, 41, 0)",
                zeroLineBorderDash: [2],
                zeroLineBorderDashOffset: [2],
              },
            },
          ],
        },
      },
    };

    var ctx = document.getElementById("line-chart").getContext("2d");

    if (window.myLine) {
      window.myLine.destroy();
    }

    window.myLine = new Chart(ctx, config);
  };

  return (
    <>
      <div className="relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded bg-blueGray-700">
        <div className="rounded-t mb-0 px-4 py-3 bg-transparent">
          <div className="flex flex-wrap items-center">
            <div className="relative w-full max-w-full flex-grow flex-1">
              <h6 className="uppercase text-blueGray-100 mb-1 text-xs font-semibold">
                Overview
              </h6>
              <h2 className="text-white text-xl font-semibold">Revenue Overview</h2>
            </div>
          </div>
        </div>
        <div className="p-4 flex-auto">
          {isLoading && (
            <div className="text-center text-white py-4">Loading data...</div>
          )}
          {error && (
            <div className="text-center text-red-300 py-4">Error: {error}</div>
          )}
          <div className="relative h-350-px">
            <canvas id="line-chart"></canvas>
          </div>
        </div>
      </div>
    </>
  );
}
