import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import jsPDF from "jspdf";
import autoTable from "jspdf-autotable";
import JsBarcode from "jsbarcode";

export default function TicketAdd() {
  const history = useHistory();

  const [ticketData, setTicketData] = useState({
    names: "",
    email: "",
    phone: "",
    express: "",
    schedule: "",
    busPlateNo: "",
    seatNo: "",
    totalAmount: 0,
    payState: "PENDING",
    date: "",
  });

  const [expressList, setExpressList] = useState([]);
  const [scheduleList, setScheduleList] = useState([]);
  const [busList, setBusList] = useState([]);
  const [role, setRole] = useState("");
  const [existingTickets, setExistingTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [availableSeats, setAvailableSeats] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem("token");
        const expressId = localStorage.getItem("expressId");

        let userRole = "";
        const userObj = localStorage.getItem("user");
        if (userObj) {
          const user = JSON.parse(userObj);
          userRole = user.role ? user.role.toUpperCase() : "";
        } else {
          userRole = localStorage.getItem("role") ? localStorage.getItem("role").toUpperCase() : "";
        }
        setRole(userRole);

        const expressResponse = await fetch("http://localhost:5000/api/express/all", {
          method: "GET",
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!expressResponse.ok) throw new Error("Cannot fetch express list");
        const expresses = await expressResponse.json();
        setExpressList(expresses);

        const scheduleResponse = await fetch("http://localhost:5000/api/tickschedule", {
          method: "GET",
          headers: { Authorization: `Bearer ${token}`, "X-Express-Id": expressId || "" },
        });
        if (!scheduleResponse.ok) throw new Error("Cannot fetch schedule list");
        const schedules = await scheduleResponse.json();

        const today = new Date();
        const filteredSchedules = schedules.filter((sch) => {
          const scheduleDate = new Date(sch.date);
          const isNotExpired = scheduleDate >= today;
          const matchesExpress = userRole === "SUPER_ADMIN" ? true : sch.express.id === expressId;
          return isNotExpired && matchesExpress && sch.express;
        });

        const finalSchedules = userRole !== "SUPER_ADMIN" && expressId
          ? filteredSchedules.filter((sch) => sch.express.id === expressId)
          : filteredSchedules;
        setScheduleList(finalSchedules);

        const busResponse = await fetch("http://localhost:5000/api/tickets/bus", {
          method: "GET",
          headers: { Authorization: `Bearer ${token}`, "X-Express-Id": expressId || "" },
        });
        if (!busResponse.ok) throw new Error("Cannot fetch breakage");
        setBusList(await busResponse.json());

        const ticketsResponse = await fetch("http://localhost:5000/api/tickets/all", {
          method: "GET",
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!ticketsResponse.ok) throw new Error("Cannot fetch existing tickets");
        setExistingTickets(await ticketsResponse.json());

        if (userRole !== "SUPER_ADMIN" && expressId) {
          setTicketData((prev) => ({ ...prev, express: expressId }));
        }

        setLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
        setLoading(false);
        alert(error.message);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (!ticketData.schedule) return;

    const selectedSchedule = scheduleList.find((s) => s.id === ticketData.schedule);
    if (!selectedSchedule || !selectedSchedule.bus) return;

    const bookedSeats = existingTickets
      .filter((t) => t.schedule.id === ticketData.schedule)
      .map((t) => t.seatNo);

    const remainingSeats = selectedSchedule.bus.busSize - bookedSeats.length;
    setAvailableSeats(remainingSeats);

    const nextSeat = [];
    for (let i = 1; i <= selectedSchedule.bus.busSize; i++) {
      if (!bookedSeats.includes(i)) {
        nextSeat.push(i);
      }
    }
    setTicketData((prev) => ({
      ...prev,
      seatNo: nextSeat.length > 0 ? nextSeat[0] : "",
      busPlateNo: selectedSchedule.bus.plateNo,
      totalAmount: selectedSchedule.destination.price || 0,
    }));
  }, [ticketData.schedule, scheduleList, existingTickets]);

  useEffect(() => {
    if (role !== "SUPER_ADMIN" || !ticketData.express) return;

    const fetchSchedulesForExpress = async () => {
      try {
        const token = localStorage.getItem("token");
        const scheduleResponse = await fetch("http://localhost:5000/api/tickets/schedule", {
          method: "GET",
          headers: { Authorization: `Bearer ${token}`, "X-Express-Id": ticketData.express },
        });
        if (!scheduleResponse.ok) throw new Error("Cannot fetch schedules for express");
        const schedules = await scheduleResponse.json();

        const today = new Date();
        const filteredSchedules = schedules.filter((sch) => {
          const scheduleDate = new Date(sch.date);
          return scheduleDate >= today && sch.express.id === ticketData.express && sch.express;
        });
        setScheduleList(filteredSchedules);
      } catch (error) {
        console.error("Error fetching schedules:", error);
        alert(error.message);
      }
    };

    fetchSchedulesForExpress();
  }, [ticketData.express, role]);

  const generateBarcodeImage = (ticketNumber) => {
    try {
      const canvas = document.createElement('canvas');
      JsBarcode(canvas, ticketNumber, {
        format: "CODE128",
        displayValue: true,
        fontSize: 14,
        background: "#ffffff",
        lineColor: "#000000",
        width: 2,
        height: 40,
        margin: 10
      });
      return canvas.toDataURL('image/png');
    } catch (error) {
      console.error("Barcode generation failed:", error);
      return null;
    }
  };

  const generatePDF = (ticket) => {
    const selectedSchedule = scheduleList.find((s) => s.id === ticket.schedule.id);
    const selectedExpress = expressList.find((e) => e.id === ticket.express.id);

    if (!selectedSchedule || !selectedExpress) {
      console.error("Missing schedule or express data:", { selectedSchedule, selectedExpress });
      alert("Cannot generate PDF: Missing schedule or express data.");
      return;
    }

    const logoUrl = `http://localhost:5000/uploads/${selectedExpress.expressLogo}`;
    const doc = new jsPDF();

    // Header with gradient
    const gradient = doc.createLinearGradient(0, 0, 210, 0);
    gradient.addColorStop(0, '#1e3a8a');
    gradient.addColorStop(1, '#3b82f6');
    doc.setFillColor(gradient);
    doc.rect(0, 0, 210, 50, 'F');

    // Add decorative wave
    doc.setFillColor(255, 255, 255);
    doc.path([
      { op: 'm', c: [0, 50] },
      { op: 'c', c: [70, 30, 140, 70, 210, 50] },
      { op: 'l', c: [210, 60] },
      { op: 'l', c: [0, 60] },
      { op: 'h' }
    ], 'F');

    // Company branding
    doc.setFontSize(30);
    doc.setFont("helvetica", "bold");
    doc.setTextColor(255, 255, 255);
    doc.text("TEGABUS", 20, 30);
    
    doc.setFontSize(12);
    doc.setFont("helvetica", "normal");
    doc.setTextColor(255, 255, 255, 0.9);
    doc.text("Luxury Travel Solutions", 20, 40);

    // Logo
    try {
      doc.setFillColor(255, 255, 255);
      doc.circle(180, 25, 15, 'F');
      doc.addImage(logoUrl, "PNG", 165, 10, 30, 30, undefined, 'FAST');
    } catch (error) {
      console.error("Error adding logo:", error);
      doc.setFontSize(10);
      doc.setTextColor(100, 100, 100);
      doc.text("Logo unavailable", 180, 25, { align: "center" });
    }

    // Ticket container
    doc.setFillColor(255, 255, 255);
    doc.setDrawColor(220, 220, 220);
    doc.roundedRect(10, 70, 190, 180, 8, 8, 'FD');

    // Ticket title
    doc.setFontSize(24);
    doc.setTextColor(17, 24, 39);
    doc.setFont("helvetica", "bold");
    doc.text("E-TICKET", 105, 90, { align: "center" });

    // Barcode
    const barcodeDataURL = generateBarcodeImage(ticket.ticketNumber || "TICKET-" + Date.now());
    if (barcodeDataURL) {
      doc.addImage(barcodeDataURL, 'PNG', 65, 100, 80, 30);
    } else {
      doc.setFontSize(10);
      doc.setTextColor(255, 0, 0);
      doc.text("Barcode generation failed", 105, 115, { align: "center" });
    }

    // Ticket details tables
    const tableConfig = {
      startY: 140,
      margin: { left: 20, right: 20 },
      styles: { 
        fontSize: 10, 
        cellPadding: 4,
        textColor: [17, 24, 39]
      },
      headStyles: { 
        fillColor: [59, 130, 246], 
        textColor: [255, 255, 255],
        fontStyle: 'bold'
      },
      columnStyles: { 
        0: { cellWidth: 50, fontStyle: 'bold', textColor: [75, 85, 99] }, 
        1: { cellWidth: 100 }
      }
    };

    autoTable(doc, {
      ...tableConfig,
      head: [['Passenger Details']],
      body: [
        ["Name", ticket.names || "-"],
        ["Phone", ticket.phone || "-"],
        ["Email", ticket.email || "-"]
      ]
    });

    autoTable(doc, {
      ...tableConfig,
      head: [['Journey Details']],
      body: [
        ["Route", `${selectedSchedule.destination.fromLocation} → ${selectedSchedule.destination.toLocation}`],
        ["Date", ticket.date ? new Date(ticket.date).toLocaleDateString() : "-"],
        ["Time", selectedSchedule.time || "-"],
        ["Bus", ticket.busPlateNo || "-"],
        ["Seat", ticket.seatNo || "-"]
      ]
    });

    autoTable(doc, {
      ...tableConfig,
      head: [['Payment Details']],
      body: [
        ["Fare", `$${ticket.totalAmount || 0}`],
        ["Status", ticket.payState || "PENDING"],
        ["Booked", new Date().toLocaleDateString()]
      ]
    });

    // Footer
    doc.setFillColor(17, 24, 39);
    doc.rect(0, 260, 210, 20, 'F');
    doc.setFontSize(8);
    doc.setTextColor(255, 255, 255);
    doc.text("Please present this ticket during boarding | Non-transferable | Contact: support@tegabus.com", 105, 270, { align: "center" });

    // Save and print
    doc.save(`ticket_${ticket.ticketNumber || "unknown"}.pdf`);
    const pdfOutput = doc.output("bloburl");
    const printWindow = window.open(pdfOutput);
    if (printWindow) {
      printWindow.onload = () => {
        printWindow.print();
        setTimeout(() => printWindow.close(), 500);
      };
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTicketData({ ...ticketData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!ticketData.seatNo) {
      return alert("No seats available for the selected schedule.");
    }

    try {
      const token = localStorage.getItem("token");
      const payload = {
        ...ticketData,
        express: role === "SUPER_ADMIN" ? { id: ticketData.express } : { id: ticketData.express },
        schedule: { id: ticketData.schedule },
      };

      const response = await fetch("http://localhost:5000/api/tickets/register", {
        method: "POST",
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify(payload),
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(errorText || "Failed to save ticket");
      }

      const savedTicket = await response.json();
      alert("Ticket added successfully!");
      generatePDF(savedTicket);
      history.push("/admin/tickets");
    } catch (error) {
      console.error(error);
      alert("Error saving ticket: " + error.message);
    }
  };

  const handleCancel = () => history.push("/admin/tickets");

  if (loading) return (
    <div className="flex items-center justify-center min-h-screen">
      <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-100 py-8 px-4 sm:px-6 lg:px-8">
      <div className="max-w-4xl mx-auto">
        <div className="bg-white shadow-2xl rounded-2xl overflow-hidden">
          <div className="bg-gradient-to-r from-blue-600 to-blue-800 px-6 py-4 flex justify-between items-center">
            <h2 className="text-2xl font-bold text-white">Add New Ticket</h2>
            <button
              onClick={handleCancel}
              className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition-colors"
            >
              Cancel
            </button>
          </div>

          <form onSubmit={handleSubmit} className="p-6 space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Passenger Name *</label>
                <input
                  type="text"
                  name="names"
                  value={ticketData.names}
                  onChange={handleInputChange}
                  required
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Email</label>
                <input
                  type="email"
                  name="email"
                  value={ticketData.email}
                  onChange={handleInputChange}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Phone *</label>
                <input
                  type="text"
                  name="phone"
                  value={ticketData.phone}
                  onChange={handleInputChange}
                  required
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>

              {role === "SUPER_ADMIN" && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Express *</label>
                  <select
                    name="express"
                    value={ticketData.express}
                    onChange={handleInputChange}
                    required
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                  >
                    <option value="">Select Express</option>
                    {expressList.map((exp) => (
                      <option key={exp.id} value={exp.id}>{exp.expressName}</option>
                    ))}
                  </select>
                </div>
              )}

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Schedule *</label>
                <select
                  name="schedule"
                  value={ticketData.schedule}
                  onChange={handleInputChange}
                  required
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                >
                  <option value="">Select Schedule</option>
                  {scheduleList.map((sch) => (
                    <option key={sch.id} value={sch.id}>
                      {sch.destination.fromLocation} - {sch.destination.toLocation} ({sch.date} {sch.time})
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Bus Plate Number *</label>
                <input
                  type="text"
                  name="busPlateNo"
                  value={ticketData.busPlateNo}
                  readOnly
                  className="w-full px-4 py-2 border border-gray-200 rounded-lg bg-gray-50 cursor-not-allowed"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Seat Number * (Available: {availableSeats})
                </label>
                <input
                  type="number"
                  name="seatNo"
                  value={ticketData.seatNo || ""}
                  readOnly
                  className="w-full px-4 py-2 border border-gray-200 rounded-lg bg-gray-50 cursor-not-allowed"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Total Amount *</label>
                <input
                  type="number"
                  name="totalAmount"
                  value={ticketData.totalAmount}
                  readOnly
                  className="w-full px-4 py-2 border border-gray-200 rounded-lg bg-gray-50 cursor-not-allowed"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Payment Status *</label>
                <select
                  name="payState"
                  value={ticketData.payState}
                  onChange={handleInputChange}
                  required
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                >
                  <option value="PENDING">Pending</option>
                  <option value="PAID">Paid</option>
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Date *</label>
                <input
                  type="date"
                  name="date"
                  value={ticketData.date}
                  onChange={handleInputChange}
                  required
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>
            </div>

            <div className="flex justify-end space-x-4 pt-6 border-t border-gray-200">
              <button
                type="button"
                onClick={handleCancel}
                className="px-6 py-2 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-50 transition-colors"
              >
                Cancel
              </button>
              <button
                type="submit"
                className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors disabled:bg-gray-400"
                disabled={availableSeats === 0}
              >
                Save Ticket
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
