import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import CardTable from "components/Cards/CardTable.js";

export default function Destinations() {
  const history = useHistory();
  const [showForm, setShowForm] = useState(false);
  const [editingDestination, setEditingDestination] = useState(null);
  const [destinationData, setDestinationData] = useState({
    destination: "",
    city: "",
    province: "Kigali",
    distance: "",
    estimatedTime: "",
    status: "active"
  });
  const [destinationList, setDestinationList] = useState([
    {
      id: 1,
      destination: "Huye",
      city: "Huye",
      province: "Southern Province",
      distance: "135 km",
      estimatedTime: "2.5 hours",
      status: "active"
    },
    {
      id: 2,
      destination: "Musanze",
      city: "Musanze",
      province: "Northern Province",
      distance: "80 km",
      estimatedTime: "1.5 hours",
      status: "active"
    },
    {
      id: 3,
      destination: "Kibuye",
      city: "Kibuye",
      province: "Western Province",
      distance: "120 km",
      estimatedTime: "2 hours",
      status: "active"
    },
    {
      id: 4,
      destination: "Rusizi",
      city: "Rusizi",
      province: "Western Province",
      distance: "150 km",
      estimatedTime: "3 hours",
      status: "active"
    },
    {
      id: 5,
      destination: "Gisenyi",
      city: "Gisenyi",
      province: "Western Province",
      distance: "140 km",
      estimatedTime: "2.5 hours",
      status: "active"
    },
    {
      id: 6,
      destination: "Butare",
      city: "Butare",
      province: "Southern Province",
      distance: "130 km",
      estimatedTime: "2.5 hours",
      status: "active"
    }
  ]);

  const provinces = [
    "Kigali", "Northern Province", "Southern Province", 
    "Eastern Province", "Western Province"
  ];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setDestinationData({
      ...destinationData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editingDestination) {
      setDestinationList(destinationList.map(item => 
        item.id === editingDestination.id ? { ...destinationData, id: item.id } : item
      ));
      setEditingDestination(null);
    } else {
      setDestinationList([...destinationList, { ...destinationData, id: Date.now() }]);
    }
    setDestinationData({
      destination: "",
      city: "",
      province: "Kigali",
      distance: "",
      estimatedTime: "",
      status: "active"
    });
    setShowForm(false);
  };

  const handleEdit = (destination) => {
    setEditingDestination(destination);
    setDestinationData(destination);
    setShowForm(true);
  };

  const handleDelete = (id) => {
    setDestinationList(destinationList.filter(item => item.id !== id));
  };

  const handleCancel = () => {
    setShowForm(false);
    setEditingDestination(null);
    setDestinationData({
      destination: "",
      city: "",
      province: "Kigali",
      distance: "",
      estimatedTime: "",
      status: "active"
    });
  };

  const handleAddNew = () => {
    history.push("/admin/destinations/add");
  };

  return (
    <>
      {/* Destinations Management */}
      <div className="flex flex-wrap mt-4">
        <div className="w-full xl:w-8/12 mb-12 xl:mb-0 px-4">
          <div className="bg-white rounded-lg shadow-md p-6">
            <div className="flex items-center justify-between mb-6">
              <h1 className="text-3xl font-bold text-gray-800">Destinations Management</h1>
              <div className="flex space-x-3">
                <button
                  onClick={handleAddNew}
                  className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition-colors"
                >
                  <i className="fas fa-plus mr-2"></i>
                  Add New Destination
                </button>
                <button
                  onClick={() => setShowForm(!showForm)}
                  className="px-4 py-2 border border-gray-300 text-gray-700 rounded-md hover:bg-gray-50 transition-colors"
                >
                  <i className="fas fa-edit mr-2"></i>
                  Quick Add
                </button>
              </div>
            </div>

            {showForm && (
              <div className="mb-6 p-4 border border-gray-200 rounded-lg bg-gray-50">
                <h3 className="text-lg font-semibold mb-4">
                  {editingDestination ? "Edit Destination" : "Add New Destination"}
                </h3>
                <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <input
                    type="text"
                    name="destination"
                    value={destinationData.destination}
                    onChange={handleInputChange}
                    placeholder="Destination Name"
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                  <input
                    type="text"
                    name="city"
                    value={destinationData.city}
                    onChange={handleInputChange}
                    placeholder="City"
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                  <select
                    name="province"
                    value={destinationData.province}
                    onChange={handleInputChange}
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  >
                    {provinces.map((province) => (
                      <option key={province} value={province}>
                        {province}
                      </option>
                    ))}
                  </select>
                  <input
                    type="number"
                    name="distance"
                    value={destinationData.distance}
                    onChange={handleInputChange}
                    placeholder="Distance (km)"
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                  <input
                    type="number"
                    name="estimatedTime"
                    value={destinationData.estimatedTime}
                    onChange={handleInputChange}
                    placeholder="Est. Time (hours)"
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                  <select
                    name="status"
                    value={destinationData.status}
                    onChange={handleInputChange}
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="active">Active</option>
                    <option value="inactive">Inactive</option>
                    <option value="maintenance">Maintenance</option>
                  </select>
                  <div className="md:col-span-3 flex space-x-3">
                    <button
                      type="submit"
                      className="px-4 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition-colors"
                    >
                      {editingDestination ? "Update" : "Add"}
                    </button>
                    <button
                      type="button"
                      onClick={handleCancel}
                      className="px-4 py-2 border border-gray-300 text-gray-700 rounded-md hover:bg-gray-50 transition-colors"
                    >
                      Cancel
                    </button>
                  </div>
                </form>
              </div>
            )}

            <CardTable>
              <div className="rounded-t mb-0 px-4 py-3 border-0">
                <div className="flex flex-wrap items-center justify-between">
                  <div className="relative w-full px-4 max-w-full flex-grow flex-1">
                    <h3 className="font-semibold text-lg text-blueGray-700">
                      Destinations List
                    </h3>
                  </div>
                  <div className="flex space-x-2">
                    <button className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition-colors">
                      <i className="fas fa-filter mr-2"></i>
                      Filter
                    </button>
                    <button className="px-4 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition-colors">
                      <i className="fas fa-download mr-2"></i>
                      Export
                    </button>
                  </div>
                </div>
              </div>
              <div className="block w-full overflow-x-auto">
                <table className="items-center w-full bg-transparent border-collapse">
                  <thead>
                    <tr>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Destination
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        City
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Province
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Distance
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Est. Time
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Status
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Actions
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    {destinationList.map((destination) => (
                      <tr key={destination.id}>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          {destination.destination}
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          {destination.city}
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          {destination.province}
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          {destination.distance} km
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          {destination.estimatedTime} hrs
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          <span className={`px-2 py-1 rounded-full text-xs ${
                            destination.status === 'active' 
                              ? 'bg-green-100 text-green-800' 
                              : destination.status === 'maintenance'
                              ? 'bg-yellow-100 text-yellow-800'
                              : 'bg-gray-100 text-gray-800'
                          }`}>
                            {destination.status}
                          </span>
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          <div className="flex space-x-2">
                            <button
                              onClick={() => handleEdit(destination)}
                              className="px-3 py-1 bg-indigo-600 text-white rounded text-xs hover:bg-indigo-700 font-medium"
                            >
                              <i className="fas fa-edit mr-1"></i>
                              Edit
                            </button>
                            <button
                              onClick={() => handleDelete(destination.id)}
                              className="px-3 py-1 bg-red-600 text-white rounded text-xs hover:bg-red-700 font-medium"
                            >
                              <i className="fas fa-trash mr-1"></i>
                              Delete
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </CardTable>
          </div>
        </div>
      </div>
    </>
  );
}
