import React, { useEffect, useState } from "react";
import Chart from "chart.js/auto"; // Use Chart.js v3
import { useHistory } from "react-router-dom";

export default function CardLineChart() {
  const history = useHistory();
  const [chartData, setChartData] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchWeeklyRevenue = async () => {
      try {
        const token = localStorage.getItem("token");
        const expressId = localStorage.getItem("expressId");

        if (!token) {
          setError("Please log in to view revenue data.");
          history.push("/login"); // Redirect to login if no token
          return;
        }

        // Get start (Monday) & end (Sunday) of current week
        const now = new Date();
        const startOfWeek = new Date(now.setDate(now.getDate() - now.getDay() + 1));
        startOfWeek.setHours(0, 0, 0, 0);
        const endOfWeek = new Date(startOfWeek);
        endOfWeek.setDate(endOfWeek.getDate() + 6);
        endOfWeek.setHours(23, 59, 59, 999);

        const response = await fetch(
          `http://localhost:5000/api/reports/date-range?start=${startOfWeek.getTime()}&end=${endOfWeek.getTime()}`,
          {
            method: "GET",
            headers: {
              "Content-Type": "application/json",
              "Authorization": `Bearer ${token}`,
              "X-Express-Id": expressId || "",
            },
          }
        );

        if (!response.ok) {
          if (response.status === 401) {
            setError("Unauthorized: Please log in again.");
            localStorage.removeItem("token"); // Clear invalid token
            history.push("/login");
            return;
          }
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const tickets = await response.json();
        console.log("Fetched tickets for revenue:", tickets);

        // Group by day of week (Mon-Sun)
        const labels = ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"];
        const revenuePerDay = Array(7).fill(0);

        tickets.forEach((ticket) => {
          const day = new Date(ticket.createdAt).getDay();
          const index = day === 0 ? 6 : day - 1; // Shift Sunday to last
          revenuePerDay[index] += ticket.price || 0;
        });

        setChartData({
          labels,
          datasets: [
            {
              label: "Revenue (RWF)",
              backgroundColor: "rgba(59,130,246,0.1)",
              borderColor: "rgb(59,130,246)",
              data: revenuePerDay,
              fill: true,
              tension: 0.4,
            },
          ],
        });
        setLoading(false);
      } catch (err) {
        console.error("Error fetching revenue:", err);
        setError("Failed to load revenue data: " + err.message);
        setLoading(false);
      }
    };

    fetchWeeklyRevenue();
  }, [history]);

  useEffect(() => {
    if (!chartData) return;

    const ctx = document.getElementById("line-chart")?.getContext("2d");
    if (!ctx) {
      console.error("Canvas element 'line-chart' not found.");
      return;
    }

    if (window.myLine) window.myLine.destroy();

    window.myLine = new Chart(ctx, {
      type: "line",
      data: chartData,
      options: {
        maintainAspectRatio: false,
        responsive: true,
        plugins: {
          legend: {
            labels: { color: "white" },
            align: "end",
            position: "bottom",
          },
        },
        scales: {
          x: { ticks: { color: "white" } },
          y: { ticks: { color: "white", beginAtZero: true } },
        },
      },
    });

    return () => window.myLine && window.myLine.destroy();
  }, [chartData]);

  if (loading) return <p className="text-center p-6 text-white">Loading revenue data...</p>;
  if (error) return <p className="text-center p-6 text-red-500">{error}</p>;

  return (
    <div className="relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded bg-blueGray-700">
      <div className="rounded-t px-4 py-3 bg-transparent">
        <h2 className="text-white text-xl font-semibold">Revenue Overview</h2>
      </div>
      <div className="p-4 flex-auto">
        <div className="relative h-350-px">
          <canvas id="line-chart"></canvas>
        </div>
      </div>
    </div>
  );
}
