import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import CardTable from "components/Cards/CardTable.js";

export default function Buses() {
  const history = useHistory();
  const [showForm, setShowForm] = useState(false);
  const [editingBus, setEditingBus] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [busData, setBusData] = useState({
    busNumber: "",
    plateNumber: "",
    capacity: "",
    model: "",
    driver: "",
    status: "active"
  });
  const [busList, setBusList] = useState([
    {
      id: 1,
      busNumber: "BUS-001",
      capacity: "50",
      model: "Toyota Coaster",
      year: "2020",
      status: "active"
    },
    {
      id: 2,
      busNumber: "BUS-002",
      capacity: "45",
      model: "Toyota Coaster",
      year: "2021",
      status: "active"
    },
    {
      id: 3,
      busNumber: "BUS-003",
      capacity: "50",
      model: "Toyota Coaster",
      year: "2022",
      status: "active"
    },
    {
      id: 4,
      busNumber: "BUS-004",
      capacity: "45",
      model: "Toyota Coaster",
      year: "2021",
      status: "maintenance"
    },
    {
      id: 5,
      busNumber: "BUS-005",
      capacity: "50",
      model: "Toyota Coaster",
      year: "2023",
      status: "active"
    },
    {
      id: 6,
      busNumber: "BUS-006",
      capacity: "45",
      model: "Toyota Coaster",
      year: "2022",
      status: "active"
    }
  ]);

  // Filter bus list based on search term
  const filteredBusList = busList.filter(bus =>
    bus.busNumber.toLowerCase().includes(searchTerm.toLowerCase()) ||
    bus.model.toLowerCase().includes(searchTerm.toLowerCase()) ||
    bus.status.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBusData({
      ...busData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editingBus) {
      setBusList(busList.map(item => 
        item.id === editingBus.id ? { ...busData, id: item.id } : item
      ));
      setEditingBus(null);
    } else {
      setBusList([...busList, { ...busData, id: Date.now() }]);
    }
    setBusData({
      busNumber: "",
      plateNumber: "",
      capacity: "",
      model: "",
      driver: "",
      status: "active"
    });
    setShowForm(false);
  };

  const handleEdit = (bus) => {
    setEditingBus(bus);
    setBusData(bus);
    setShowForm(true);
  };

  const handleDelete = (id) => {
    setBusList(busList.filter(item => item.id !== id));
  };

  const handleCancel = () => {
    setShowForm(false);
    setEditingBus(null);
    setBusData({
      busNumber: "",
      plateNumber: "",
      capacity: "",
      model: "",
      driver: "",
      status: "active"
    });
  };

  const handleAddNew = () => {
    history.push("/admin/buses/add");
  };

  return (
    <>
      {/* Buses Management */}
      <div className="flex flex-wrap mt-4">
        <div className="w-full xl:w-8/12 mb-12 xl:mb-0 px-4">
          <div className="bg-white rounded-lg shadow-md p-6">
            <div className="flex items-center justify-between mb-6">
              <h1 className="text-3xl font-bold text-gray-800">Buses Management</h1>
              <div className="flex space-x-3">
                <button
                  onClick={handleAddNew}
                  className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition-colors"
                >
                  <i className="fas fa-plus mr-2"></i>
                  Add New Bus
                </button>
                <button
                  onClick={() => setShowForm(!showForm)}
                  className="px-4 py-2 border border-gray-300 text-gray-700 rounded-md hover:bg-gray-50 transition-colors"
                >
                  <i className="fas fa-edit mr-2"></i>
                  Quick Add
                </button>
              </div>
            </div>

            {showForm && (
              <div className="mb-6 p-4 border border-gray-200 rounded-lg bg-gray-50">
                <h3 className="text-lg font-semibold mb-4">
                  {editingBus ? "Edit Bus" : "Add New Bus"}
                </h3>
                <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <input
                    type="text"
                    name="busNumber"
                    value={busData.busNumber}
                    onChange={handleInputChange}
                    placeholder="Bus Number"
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                  <input
                    type="text"
                    name="plateNumber"
                    value={busData.plateNumber}
                    onChange={handleInputChange}
                    placeholder="Plate Number"
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                  <input
                    type="number"
                    name="capacity"
                    value={busData.capacity}
                    onChange={handleInputChange}
                    placeholder="Capacity"
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                  <input
                    type="text"
                    name="model"
                    value={busData.model}
                    onChange={handleInputChange}
                    placeholder="Model"
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                  <input
                    type="text"
                    name="driver"
                    value={busData.driver}
                    onChange={handleInputChange}
                    placeholder="Driver"
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                  <select
                    name="status"
                    value={busData.status}
                    onChange={handleInputChange}
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="active">Active</option>
                    <option value="maintenance">Maintenance</option>
                    <option value="inactive">Inactive</option>
                  </select>
                  <div className="md:col-span-3 flex space-x-3">
                    <button
                      type="submit"
                      className="px-4 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition-colors"
                    >
                      {editingBus ? "Update" : "Add"}
                    </button>
                    <button
                      type="button"
                      onClick={handleCancel}
                      className="px-4 py-2 border border-gray-300 text-gray-700 rounded-md hover:bg-gray-50 transition-colors"
                    >
                      Cancel
                    </button>
                  </div>
                </form>
              </div>
            )}

            <CardTable>
              <div className="rounded-t mb-0 px-4 py-3 border-0">
                <div className="flex flex-wrap items-center justify-between">
                  <div className="relative w-full px-4 max-w-full flex-grow flex-1">
                    <h3 className="font-semibold text-lg text-blueGray-700">
                      Buses List
                    </h3>
                  </div>
                  <div className="flex space-x-2">
                    <button className="px-4 py-2 bg-amber-500 text-white rounded-md hover:bg-amber-600 transition-colors font-medium">
                      <i className="fas fa-filter mr-2"></i>
                      Filter
                    </button>
                  </div>
                </div>
              </div>
              <div className="block w-full overflow-x-auto">
                <table className="items-center w-full bg-transparent border-collapse">
                  <thead>
                    <tr>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Bus Number
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Plate Number
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Capacity
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Model
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Driver
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Status
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Actions
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredBusList.map((bus) => (
                      <tr key={bus.id}>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          {bus.busNumber}
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          {bus.plateNumber}
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          {bus.capacity} seats
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          {bus.model}
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          {bus.driver}
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          <span className={`px-2 py-1 rounded-full text-xs ${
                            bus.status === 'active' 
                              ? 'bg-green-100 text-green-800' 
                              : bus.status === 'maintenance'
                              ? 'bg-yellow-100 text-yellow-800'
                              : 'bg-gray-100 text-gray-800'
                          }`}>
                            {bus.status}
                          </span>
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          <div className="flex space-x-2">
                            <button
                              onClick={() => handleEdit(bus)}
                              className="px-3 py-1 bg-blue-500 text-white rounded text-xs hover:bg-blue-600"
                            >
                              Edit
                            </button>
                            <button
                              onClick={() => handleDelete(bus.id)}
                              className="px-3 py-1 bg-red-500 text-white rounded text-xs hover:bg-red-600"
                            >
                              Delete
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </CardTable>
          </div>
        </div>
      </div>
    </>
  );
}
