import React, { useState } from "react";
import CardStats from "components/Cards/CardStats.js";
import CardLineChart from "components/Cards/CardLineChart.js";
import CardBarChart from "components/Cards/CardBarChart.js";
import CardTable from "components/Cards/CardTable.js";
import jsPDF from "jspdf";
import "jspdf-autotable";

export default function Reports() {
  const [reportPeriod, setReportPeriod] = useState("weekly");
  const [reportType, setReportType] = useState("overview");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  const [totalRevenue, setTotalRevenue] = useState(0);
  const [totalTickets, setTotalTickets] = useState(0);
  const [revenueData, setRevenueData] = useState({ labels: [], datasets: [] });
  const [ticketSalesData, setTicketSalesData] = useState({ labels: [], datasets: [] });
  const [tableData, setTableData] = useState([]);

  const handleGenerateReport = async () => {
    if (!startDate || !endDate) {
      alert("Please select both start and end dates");
      return;
    }

    try {
      const startMillis = new Date(startDate).getTime();
      const endMillis = new Date(endDate).getTime();

      const token = localStorage.getItem("token");

      const response = await fetch(
        `http://localhost:5000/api/reports/date-range?start=${startMillis}&end=${endMillis}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`,
          },
        }
      );

      if (!response.ok) {
        throw new Error("Failed to fetch report data");
      }

      const data = await response.json();

      // --- Group tickets by Date + Destination ---
      const grouped = {};
      data.forEach((t) => {
        const date = new Date(t.date).toLocaleDateString();
        // Fixed: Properly handle destination data
        const destination = t.schedule ? 
          (typeof t.schedule.destination === 'object' ? 
            `${t.schedule.destination.fromLocation} - ${t.schedule.destination.toLocation}` : 
            t.schedule.destination) : 
          "Unknown";

        const key = `${date}-${destination}`;

        if (!grouped[key]) {
          grouped[key] = {
            date,
            destination,
            tickets: 0,
            revenue: 0,
            paidTickets: 0,
            unpaidTickets: 0,
          };
        }

        grouped[key].tickets += 1;
        grouped[key].revenue += t.totalAmount || 0;

        if (t.payState === "PAID") {
          grouped[key].paidTickets += 1;
        } else {
          grouped[key].unpaidTickets += 1;
        }
      });

      const groupedData = Object.values(grouped);
      setTableData(groupedData);

      setTotalRevenue(groupedData.reduce((sum, r) => sum + r.revenue, 0));
      setTotalTickets(groupedData.reduce((sum, r) => sum + r.tickets, 0));

      // Charts
      const sortedData = groupedData.sort(
        (a, b) => new Date(a.date) - new Date(b.date)
      );
      const labels = sortedData.map((r) => r.date);

      const revenueChart = {
        labels,
        datasets: [
          {
            label: "Revenue (RWF)",
            data: sortedData.map((r) => r.revenue),
            borderColor: "rgb(59,130,246)",
            backgroundColor: "rgba(59,130,246,0.1)",
            tension: 0.4, // Makes line chart smoother
          },
        ],
      };
      setRevenueData(revenueChart);

      const ticketChart = {
        labels,
        datasets: [
          {
            label: "Paid Tickets",
            data: sortedData.map((r) => r.paidTickets),
            backgroundColor: "rgba(34,197,94,0.8)",
          },
          {
            label: "Unpaid Tickets",
            data: sortedData.map((r) => r.unpaidTickets),
            backgroundColor: "rgba(239,68,68,0.8)",
          },
        ],
      };
      setTicketSalesData(ticketChart);

      alert("Report generated successfully!");
    } catch (error) {
      console.error(error);
      alert("Error generating report: " + error.message);
    }
  };

  const handleExportReport = () => {
    if (tableData.length === 0) {
      alert("No data to export. Please generate a report first.");
      return;
    }

    // Create PDF document
    const doc = new jsPDF();

    // Add title
    doc.setFontSize(20);
    doc.text("Travel Report", 105, 15, { align: "center" });

    // Add date range
    doc.setFontSize(12);
    doc.text(`Date Range: ${startDate} to ${endDate}`, 105, 25, { align: "center" });

    // Add summary stats
    doc.setFontSize(14);
    doc.text("Summary", 14, 35);
    doc.setFontSize(10);
    doc.text(`Total Revenue: RWF ${totalRevenue.toLocaleString()}`, 14, 45);
    doc.text(`Total Tickets: ${totalTickets}`, 14, 55);

    // Add table
    doc.autoTable({
      startY: 65,
      head: [['Date', 'Destination', 'Tickets', 'Revenue', 'Paid', 'Unpaid']],
      body: tableData.map(row => [
        row.date,
        row.destination,
        row.tickets,
        `RWF ${row.revenue.toLocaleString()}`,
        row.paidTickets,
        row.unpaidTickets
      ]),
      theme: 'grid',
      headStyles: {
        fillColor: [59, 130, 246],
        textColor: 255
      }
    });

    // Save the PDF
    doc.save(`travel-report-${startDate}-to-${endDate}.pdf`);
    alert("PDF exported successfully!");
  };

  return (
    <>
      <div className="flex flex-wrap mt-4">
        <div className="w-full xl:w-8/12 mb-12 xl:mb-0 px-4">
          <CardStats
            statSubtitle="Total Revenue"
            statTitle={`RWF ${totalRevenue.toLocaleString()}`}
            statArrow="up"
            statPercent="12"
            statPercentColor="text-emerald-500"
            statDescripiron="Since last month"
            statIconName="fas fa-dollar-sign"
            statIconColor="bg-emerald-500"
          />
        </div>
        <div className="w-full xl:w-4/12 px-4">
          <CardStats
            statSubtitle="Total Tickets"
            statTitle={totalTickets}
            statArrow="up"
            statPercent="3.48"
            statPercentColor="text-emerald-500"
            statDescripiron="Since last week"
            statIconName="fas fa-ticket-alt"
            statIconColor="bg-red-500"
          />
        </div>
      </div>

      {/* Date Range Selection */}
      <div className="flex flex-wrap mt-4">
        <div className="w-full px-4">
          <div className="bg-white rounded-lg shadow-md p-6 mb-6">
            <h3 className="text-lg font-semibold text-gray-800 mb-6">
              Report Configuration
            </h3>

            <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-6"> 

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Start Date
                </label>
                <input
                  type="date"
                  value={startDate}
                  onChange={(e) => setStartDate(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  End Date
                </label>
                <input
                  type="date"
                  value={endDate}
                  onChange={(e) => setEndDate(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
            </div>

            <div className="flex space-x-4">
              <button
                onClick={handleGenerateReport}
                className="px-6 py-2 bg-red-500 text-white rounded-md hover:bg-red-600 transition-colors font-medium"
              >
                <i className="fas fa-chart-line mr-2"></i>
                Generate Report
              </button>
              <button
                onClick={handleExportReport}
                className="px-6 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition-colors font-medium"
              >
                <i className="fas fa-download mr-2"></i>
                Export Report
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Charts */}
      <div className="flex flex-wrap mt-4">
        <div className="w-full xl:w-8/12 mb-12 xl:mb-0 px-4">
          {revenueData.labels.length > 0 ? (
            <CardLineChart chartData={revenueData} />
          ) : (
            <div className="bg-white rounded-lg shadow-md p-6 text-center">
              <p className="text-gray-500">Generate a report to view revenue chart</p>
            </div>
          )}
        </div>
        <div className="w-full xl:w-4/12 px-4">
          {ticketSalesData.labels.length > 0 ? (
            <CardBarChart chartData={ticketSalesData} />
          ) : (
            <div className="bg-white rounded-lg shadow-md p-6 text-center">
              <p className="text-gray-500">Generate a report to view ticket sales chart</p>
            </div>
          )}
        </div>
      </div>

      {/* Table */}
      <div className="flex flex-wrap mt-4">
        <div className="w-full px-4">
          <CardTable>
            <div className="rounded-t mb-0 px-4 py-3 border-0">
              <div className="flex flex-wrap items-center justify-between">
                <div className="relative w-full px-4 max-w-full flex-grow flex-1">
                  <h3 className="font-semibold text-lg text-blueGray-700">
                    Detailed Report Data
                  </h3>
                </div>
              </div>
            </div>
            <div className="block w-full overflow-x-auto">
              {tableData.length > 0 ? (
                <table className="items-center w-full bg-transparent border-collapse">
                  <thead>
                    <tr>
                      <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50">
                        Date
                      </th>
                      <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50">
                        Destination
                      </th>
                      <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50">
                        Tickets Sold
                      </th>
                      <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50">
                        Revenue
                      </th>
                      <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50">
                        Status
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    {tableData.map((row, index) => (
                      <tr key={index}>
                        <td className="px-6 py-4">{row.date}</td> 
                        <td className="px-6 py-4">{row.destination}</td>
                        <td className="px-6 py-4">{row.tickets}</td>
                        <td className="px-6 py-4">{`RWF ${row.revenue.toLocaleString()}`}</td>
                        <td className="px-6 py-4">
                          <span className="mr-4">
                            <i className="fas fa-circle text-emerald-500 mr-1"></i> 
                            Paid: {row.paidTickets}
                          </span>
                          <span>
                            <i className="fas fa-circle text-orange-500 mr-1"></i> 
                            Unpaid: {row.unpaidTickets}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              ) : (
                <div className="text-center py-8">
                  <p className="text-gray-500">No data available. Generate a report to view details.</p>
                </div>
              )}
            </div>
          </CardTable>
        </div>
      </div>
    </>
  );
}
