import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

export default function DestinationAdd() {
  const navigate = useNavigate();

  const [destinationData, setDestinationData] = useState({
    express: "",
    fromLocation: "",
    toLocation: "",
    cost: "",
  });

  const [expressList, setExpressList] = useState([]);
  const [districtList, setDistrictList] = useState([]);
  const [role, setRole] = useState("");  
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem("token");
        const expressId = localStorage.getItem("expressId");
 
        const user = JSON.parse(localStorage.getItem("user") || "{}");
        const userRole = (user.role || localStorage.getItem("role") || "").toUpperCase();
        setRole(userRole);
 
        const expressRes = await fetch("http://localhost:5000/api/express/all", {
          headers: { Authorization: `Bearer ${token}` },
          credentials: "include",
        });
        setExpressList(await expressRes.json());

        // Set default express for non-SUPER_ADMIN users
        if (userRole !== "SUPER_ADMIN") {
          setDestinationData(prev => ({ ...prev, express: expressId }));
        }

        // Fetch districts
        const districtRes = await fetch("http://localhost:5000/api/destinations/districts", {
          headers: { Authorization: `Bearer ${token}` },
          credentials: "include",
        });
        setDistrictList(await districtRes.json());
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleInputChange = e => {
    const { name, value } = e.target;
    setDestinationData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (destinationData.fromLocation === destinationData.toLocation) {
      return alert("From Location and To Location cannot be the same!");
    }

    try {
      const token = localStorage.getItem("token");
      const payload = {
        ...destinationData,
        express: role === "SUPER_ADMIN"
          ? expressList.find(exp => exp.id === destinationData.express)
          : { id: destinationData.express },
      };

      const res = await fetch("http://localhost:5000/api/destinations/register", {
        method: "POST",
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify(payload),
      });

      if (!res.ok) {
        const errorText = await res.text();
        throw new Error(errorText || "Failed to save destination");
      }

      alert("Destination added successfully!");
      navigate("/admin/destinations");
    } catch (error) {
      console.error(error);
      alert("Error saving destination: " + error.message);
    }
  };

  const handleCancel = () => navigate("/admin/destinations");

  if (loading) return <p className="text-center p-6">Loading...</p>;

  return (
    <div className="flex flex-wrap">
      <div className="w-full xl:w-8/12 mb-12 xl:mb-0 px-4 mx-auto">
        <div className="relative flex flex-col w-full mb-6 shadow-lg rounded-lg bg-white border-0">
          <div className="rounded-t bg-white mb-0 px-6 py-6 flex justify-between items-center">
            <h6 className="text-blueGray-700 text-xl font-bold">Add New Destination</h6>
            <button
              onClick={handleCancel}
              className="bg-red-500 text-white font-bold uppercase text-xs px-4 py-2 rounded shadow hover:shadow-md"
            >
              Back
            </button>
          </div>

          <form onSubmit={handleSubmit} className="flex-auto px-4 lg:px-10 py-10 pt-0">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {role === "SUPER_ADMIN" && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Express *</label>
                  <select
                    name="express"
                    value={destinationData.express}
                    onChange={handleInputChange}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    required
                  >
                    <option value="">Select Express</option>
                    {expressList.map(exp => (
                      <option key={exp.id} value={exp.id}>{exp.expressName}</option>
                    ))}
                  </select>
                </div>
              )}

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">From Location *</label>
                <select
                  name="fromLocation"
                  value={destinationData.fromLocation}
                  onChange={handleInputChange}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  required
                >
                  <option value="">Select From Location</option>
                  {districtList.map(d => (
                    <option key={d} value={d}>{d}</option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">To Location *</label>
                <select
                  name="toLocation"
                  value={destinationData.toLocation}
                  onChange={handleInputChange}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  required
                >
                  <option value="">Select To Location</option>
                  {districtList
                    .filter(d => d !== destinationData.fromLocation)
                    .map(d => (
                      <option key={d} value={d}>{d}</option>
                    ))}
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Cost (RWF) *</label>
                <input
                  type="number"
                  name="cost"
                  value={destinationData.cost}
                  onChange={handleInputChange}
                  placeholder="Enter cost"
                  min="0"
                  step="100"
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  required
                />
              </div>
            </div>

            <div className="flex justify-end space-x-4 pt-6 border-t border-gray-200">
              <button
                type="button"
                onClick={handleCancel}
                className="px-6 py-3 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-50 transition-colors"
              >
                Cancel
              </button>
              <button
                type="submit"
                className="px-6 py-3 bg-black text-white rounded-lg hover:bg-gray-700 transition-colors"
              >
                <i className="fas fa-save mr-2"></i>
                Save
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
