import React, { useState, useEffect } from "react";
import CardStats from "components/Cards/CardStats.js";
import CardLineChart from "components/Cards/CardLineChart.js";
import CardBarChart from "components/Cards/CardBarChart.js";
import CardTable from "components/Cards/CardTable.js";
import jsPDF from "jspdf";
import "jspdf-autotable";

export default function Reports() {
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  const [totalRevenue, setTotalRevenue] = useState(0);
  const [totalTickets, setTotalTickets] = useState(0);
  const [totalPaidTickets, setTotalPaidTickets] = useState(0);
  const [totalUnpaidTickets, setTotalUnpaidTickets] = useState(0);
  const [tableData, setTableData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  // Load default weekly data on component mount
  useEffect(() => {
    loadDefaultWeeklyData();
  }, []);

  const loadDefaultWeeklyData = async () => {
    try {
      setIsLoading(true);
      // Calculate date range for the past week
      const end = new Date();
      const start = new Date();
      start.setDate(start.getDate() - 7);

      const startStr = start.toISOString().split('T')[0];
      const endStr = end.toISOString().split('T')[0];

      setStartDate(startStr);
      setEndDate(endStr);

      await fetchReportData(start.getTime(), end.getTime());
    } catch (error) {
      console.error("Error loading default weekly data:", error);
      alert("Error loading default weekly data: " + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const fetchReportData = async (startMillis, endMillis) => {
    try {
      const token = localStorage.getItem("token");

      const response = await fetch(
        `http://localhost:5000/api/reports/date-range?start=${startMillis}&end=${endMillis}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`,
          },
        }
      );

      if (!response.ok) {
        throw new Error("Failed to fetch report data");
      }

      const data = await response.json();
      processReportData(data);
    } catch (error) {
      console.error(error);
      alert("Error fetching report data: " + error.message);
    }
  };

  const processReportData = (data) => {
    // --- Group tickets by Date only (ignore destination) ---
    const groupedByDate = {};
    data.forEach((t) => {
      const date = new Date(t.date).toLocaleDateString();

      if (!groupedByDate[date]) {
        groupedByDate[date] = {
          date,
          tickets: 0,
          revenue: 0,
          paidTickets: 0,
          unpaidTickets: 0,
        };
      }

      groupedByDate[date].tickets += 1;
      groupedByDate[date].revenue += t.totalAmount || 0;

      if (t.payState === "PAID") {
        groupedByDate[date].paidTickets += 1;
      } else {
        groupedByDate[date].unpaidTickets += 1;
      }
    });

    const groupedData = Object.values(groupedByDate);
    setTableData(groupedData);

    const totalRev = groupedData.reduce((sum, r) => sum + r.revenue, 0);
    const totalTix = groupedData.reduce((sum, r) => sum + r.tickets, 0);
    const totalPaid = groupedData.reduce((sum, r) => sum + r.paidTickets, 0);
    const totalUnpaid = groupedData.reduce((sum, r) => sum + r.unpaidTickets, 0);

    setTotalRevenue(totalRev);
    setTotalTickets(totalTix);
    setTotalPaidTickets(totalPaid);
    setTotalUnpaidTickets(totalUnpaid);
  };

  const handleGenerateReport = async () => {
    if (!startDate || !endDate) {
      alert("Please select both start and end dates");
      return;
    }

    try {
      setIsLoading(true);
      const startMillis = new Date(startDate).getTime();
      const endMillis = new Date(endDate).getTime();
      await fetchReportData(startMillis, endMillis);
      alert("Report generated successfully!");
    } catch (error) {
      console.error(error);
      alert("Error generating report: " + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleExportReport = () => {
    if (tableData.length === 0) {
      alert("No data to export. Please generate a report first.");
      return;
    }

    // Create PDF document
    const doc = new jsPDF();

    // Add title
    doc.setFontSize(20);
    doc.text("Travel Report", 105, 15, { align: "center" });

    // Add date range
    doc.setFontSize(12);
    doc.text(`Date Range: ${startDate} to ${endDate}`, 105, 25, { align: "center" });

    // Add summary stats
    doc.setFontSize(14);
    doc.text("Summary", 14, 35);
    doc.setFontSize(10);
    doc.text(`Total Revenue: RWF ${totalRevenue.toLocaleString()}`, 14, 45);
    doc.text(`Total Tickets: ${totalTickets}`, 14, 55);
    doc.text(`Paid Tickets: ${totalPaidTickets}`, 14, 65);
    doc.text(`Unpaid Tickets: ${totalUnpaidTickets}`, 14, 75);

    // Add table
    doc.autoTable({
      startY: 85,
      head: [['Date', 'Tickets', 'Revenue', 'Paid', 'Unpaid']],
      body: tableData.map(row => [
        row.date,
        row.tickets,
        `RWF ${row.revenue.toLocaleString()}`,
        row.paidTickets,
        row.unpaidTickets
      ]),
      theme: 'grid',
      headStyles: {
        fillColor: [59, 130, 246],
        textColor: 255
      }
    });

    // Save the PDF
    doc.save(`travel-report-${startDate}-to-${endDate}.pdf`);
    alert("PDF exported successfully!");
  };

  return (
    <>
      <div className="flex flex-wrap mt-4">
        <div className="w-full xl:w-3/12 mb-12 xl:mb-0 px-4">
          <CardStats
            statSubtitle="Total Revenue"
            statTitle={`RWF ${totalRevenue.toLocaleString()}`}
            statArrow="up"
            statPercent="12"
            statPercentColor="text-emerald-500"
            statDescripiron="Since last month"
            statIconName="fas fa-dollar-sign"
            statIconColor="bg-emerald-500"
          />
        </div>
        <div className="w-full xl:w-3/12 px-4">
          <CardStats
            statSubtitle="Total Tickets"
            statTitle={totalTickets}
            statArrow="up"
            statPercent="3.48"
            statPercentColor="text-emerald-500"
            statDescripiron="Since last week"
            statIconName="fas fa-ticket-alt"
            statIconColor="bg-red-500"
          />
        </div>
        <div className="w-full xl:w-3/12 px-4">
          <CardStats
            statSubtitle="Paid Tickets"
            statTitle={totalPaidTickets}
            statArrow="up"
            statPercent="1.10"
            statPercentColor="text-emerald-500"
            statDescripiron="Since yesterday"
            statIconName="fas fa-check-circle"
            statIconColor="bg-blue-500"
          />
        </div>
        <div className="w-full xl:w-3/12 px-4">
          <CardStats
            statSubtitle="Unpaid Tickets"
            statTitle={totalUnpaidTickets}
            statArrow="down"
            statPercent="2.30"
            statPercentColor="text-red-500"
            statDescripiron="Since last week"
            statIconName="fas fa-exclamation-circle"
            statIconColor="bg-orange-500"
          />
        </div>
      </div>

      {/* Date Range Selection */}
      <div className="flex flex-wrap mt-4">
        <div className="w-full px-4">
          <div className="bg-white rounded-lg shadow-md p-6 mb-6">
            <h3 className="text-lg font-semibold text-gray-800 mb-6">
              Report Configuration
            </h3>

            <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-6"> 
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Start Date
                </label>
                <input
                  type="date"
                  value={startDate}
                  onChange={(e) => setStartDate(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  End Date
                </label>
                <input
                  type="date"
                  value={endDate}
                  onChange={(e) => setEndDate(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>

              <div className="flex items-end">
                <button
                  onClick={loadDefaultWeeklyData}
                  className="px-4 py-2 bg-gray-500 text-white rounded-md hover:bg-gray-600 transition-colors font-medium"
                >
                  <i className="fas fa-calendar-week mr-2"></i>
                  This Week
                </button>
              </div>
            </div>

            <div className="flex space-x-4">
              <button
                onClick={handleGenerateReport}
                disabled={isLoading}
                className="px-6 py-2 bg-red-500 text-white rounded-md hover:bg-red-600 transition-colors font-medium disabled:opacity-50"
              >
                {isLoading ? (
                  <>
                    <i className="fas fa-spinner fa-spin mr-2"></i>
                    Generating...
                  </>
                ) : (
                  <>
                    <i className="fas fa-chart-line mr-2"></i>
                    Generate Report
                  </>
                )}
              </button>
              <button
                onClick={handleExportReport}
                disabled={tableData.length === 0}
                className="px-6 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition-colors font-medium disabled:opacity-50"
              >
                <i className="fas fa-download mr-2"></i>
                Export Report
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Charts */}
      <div className="flex flex-wrap mt-4">
        <div className="w-full xl:w-8/12 mb-12 xl:mb-0 px-4">
          <CardLineChart 
            title="Revenue" 
            startDate={startDate} 
            endDate={endDate} 
          />
        </div>
        <div className="w-full xl:w-4/12 px-4">
          <CardBarChart 
            title="Ticket Sales" 
            startDate={startDate} 
            endDate={endDate} 
          />
        </div>
      </div>

      {/* Table */}
      <div className="flex flex-wrap mt-4">
        <div className="w-full px-4">
          <CardTable>
            <div className="rounded-t mb-0 px-4 py-3 border-0">
              <div className="flex flex-wrap items-center justify-between">
                <div className="relative w-full px-4 max-w-full flex-grow flex-1">
                  <h3 className="font-semibold text-lg text-blueGray-700">
                    Daily Summary
                  </h3>
                </div>
              </div>
            </div>
            <div className="block w-full overflow-x-auto">
              {tableData.length > 0 ? (
                <table className="items-center w-full bg-transparent border-collapse">
                  <thead>
                    <tr>
                      <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50">
                        Date
                      </th>
                      <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50">
                        Tickets Sold
                      </th>
                      <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50">
                        Revenue
                      </th>
                      <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50">
                        Status
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    {tableData.map((row, index) => (
                      <tr key={index}>
                        <td className="px-6 py-4">{row.date}</td> 
                        <td className="px-6 py-4">{row.tickets}</td>
                        <td className="px-6 py-4">{`RWF ${row.revenue.toLocaleString()}`}</td>
                        <td className="px-6 py-4">
                          <span className="mr-4">
                            <i className="fas fa-circle text-emerald-500 mr-1"></i> 
                            Paid: {row.paidTickets}
                          </span>
                          <span>
                            <i className="fas fa-circle text-orange-500 mr-1"></i> 
                            Unpaid: {row.unpaidTickets}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              ) : (
                <div className="text-center py-8">
                  <p className="text-gray-500">No data available. Generate a report to view details.</p>
                </div>
              )}
            </div>
          </CardTable>
        </div>
      </div>
    </>
  );
}
