 import React, { useState, useEffect } from "react";
import Chart from "chart.js";

export default function CardBarChart() {
    const [chartData, setChartData] = useState(null);
    const [destinations, setDestinations] = useState([]);
    const [startDate, setStartDate] = useState("");
    const [endDate, setEndDate] = useState("");
    const [destination, setDestination] = useState("");

    // Fetch destinations on mount
    useEffect(() => {
        fetch("http://localhost:5000/api/reports/destinations", {
            headers: { Authorization: "Bearer your-token-here" }, // Replace with actual token
        })
            .then((response) => response.json())
            .then((data) => setDestinations(data))
            .catch((error) => console.error("Error fetching destinations:", error));
    }, []);

    // Fetch ticket data based on filters
    useEffect(() => {
        const fetchTickets = async () => {
            try {
                let url = "http://localhost:5000/api/reports/all";
                if (startDate && endDate) {
                    const startMillis = new Date(startDate).getTime();
                    const endMillis = new Date(endDate).getTime();
                    url = `http://localhost:5000/api/reports/date-range?start=${startMillis}&end=${endMillis}`;
                    if (destination) {
                        url += `&destination=${encodeURIComponent(destination)}`;
                    }
                } else if (destination) {
                    const startMillis = new Date().setDate(new Date().getDate() - 7);
                    const endMillis = Date.now();
                    url = `http://localhost:5000/api/reports/destination?destination=${encodeURIComponent(destination)}&start=${startMillis}&end=${endMillis}`;
                }

                const response = await fetch(url, {
                    headers: { Authorization: "Bearer your-token-here" }, // Replace with actual token
                });
                if (!response.ok) throw new Error("Failed to fetch tickets");
                const tickets = await response.json();

                // Process data for chart
                const days = 7;
                const today = new Date();
                const labels = Array.from({ length: days }, (_, i) => {
                    const date = new Date(today);
                    date.setDate(today.getDate() - (days - 1 - i));
                    return date.toLocaleDateString("en-US", { weekday: "short" });
                });

                const paidTickets = Array(days).fill(0);
                const unpaidTickets = Array(days).fill(0);

                tickets.forEach((ticket) => {
                    const ticketDate = new Date(ticket.createdAt);
                    const dayIndex = Math.floor((today - ticketDate) / (1000 * 60 * 60 * 24));
                    if (dayIndex >= 0 && dayIndex < days) {
                        const index = days - 1 - dayIndex;
                        if (ticket.paid) {
                            paidTickets[index] += 1;
                        } else {
                            unpaidTickets[index] += 1;
                        }
                    }
                });

                setChartData({
                    labels,
                    datasets: [
                        {
                            label: "Paid Tickets",
                            backgroundColor: "rgba(34,197,94,0.8)",
                            borderColor: "rgba(34,197,94,1)",
                            data: paidTickets,
                            barThickness: 8,
                        },
                        {
                            label: "Unpaid Tickets",
                            backgroundColor: "rgba(239,68,68,0.8)",
                            borderColor: "rgba(239,68,68,1)",
                            data: unpaidTickets,
                            barThickness: 8,
                        },
                    ],
                });
            } catch (error) {
                console.error("Error fetching tickets:", error);
                setChartData({
                    labels: ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"],
                    datasets: [
                        {
                            label: "Paid Tickets",
                            backgroundColor: "rgba(34,197,94,0.8)",
                            borderColor: "rgba(34,197,94,1)",
                            data: [30, 45, 25, 50, 40, 60, 70],
                            barThickness: 8,
                        },
                        {
                            label: "Unpaid Tickets",
                            backgroundColor: "rgba(239,68,68,0.8)",
                            borderColor: "rgba(239,68,68,1)",
                            data: [15, 20, 10, 25, 18, 30, 35],
                            barThickness: 8,
                        },
                    ],
                });
            }
        };

        fetchTickets();
    }, [startDate, endDate, destination]);

    // Chart rendering
    useEffect(() => {
        if (!chartData) return;

        const config = {
            type: "bar",
            data: chartData,
            options: {
                maintainAspectRatio: false,
                responsive: true,
                plugins: {
                    title: { display: false },
                    tooltip: {
                        mode: "index",
                        intersect: false,
                    },
                    legend: {
                        labels: { color: "rgba(0,0,0,.4)" },
                        align: "end",
                        position: "bottom",
                    },
                },
                hover: {
                    mode: "nearest",
                    intersect: true,
                },
                scales: {
                    x: {
                        display: true,
                        grid: {
                            borderDash: [2],
                            borderDashOffset: [2],
                            color: "rgba(33, 37, 41, 0.3)",
                            zeroLineColor: "rgba(33, 37, 41, 0.3)",
                        },
                    },
                    y: {
                        display: true,
                        grid: {
                            borderDash: [2],
                            drawBorder: false,
                            borderDashOffset: [2],
                            color: "rgba(33, 37, 41, 0.2)",
                            zeroLineColor: "rgba(33, 37, 41, 0.15)",
                        },
                        ticks: {
                            beginAtZero: true,
                            precision: 0,
                        },
                    },
                },
            },
        };

        const ctx = document.getElementById("bar-chart").getContext("2d");
        if (window.myBar) {
            window.myBar.destroy();
        }
        window.myBar = new Chart(ctx, config);

        return () => {
            if (window.myBar) {
                window.myBar.destroy();
            }
        };
    }, [chartData]);

    return (
        <div className="container mx-auto p-4">
            <div className="bg-white p-6 rounded-lg shadow-lg mb-6">
                <h3 className="text-lg font-semibold mb-4">Filter Ticket Data</h3>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Start Date</label>
                        <input
                            type="date"
                            value={startDate}
                            onChange={(e) => setStartDate(e.target.value)}
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-500 focus:ring-opacity-50"
                        />
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">End Date</label>
                        <input
                            type="date"
                            value={endDate}
                            onChange={(e) => setEndDate(e.target.value)}
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-500 focus:ring-opacity-50"
                        />
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Destination</label>
                        <select
                            value={destination}
                            onChange={(e) => setDestination(e.target.value)}
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-500 focus:ring-opacity-50"
                        >
                            <option value="">All Destinations</option>
                            {destinations.map((dest) => (
                                <option key={dest} value={dest}>{dest}</option>
                            ))}
                        </select>
                    </div>
                </div>
            </div>
            <div className="relative flex flex-col min-w-0 break-words bg-white w-full mb-6 shadow-lg rounded">
                <div className="rounded-t mb-0 px-4 py-3 bg-transparent">
                    <div className="flex flex-wrap items-center">
                        <div className="relative w-full max-w-full flex-grow flex-1">
                            <h6 className="uppercase text-gray-500 mb-1 text-xs font-semibold">Performance</h6>
                            <h2 className="text-gray-800 text-xl font-semibold">Ticket Sales</h2>
                        </div>
                    </div>
                </div>
                <div className="p-4 flex-auto">
                    <div className="relative h-80">
                        <canvas id="bar-chart"></canvas>
                    </div>
                </div>
            </div>
        </div>
    );
}
