import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import CardTable from "components/Cards/CardTable.js";

export default function Express() {
  const history = useHistory();
  const [showForm, setShowForm] = useState(false);
  const [editingExpress, setEditingExpress] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [expressData, setExpressData] = useState({
    name: "",
    route: "",
    departureTime: "",
    arrivalTime: "",
    price: "",
    status: "active"
  });
  const [expressList, setExpressList] = useState([
    {
      id: 1,
      name: "Express 1",
      route: "Kigali - Huye",
      departureTime: "08:00",
      arrivalTime: "10:30",
      price: "2500",
      status: "active"
    },
    {
      id: 2,
      name: "Express 2",
      route: "Kigali - Musanze",
      departureTime: "10:00",
      arrivalTime: "12:00",
      price: "3000",
      status: "active"
    },
    {
      id: 3,
      name: "Express 3",
      route: "Kigali - Kibuye",
      departureTime: "12:00",
      arrivalTime: "14:30",
      price: "2800",
      status: "active"
    },
    {
      id: 4,
      name: "Express 4",
      route: "Kigali - Rusizi",
      departureTime: "14:00",
      arrivalTime: "16:30",
      price: "3200",
      status: "active"
    },
    {
      id: 5,
      name: "Express 5",
      route: "Kigali - Huye",
      departureTime: "16:00",
      arrivalTime: "18:30",
      price: "2500",
      status: "active"
    },
    {
      id: 6,
      name: "Express 6",
      route: "Kigali - Musanze",
      departureTime: "18:00",
      arrivalTime: "20:00",
      price: "3000",
      status: "active"
    }
  ]);

  // Filter express list based on search term
  const filteredExpressList = expressList.filter(express =>
    express.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    express.route.toLowerCase().includes(searchTerm.toLowerCase()) ||
    express.status.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setExpressData({
      ...expressData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editingExpress) {
      setExpressList(expressList.map(item => 
        item.id === editingExpress.id ? { ...expressData, id: item.id } : item
      ));
      setEditingExpress(null);
    } else {
      setExpressList([...expressList, { ...expressData, id: Date.now() }]);
    }
    setExpressData({
      name: "",
      route: "",
      departureTime: "",
      arrivalTime: "",
      price: "",
      status: "active"
    });
    setShowForm(false);
  };

  const handleEdit = (express) => {
    setEditingExpress(express);
    setExpressData(express);
    setShowForm(true);
  };

  const handleDelete = (id) => {
    setExpressList(expressList.filter(item => item.id !== id));
  };

  const handleCancel = () => {
    setShowForm(false);
    setEditingExpress(null);
    setExpressData({
      name: "",
      route: "",
      departureTime: "",
      arrivalTime: "",
      price: "",
      status: "active"
    });
  };

  const handleAddNew = () => {
    history.push("/admin/express/add");
  };

  return (
    <>
    {/* Express Management */ }
      <div className="flex flex-wrap mt-4">
        <div className="w-full xl:w-8/12 mb-12 xl:mb-0 px-4">
          <div className="bg-white rounded-lg shadow-md p-6">
            <div className="flex items-center justify-between mb-6">
              <h1 className="text-3xl font-bold text-gray-800">Express Management</h1>
              <div className="flex space-x-3">
                <button
                  onClick={handleAddNew}
                  className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition-colors"
                >
                  <i className="fas fa-plus mr-2"></i>
                  Add New Express
                </button>
                <button
                  onClick={() => setShowForm(!showForm)}
                  className="px-4 py-2 border border-gray-300 text-gray-700 rounded-md hover:bg-gray-50 transition-colors"
                >
                  <i className="fas fa-edit mr-2"></i>
                  Quick Add
                </button>
              </div>
            </div>

            {showForm && (
              <div className="mb-6 p-4 border border-gray-200 rounded-lg bg-gray-50">
                <h3 className="text-lg font-semibold mb-4">
                  {editingExpress ? "Edit Express" : "Add New Express"}
                </h3>
                <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <input
                    type="text"
                    name="name"
                    value={expressData.name}
                    onChange={handleInputChange}
                    placeholder="Express Name"
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                  <input
                    type="text"
                    name="route"
                    value={expressData.route}
                    onChange={handleInputChange}
                    placeholder="Route"
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                  <input
                    type="time"
                    name="departureTime"
                    value={expressData.departureTime}
                    onChange={handleInputChange}
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                  <input
                    type="time"
                    name="arrivalTime"
                    value={expressData.arrivalTime}
                    onChange={handleInputChange}
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                  <input
                    type="number"
                    name="price"
                    value={expressData.price}
                    onChange={handleInputChange}
                    placeholder="Price"
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                  <select
                    name="status"
                    value={expressData.status}
                    onChange={handleInputChange}
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="active">Active</option>
                    <option value="inactive">Inactive</option>
                  </select>
                  <div className="md:col-span-3 flex space-x-3">
                    <button
                      type="submit"
                      className="px-4 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition-colors"
                    >
                      {editingExpress ? "Update" : "Add"}
                    </button>
                    <button
                      type="button"
                      onClick={handleCancel}
                      className="px-4 py-2 border border-gray-300 text-gray-700 rounded-md hover:bg-gray-50 transition-colors"
                    >
                      Cancel
                    </button>
                  </div>
                </form>
              </div>
            )}

            <CardTable>
              <div className="rounded-t mb-0 px-4 py-3 border-0">
                <div className="flex flex-wrap items-center justify-between">
                  <div className="relative w-full px-4 max-w-full flex-grow flex-1">
                    <h3 className="font-semibold text-lg text-blueGray-700">
                      Express Routes
                    </h3>
                  </div>
                  <div className="flex space-x-2">
                    <button className="px-4 py-2 bg-amber-500 text-white rounded-md hover:bg-amber-600 transition-colors font-medium">
                      <i className="fas fa-filter mr-2"></i>
                      Filter
                    </button>
                  </div>
                </div>
              </div>
              <div className="block w-full overflow-x-auto">
                <table className="items-center w-full bg-transparent border-collapse">
                  <thead>
                    <tr>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Name
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Route
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Departure
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Arrival
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Price
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Status
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Actions
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredExpressList.map((express) => (
                      <tr key={express.id}>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          {express.name}
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          {express.route}
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          {express.departureTime}
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          {express.arrivalTime}
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          RWF {express.price}
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          <span className={`px-2 py-1 rounded-full text-xs ${
                            express.status === 'active' 
                              ? 'bg-green-100 text-green-800' 
                              : 'bg-red-100 text-red-800'
                          }`}>
                            {express.status}
                          </span>
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          <div className="flex space-x-2">
                            <button
                              onClick={() => handleEdit(express)}
                              className="px-3 py-1 bg-indigo-600 text-white rounded text-xs hover:bg-indigo-700 font-medium"
                            >
                              <i className="fas fa-edit mr-1"></i>
                              Edit
                            </button>
                            <button
                              onClick={() => handleDelete(express.id)}
                              className="px-3 py-1 bg-red-600 text-white rounded text-xs hover:bg-red-700 font-medium"
                            >
                              <i className="fas fa-trash mr-1"></i>
                              Delete
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </CardTable>
          </div>
        </div>
      </div>
    </>
  );
}
