import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import CardTable from "components/Cards/CardTable.js";

export default function Express() {
  const history = useHistory();
  const API_URL = "http://localhost:5000/api/express";

  const [searchTerm, setSearchTerm] = useState("");
  const [expresses, setExpressList] = useState([]);
 
  useEffect(() => {
    fetchExpressList();
  }, []);

  const fetchExpressList = async () => {
    try {
      const response = await fetch(`${API_URL}/all`, {
        method: 'GET',
        credentials: 'include', 
      });
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      console.log("Fetched data:", data);
      setExpressList(data);
    } catch (error) {
      console.error("Error fetching express list:", error);
    }
  };
 
  const filteredExpressList = expresses.filter(
    (express) =>
      express.expressName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      express.expressDescription?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this express route?")) return;

    try {
      const response = await fetch(`${API_URL}/delete/${id}`, { method: "DELETE" });
      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);

      setExpressList(expresses.filter((item) => item.id !== id));
      alert("Express route deleted successfully!");
    } catch (error) {
      console.error("Error deleting express:", error);
      alert("Failed to delete express: " + error.message);
    }
  };

  const handleAddNew = () => {
    history.push("/admin/express/add");
  };

  const handleUpdate = (id) => {
    history.push(`/admin/express/edit/${id}`);
  };

  return (
    <>
      {/* Show Express */}
      <div className="flex flex-wrap mt-4">
        <div className="w-full xl:w-8/12 mb-12 xl:mb-0 px-4">
          <div className="bg-white rounded-lg shadow-md p-6">
            <CardTable>
              <div className="rounded-t mb-0 px-4 py-3 border-0">
                <div className="flex flex-wrap items-center justify-between">
                  <div className="relative w-full px-4 max-w-full flex-grow flex-1">
                    <h3 className="font-semibold text-lg text-blueGray-700">
                      Express Routes
                    </h3>
                  </div>
                  <div className="flex space-x-2">
                    <div className="relative">
                      <input
                        type="text"
                        placeholder="Search express routes..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="px-4 py-2 pl-10 pr-4 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      />
                    </div>
                    &nbsp;
                    <button
                      onClick={handleAddNew}
                      className="px-4 py-2 bg-teal-500 text-white rounded-md hover:bg-teal-600 transition-colors"
                    >
                      <i className="fas fa-plus mr-2"></i> Add
                    </button>
                  </div>
                </div>
              </div>
              <div className="block w-full overflow-x-auto">
                <table className="items-center w-full bg-transparent border-collapse">
                  <thead>
                    <tr>
                      <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50 text-blueGray-500">
                        #
                      </th>
                      <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50 text-blueGray-500">
                        Logo
                      </th>
                      <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50 text-blueGray-500">
                        Name
                      </th>
                      <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50 text-blueGray-500">
                        Description
                      </th>
                      <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50 text-blueGray-500">
                        Actions
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredExpressList.map((express, index) => ( 
                      <tr key={express.id}>
                        <td className="border-t-0 px-6 py-4 text-xs whitespace-nowrap">
                          {index + 1}
                        </td>
                        <td className="border-t-0 px-6 py-4 text-xs whitespace-nowrap">
                          {express.expressLogo ? (
                            <img
                               src={`http://localhost:5000/uploads/${express.expressLogo}`}
                               alt={express.expressName}
                               className="h-12 w-12 object-cover rounded"
                            />
                          ) : (
                            "No logo"
                          )}
                        </td>
                        <td className="border-t-0 px-6 py-4 text-xs whitespace-nowrap">
                          {express.expressName}
                        </td>
                        <td className="border-t-0 px-6 py-4 text-xs whitespace-nowrap">
                          {express.expressDescription?.length > 50
                            ? express.expressDescription.substring(0, 50) + "..."
                            : express.expressDescription}
                        </td>
                        <td className="border-t-0 px-6 py-4 text-xs whitespace-nowrap">
                          <div className="flex space-x-2">
                            <button
                              onClick={() => handleUpdate(express.id)}
                              className="px-3 py-1 bg-blueGray-600 text-white rounded text-xs hover:bg-blueGray-700 font-medium"
                            >
                              <i className="fas fa-edit mr-1"></i>
                            </button>
                            &nbsp;
                            <button
                              onClick={() => handleDelete(express.id)}
                              className="px-3 py-1 bg-red-600 text-white rounded text-xs hover:bg-red-700 font-medium"
                            >
                              <i className="fas fa-trash mr-1"></i>
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </CardTable>
          </div>
        </div>
      </div>
    </>
  );
}
