import React from "react";
import { Switch, Route, Redirect, useLocation } from "react-router-dom";


import AdminNavbar from "components/Navbars/AdminNavbar.js";
import Sidebar from "components/Sidebar/Sidebar.js";
import HeaderStats from "components/Headers/HeaderStats.js";
import FooterAdmin from "components/Footers/FooterAdmin.js";


import Dashboard from "views/admin/Dashboard.js"; 
import Settings from "views/admin/Settings.js";
import Tables from "views/admin/Tables.js";
import Express from "views/admin/Express.js";
import Users from "views/admin/Users.js";
import Buses from "views/admin/Buses.js";
import Destinations from "views/admin/Destinations.js";
import Schedules from "views/admin/Schedules.js";
import Tickets from "views/admin/Tickets.js";
import Reports from "views/admin/Reports.js";
import ExpressAdd from "views/admin/ExpressAdd.js";
import UserAdd from "views/admin/UserAdd.js";
import BusAdd from "views/admin/BusAdd.js";
import DestinationAdd from "views/admin/DestinationAdd.js";
import ScheduleAdd from "views/admin/ScheduleAdd.js";
import TicketAdd from "views/admin/TicketAdd.js";

export default function Admin() {
  const location = useLocation();
  const isDashboard = location.pathname === "/admin/dashboard";

  return (
    <>
      <Sidebar />
      <div className="relative md:ml-64 bg-blueGray-100">
        <AdminNavbar />
        {/* Header - Only show on Dashboard */}
        {isDashboard && <HeaderStats />}
        <div className={`px-4 md:px-10 mx-auto w-full ${isDashboard ? '-m-24' : 'mt-4'}`}>
          <Switch>
            <Route path="/admin/dashboard" exact component={Dashboard} /> 
            <Route path="/admin/settings" exact component={Settings} />
            <Route path="/admin/tables" exact component={Tables} />
            <Route path="/admin/express" exact component={Express} />
            <Route path="/admin/users" exact component={Users} />
            <Route path="/admin/buses" exact component={Buses} />
            <Route path="/admin/destinations" exact component={Destinations} />
            <Route path="/admin/schedules" exact component={Schedules} />
            <Route path="/admin/tickets" exact component={Tickets} />
            <Route path="/admin/reports" exact component={Reports} />
            <Route path="/admin/express/add" exact component={ExpressAdd} />
            <Route path="/admin/users/add" exact component={UserAdd} />
            <Route path="/admin/buses/add" exact component={BusAdd} />
            <Route path="/admin/destinations/add" exact component={DestinationAdd} />
            <Route path="/admin/schedules/add" exact component={ScheduleAdd} />
            <Route path="/admin/tickets/add" exact component={TicketAdd} />
            <Redirect from="/admin" to="/admin/dashboard" />
          </Switch>
          <FooterAdmin />
        </div>
      </div>
    </>
  );
}
