import React, { useState } from "react";
import { useHistory } from "react-router-dom";

export default function ExpressAdd() {
  const history = useHistory();
  const API_URL = "http://localhost:5000/api/express";

  const [expressData, setExpressData] = useState({
    expressName: "",
    expressDescription: "",
    expressLogo: null, // Store file object
    expressProfile: null, // Store file object
  });

  const [isLoading, setIsLoading] = useState(false);

  const handleInputChange = (e) => {
    const { name, value, files } = e.target;
    if (files) {
      setExpressData({
        ...expressData,
        [name]: files[0], // Store the first file
      });
    } else {
      setExpressData({
        ...expressData,
        [name]: value,
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    // Basic validation
    if (!expressData.expressName || !expressData.expressDescription) {
      alert("Please fill in all required fields.");
      setIsLoading(false);
      return;
    }

    // Validate file types (images only)
    if (expressData.expressLogo && !expressData.expressLogo.type.startsWith("image/")) {
      alert("Logo must be an image file.");
      setIsLoading(false);
      return;
    }
    if (expressData.expressProfile && !expressData.expressProfile.type.startsWith("image/")) {
      alert("Profile must be an image file.");
      setIsLoading(false);
      return;
    }

    // Prepare form data
    const formData = new FormData();
    formData.append("expressName", expressData.expressName);
    formData.append("expressDescription", expressData.expressDescription);
    if (expressData.expressLogo) {
      formData.append("expressLogo", expressData.expressLogo);
    }
    if (expressData.expressProfile) {
      formData.append("expressProfile", expressData.expressProfile);
    }

    try {
      const response = await fetch(`${API_URL}/register`, {
        method: "POST",
        body: formData,
        credentials: "include", // Match CORS configuration
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const result = await response.json();
      console.log("Express added:", result);
      alert("Express route added successfully!");
      history.push("/admin/express");
    } catch (error) {
      console.error("Error adding express:", error);
      alert("Failed to add express route. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleCancel = () => {
    history.push("/admin/express");
  };

  return (
    <>
    {}
      <div className="flex flex-wrap">
        <div className="w-full xl:w-8/12 mb-12 xl:mb-0 px-4 mx-auto">
          <div className="relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded-lg bg-white border-0">
            <div className="rounded-t bg-white mb-0 px-6 py-6">
              <div className="text-center flex justify-between">
                <h6 className="text-blueGray-700 text-xl font-bold">Add Express Route</h6>
                <button
                  onClick={handleCancel}
                  className="bg-red-500 text-white active:bg-red-600 font-bold uppercase text-xs px-4 py-2 rounded shadow hover:shadow-md outline-none focus:outline-none mr-1 ease-linear transition-all duration-150"
                  type="button"
                >
                  Back
                </button>
              </div>
            </div>

            <form onSubmit={handleSubmit} className="flex-auto px-4 lg:px-10 py-10 pt-0">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Express Name *
                  </label>
                  <input
                    type="text"
                    name="expressName"
                    value={expressData.expressName}
                    onChange={handleInputChange}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="Enter express name"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Logo *
                  </label>
                  <input
                    type="file"
                    name="expressLogo"
                    onChange={handleInputChange}
                    accept="image/*"
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    required
                  />
                  {expressData.expressLogo && (
                    <img
                      src={URL.createObjectURL(expressData.expressLogo)}
                      alt="Logo Preview"
                      className="mt-2 h-20 w-20 object-cover"
                    />
                  )}
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Bus Profile *
                  </label>
                  <input
                    type="file"
                    name="expressProfile"
                    onChange={handleInputChange}
                    accept="image/*"
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    required
                  />
                  {expressData.expressProfile && (
                    <img
                      src={URL.createObjectURL(expressData.expressProfile)}
                      alt="Profile Preview"
                      className="mt-2 h-20 w-20 object-cover"
                    />
                  )}
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Description *
                  </label>
                  <textarea
                    name="expressDescription"
                    value={expressData.expressDescription}
                    onChange={handleInputChange}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="Enter express description"
                    required
                  />
                </div>
              </div>

              <div className="flex justify-end space-x-4 pt-6 border-t border-gray-200">
                <button
                  type="button"
                  onClick={handleCancel}
                  className="px-6 py-3 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-50 transition-colors"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  disabled={isLoading}
                  className="px-6 py-3 bg-teal-500 text-white rounded-lg hover:bg-teal-600 transition-colors disabled:bg-teal-300"
                >
                  <i className="fas fa-save mr-2"></i>
                  {isLoading ? "Saving..." : "Save Express"}
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}
