import React, { useEffect, useState } from "react";
import Chart from "chart.js";

function getCurrentWeekRange() {
  const now = new Date();
  const dayOfWeek = now.getDay();
  const start = new Date(now);
  start.setDate(now.getDate() - (dayOfWeek === 0 ? 6 : dayOfWeek - 1));
  start.setHours(0, 0, 0, 0);

  const end = new Date(start);
  end.setDate(start.getDate() + 6);
  end.setHours(23, 59, 59, 999);

  return { start, end };
}

export default function CardBarChart() {
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    const { start, end } = getCurrentWeekRange();

    fetch(`/api/reports/date-range?start=${start.getTime()}&end=${end.getTime()}`, {
      headers: {
        "Authorization": `Bearer ${localStorage.getItem("token")}`
      }
    })
      .then(res => res.json())
      .then(tickets => {
        const days = ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"];
        const paid = new Array(7).fill(0);
        const unpaid = new Array(7).fill(0);

        tickets.forEach(ticket => {
          const d = new Date(ticket.createdAt);
          let idx = d.getDay();
          idx = idx === 0 ? 6 : idx - 1;
          if (ticket.isPaid) {
            paid[idx]++;
          } else {
            unpaid[idx]++;
          }
        });

        setChartData({
          labels: days,
          datasets: [
            {
              label: "Paid Tickets",
              backgroundColor: "rgba(34,197,94,0.8)",
              borderColor: "rgba(34,197,94,1)",
              data: paid,
              barThickness: 8,
            },
            {
              label: "Unpaid Tickets",
              backgroundColor: "rgba(239,68,68,0.8)",
              borderColor: "rgba(239,68,68,1)",
              data: unpaid,
              barThickness: 8,
            },
          ],
        });
      })
      .catch(err => console.error("Error fetching bar chart data:", err));
  }, []);

  useEffect(() => {
    if (!chartData) return;

    const ctx = document.getElementById("bar-chart").getContext("2d");
    if (window.myBar) window.myBar.destroy();

    window.myBar = new Chart(ctx, {
      type: "bar",
      data: chartData,
      options: { responsive: true, maintainAspectRatio: false }
    });

    return () => {
      if (window.myBar) window.myBar.destroy();
    };
  }, [chartData]);

  return (
    <div className="relative flex flex-col w-full mb-6 shadow-lg rounded bg-white">
      <div className="p-4 flex-auto">
        <div className="relative h-350-px">
          <canvas id="bar-chart"></canvas>
        </div>
      </div>
    </div>
  );
}
