import React, { useState, useEffect } from "react";
import CardStats from "components/Cards/CardStats.js";
import CardLineChart from "components/Cards/CardLineChart.js";
import CardBarChart from "components/Cards/CardBarChart.js";
import CardTable from "components/Cards/CardTable.js";

export default function Reports() {
  const [reportPeriod, setReportPeriod] = useState("weekly");
  const [reportType, setReportType] = useState("overview");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [destination, setDestination] = useState("");
  const [destinations, setDestinations] = useState([]);

  const [totalRevenue, setTotalRevenue] = useState(0);
  const [totalTickets, setTotalTickets] = useState(0);
  const [revenueData, setRevenueData] = useState({ labels: [], datasets: [] });
  const [ticketSalesData, setTicketSalesData] = useState({ labels: [], datasets: [] });
  const [tableData, setTableData] = useState([]);
  const [loading, setLoading] = useState(false);

  // Fetch destinations on component mount
  useEffect(() => {
    fetchDestinations();
  }, []);

  const fetchDestinations = async () => {
    try {
      const token = localStorage.getItem("authToken");
      const response = await fetch("http://localhost:5000/api/reports/destinations", {
        headers: {
          "Authorization": `Bearer ${token}`
        }
      });

      if (!response.ok) {
        throw new Error("Failed to fetch destinations");
      }

      const data = await response.json();
      setDestinations(data);
    } catch (error) {
      console.error("Error fetching destinations:", error);
      alert("Error fetching destinations: " + error.message);
    }
  };

  const handleGenerateReport = async () => {
    if (!startDate || !endDate) {
      alert("Please select both start and end dates");
      return;
    }

    setLoading(true);
    try {
      const token = localStorage.getItem("authToken");
      const startMillis = new Date(startDate).getTime();
      const endMillis = new Date(endDate).getTime();

      let url;
      if (reportType === "destination" && destination) {
        url = `http://localhost:5000/api/reports/destination?destination=${destination}&start=${startMillis}&end=${endMillis}`;
      } else {
        url = `http://localhost:5000/api/reports/date-range?start=${startMillis}&end=${endMillis}`;
      }

      const response = await fetch(url, {
        headers: {
          "Authorization": `Bearer ${token}`
        }
      });

      if (!response.ok) {
        if (response.status === 401) {
          throw new Error("Unauthorized. Please log in again.");
        }
        throw new Error("Failed to fetch report data");
      }

      const data = await response.json();
      processReportData(data);
      alert("Report generated successfully!");
    } catch (error) {
      console.error(error);
      alert("Error generating report: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  const processReportData = (data) => {
    // Calculate totals for cards
    const totalRev = data.reduce((sum, t) => sum + t.totalAmount, 0);
    const totalTix = data.length;

    setTotalRevenue(totalRev);
    setTotalTickets(totalTix);
    setTableData(data);

    // Group data by date for charts
    const groupedByDate = data.reduce((acc, ticket) => {
      const dateStr = new Date(ticket.date).toLocaleDateString();
      if (!acc[dateStr]) {
        acc[dateStr] = {
          revenue: 0,
          tickets: 0
        };
      }
      acc[dateStr].revenue += ticket.totalAmount;
      acc[dateStr].tickets += 1;
      return acc;
    }, {});

    const sortedDates = Object.keys(groupedByDate).sort((a, b) => new Date(a) - new Date(b));

    // Prepare revenue chart data
    const revenueChart = {
      labels: sortedDates,
      datasets: [
        {
          label: "Revenue (RWF)",
          data: sortedDates.map(date => groupedByDate[date].revenue),
          borderColor: "rgb(59,130,246)",
          backgroundColor: "rgba(59,130,246,0.1)",
          tension: 0.1,
          fill: true
        },
      ],
    };
    setRevenueData(revenueChart);

    // Prepare ticket sales chart data
    const ticketChart = {
      labels: sortedDates,
      datasets: [
        {
          label: "Tickets Sold",
          data: sortedDates.map(date => groupedByDate[date].tickets),
          backgroundColor: "rgba(34,197,94,0.8)",
          borderColor: "rgba(34,197,94,1)",
          borderWidth: 1
        },
      ],
    };
    setTicketSalesData(ticketChart);
  };

  const handleExportReport = () => {
    if (tableData.length === 0) {
      alert("No data to export. Please generate a report first.");
      return;
    }

    // Convert data to CSV
    const headers = ["Ticket Number", "Passenger Name", "Phone", "Bus Plate", "Seat", "Amount", "Payment Status", "Date", "Destination"];
    const csvData = tableData.map(ticket => [
      ticket.ticketNumber,
      ticket.names,
      ticket.phone,
      ticket.busPlateNo,
      ticket.seatNo,
      ticket.totalAmount,
      ticket.payState,
      new Date(ticket.date).toLocaleDateString(),
      ticket.schedule?.destination || "N/A"
    ]);

    const csvContent = [headers, ...csvData]
      .map(row => row.map(field => `"${field}"`).join(","))
      .join("\n");

    // Create download link
    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.setAttribute("href", url);
    link.setAttribute("download", `tegabus_report_${new Date().toISOString().split('T')[0]}.csv`);
    link.style.visibility = "hidden";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);

    alert("Report exported successfully!");
  };

  return (
    <>
      <div className="flex flex-wrap mt-4">
        <div className="w-full xl:w-8/12 mb-12 xl:mb-0 px-4">
          <CardStats
            statSubtitle="Total Revenue"
            statTitle={`RWF ${totalRevenue.toLocaleString()}`}
            statArrow="up"
            statPercent="12"
            statPercentColor="text-emerald-500"
            statDescripiron="Since last month"
            statIconName="fas fa-dollar-sign"
            statIconColor="bg-emerald-500"
          />
        </div>
        <div className="w-full xl:w-4/12 px-4">
          <CardStats
            statSubtitle="Total Tickets"
            statTitle={totalTickets}
            statArrow="up"
            statPercent="3.48"
            statPercentColor="text-emerald-500"
            statDescripiron="Since last week"
            statIconName="fas fa-ticket-alt"
            statIconColor="bg-red-500"
          />
        </div>
      </div>

      {/* Date Range Selection */}
      <div className="flex flex-wrap mt-4">
        <div className="w-full px-4">
          <div className="bg-white rounded-lg shadow-md p-6 mb-6">
            <h3 className="text-lg font-semibold text-gray-800 mb-6">
              Report Configuration
            </h3>

            <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Report Type
                </label>
                <select
                  value={reportType}
                  onChange={(e) => setReportType(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="overview">Overview</option>
                  <option value="revenue">Revenue</option>
                  <option value="tickets">Tickets</option>
                  <option value="destination">Destination</option>
                </select>
              </div>

              {reportType === "destination" && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Destination
                  </label>
                  <select
                    value={destination}
                    onChange={(e) => setDestination(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="">Select Destination</option>
                    {destinations.map((dest) => (
                      <option key={dest} value={dest}>
                        {dest}
                      </option>
                    ))}
                  </select>
                </div>
              )}

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Start Date
                </label>
                <input
                  type="date"
                  value={startDate}
                  onChange={(e) => setStartDate(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  End Date
                </label>
                <input
                  type="date"
                  value={endDate}
                  onChange={(e) => setEndDate(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
            </div>

            <div className="flex space-x-4">
              <button
                onClick={handleGenerateReport}
                disabled={loading}
                className="px-6 py-2 bg-red-500 text-white rounded-md hover:bg-red-600 transition-colors font-medium disabled:bg-gray-400 disabled:cursor-not-allowed"
              >
                {loading ? (
                  <>
                    <i className="fas fa-spinner fa-spin mr-2"></i>
                    Generating...
                  </>
                ) : (
                  <>
                    <i className="fas fa-chart-line mr-2"></i>
                    Generate Report
                  </>
                )}
              </button>
              <button
                onClick={handleExportReport}
                disabled={tableData.length === 0}
                className="px-6 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition-colors font-medium disabled:bg-gray-400 disabled:cursor-not-allowed"
              >
                <i className="fas fa-download mr-2"></i>
                Export Report
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Charts */}
      {tableData.length > 0 && (
        <>
          <div className="flex flex-wrap mt-4">
            <div className="w-full xl:w-8/12 mb-12 xl:mb-0 px-4">
              <CardLineChart chartData={revenueData} />
            </div>
            <div className="w-full xl:w-4/12 px-4">
              <CardBarChart chartData={ticketSalesData} />
            </div>
          </div>

          {/* Table */}
          <div className="flex flex-wrap mt-4">
            <div className="w-full px-4">
              <CardTable>
                <div className="rounded-t mb-0 px-4 py-3 border-0">
                  <div className="flex flex-wrap items-center justify-between">
                    <div className="relative w-full px-4 max-w-full flex-grow flex-1">
                      <h3 className="font-semibold text-lg text-blueGray-700">
                        Detailed Report Data
                      </h3>
                    </div>
                  </div>
                </div>
                <div className="block w-full overflow-x-auto">
                  <table className="items-center w-full bg-transparent border-collapse">
                    <thead>
                      <tr>
                        <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50">
                          Ticket Number
                        </th>
                        <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50">
                          Passenger
                        </th>
                        <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50">
                          Date
                        </th>
                        <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50">
                          Destination
                        </th>
                        <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50">
                          Bus Plate
                        </th>
                        <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50">
                          Seat
                        </th>
                        <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50">
                          Amount
                        </th>
                        <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50">
                          Status
                        </th>
                      </tr>
                    </thead>
                    <tbody>
                      {tableData.map((ticket) => (
                        <tr key={ticket.id}>
                          <td className="px-6 py-4">{ticket.ticketNumber}</td>
                          <td className="px-6 py-4">
                            <div>{ticket.names}</div>
                            <div className="text-xs text-gray-500">{ticket.phone}</div>
                          </td>
                          <td className="px-6 py-4">{new Date(ticket.date).toLocaleDateString()}</td>
                          <td className="px-6 py-4">{ticket.schedule?.destination || "N/A"}</td>
                          <td className="px-6 py-4">{ticket.busPlateNo}</td>
                          <td className="px-6 py-4">{ticket.seatNo}</td>
                          <td className="px-6 py-4">{`RWF ${ticket.totalAmount.toLocaleString()}`}</td>
                          <td className="px-6 py-4">
                            <span className={`px-2 py-1 rounded-full text-xs ${
                              ticket.payState === "PAID" 
                                ? "bg-green-100 text-green-800" 
                                : "bg-orange-100 text-orange-800"
                            }`}>
                              {ticket.payState}
                            </span>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </CardTable>
            </div>
          </div>
        </>
      )}
    </>
  );
}
