import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import CardTable from "components/Cards/CardTable.js";

export default function Tickets() {
  const history = useHistory();
  const [showForm, setShowForm] = useState(false);
  const [editingTicket, setEditingTicket] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [ticketData, setTicketData] = useState({
    passenger: "",
    schedule: "",
    seatNumber: "",
    price: "",
    status: "confirmed",
    paymentMethod: "cash"
  });
  const [ticketList, setTicketList] = useState([
    {
      id: 1,
      passenger: "John Doe",
      schedule: "Kigali - Huye (Jan 15, 2024, 08:00)",
      seatNumber: "A1",
      price: "2500",
      status: "confirmed",
      paymentMethod: "cash"
    },
    {
      id: 2,
      passenger: "Jane Smith",
      schedule: "Kigali - Musanze (Jan 15, 2024, 10:00)",
      seatNumber: "B5",
      price: "3000",
      status: "confirmed",
      paymentMethod: "mobile_money"
    },
    {
      id: 3,
      passenger: "Mike Johnson",
      schedule: "Kigali - Kibuye (Jan 15, 2024, 12:00)",
      seatNumber: "C12",
      price: "2800",
      status: "pending",
      paymentMethod: "card"
    },
    {
      id: 4,
      passenger: "Sarah Wilson",
      schedule: "Kigali - Rusizi (Jan 15, 2024, 14:00)",
      seatNumber: "D8",
      price: "3200",
      status: "cancelled",
      paymentMethod: "bank_transfer"
    },
    {
      id: 5,
      passenger: "David Brown",
      schedule: "Kigali - Huye (Jan 16, 2024, 08:00)",
      seatNumber: "A3",
      price: "2500",
      status: "confirmed",
      paymentMethod: "mobile_money"
    },
    {
      id: 6,
      passenger: "Lisa Davis",
      schedule: "Kigali - Musanze (Jan 16, 2024, 10:00)",
      seatNumber: "B7",
      price: "3000",
      status: "confirmed",
      paymentMethod: "cash"
    },
    {
      id: 7,
      passenger: "Tom Wilson",
      schedule: "Kigali - Kibuye (Jan 16, 2024, 12:00)",
      seatNumber: "C15",
      price: "2800",
      status: "pending",
      paymentMethod: "card"
    }
  ]);

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5;

  // Calculate pagination
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  
  // Filter ticket list based on search term
  const filteredTicketList = ticketList.filter(ticket =>
    ticket.passenger.toLowerCase().includes(searchTerm.toLowerCase()) ||
    ticket.status.toLowerCase().includes(searchTerm.toLowerCase()) ||
    ticket.paymentMethod.toLowerCase().includes(searchTerm.toLowerCase())
  );
  
  const currentItems = filteredTicketList.slice(indexOfFirstItem, indexOfLastItem);
  const totalPages = Math.ceil(filteredTicketList.length / itemsPerPage);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTicketData({
      ...ticketData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editingTicket) {
      setTicketList(ticketList.map(item => 
        item.id === editingTicket.id ? { ...ticketData, id: item.id } : item
      ));
      setEditingTicket(null);
    } else {
      setTicketList([...ticketList, { ...ticketData, id: Date.now() }]);
    }
    setTicketData({
      passenger: "",
      schedule: "",
      seatNumber: "",
      price: "",
      status: "confirmed",
      paymentMethod: "cash"
    });
    setShowForm(false);
  };

  const handleEdit = (ticket) => {
    setEditingTicket(ticket);
    setTicketData(ticket);
    setShowForm(true);
  };

  const handleDelete = (id) => {
    setTicketList(ticketList.filter(item => item.id !== id));
  };

  const handleCancel = () => {
    setShowForm(false);
    setEditingTicket(null);
    setTicketData({
      passenger: "",
      schedule: "",
      seatNumber: "",
      price: "",
      status: "confirmed",
      paymentMethod: "cash"
    });
  };

  const handleAddNew = () => {
    history.push("/admin/tickets/add");
  };

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <>
      {/* Tickets Management */}
      <div className="flex flex-wrap mt-4">
        <div className="w-full xl:w-8/12 mb-12 xl:mb-0 px-4">
          <div className="bg-white rounded-lg shadow-md p-6">
            <div className="flex items-center justify-between mb-6">
              <h1 className="text-3xl font-bold text-gray-800">Tickets Management</h1>
              <div className="flex space-x-3">
                <button
                  onClick={handleAddNew}
                  className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition-colors"
                >
                  <i className="fas fa-plus mr-2"></i>
                  Add New Ticket
                </button>
                <button
                  onClick={() => setShowForm(!showForm)}
                  className="px-4 py-2 border border-gray-300 text-gray-700 rounded-md hover:bg-gray-50 transition-colors"
                >
                  <i className="fas fa-edit mr-2"></i>
                  Quick Add
                </button>
              </div>
            </div>

            {showForm && (
              <div className="mb-6 p-4 border border-gray-200 rounded-lg bg-gray-50">
                <h3 className="text-lg font-semibold mb-4">
                  {editingTicket ? "Edit Ticket" : "Add New Ticket"}
                </h3>
                <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <select
                    name="passenger"
                    value={ticketData.passenger}
                    onChange={handleInputChange}
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  >
                    <option value="">Select Passenger</option>
                    <option value="John Doe">John Doe</option>
                    <option value="Jane Smith">Jane Smith</option>
                    <option value="Mike Johnson">Mike Johnson</option>
                    <option value="Sarah Wilson">Sarah Wilson</option>
                  </select>
                  <select
                    name="schedule"
                    value={ticketData.schedule}
                    onChange={handleInputChange}
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  >
                    <option value="">Select Schedule</option>
                    <option value="Kigali-Huye 2024-01-15 08:00">Kigali - Huye (Jan 15, 2024, 08:00)</option>
                    <option value="Kigali-Musanze 2024-01-15 10:00">Kigali - Musanze (Jan 15, 2024, 10:00)</option>
                    <option value="Kigali-Kibuye 2024-01-15 12:00">Kigali - Kibuye (Jan 15, 2024, 12:00)</option>
                  </select>
                  <input
                    type="text"
                    name="seatNumber"
                    value={ticketData.seatNumber}
                    onChange={handleInputChange}
                    placeholder="Seat Number"
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                  <input
                    type="number"
                    name="price"
                    value={ticketData.price}
                    onChange={handleInputChange}
                    placeholder="Price (RWF)"
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                  <select
                    name="status"
                    value={ticketData.status}
                    onChange={handleInputChange}
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="confirmed">Confirmed</option>
                    <option value="pending">Pending</option>
                    <option value="cancelled">Cancelled</option>
                    <option value="completed">Completed</option>
                  </select>
                  <select
                    name="paymentMethod"
                    value={ticketData.paymentMethod}
                    onChange={handleInputChange}
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="cash">Cash</option>
                    <option value="mobile_money">Mobile Money</option>
                    <option value="card">Card</option>
                    <option value="bank_transfer">Bank Transfer</option>
                  </select>
                  <div className="md:col-span-3 flex space-x-3">
                    <button
                      type="submit"
                      className="px-4 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition-colors"
                    >
                      {editingTicket ? "Update" : "Add"}
                    </button>
                    <button
                      type="button"
                      onClick={handleCancel}
                      className="px-4 py-2 border border-gray-300 text-gray-700 rounded-md hover:bg-gray-50 transition-colors"
                    >
                      Cancel
                    </button>
                  </div>
                </form>
              </div>
            )}

            <CardTable>
              <div className="rounded-t mb-0 px-4 py-3 border-0">
                <div className="flex flex-wrap items-center justify-between">
                  <div className="relative w-full px-4 max-w-full flex-grow flex-1">
                    <h3 className="font-semibold text-lg text-blueGray-700">
                      Tickets List
                    </h3>
                  </div>
                  <div className="flex space-x-2">
                    <div className="relative">
                      <input
                        type="text"
                        placeholder="Search tickets..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="px-4 py-2 pl-10 pr-4 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      />
                      <i className="fas fa-search absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400"></i>
                    </div>
                    <button className="px-4 py-2 bg-amber-500 text-white rounded-md hover:bg-amber-600 transition-colors font-medium">
                      <i className="fas fa-filter mr-2"></i>
                      Filter
                    </button>
                  </div>
                </div>
              </div>
              <div className="block w-full overflow-x-auto">
                <table className="items-center w-full bg-transparent border-collapse">
                  <thead>
                    <tr>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Passenger
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Schedule
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Seat
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Price
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Status
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Payment
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Actions
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    {currentItems.map((ticket) => (
                      <tr key={ticket.id}>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          {ticket.passenger}
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          <div className="max-w-xs truncate" title={ticket.schedule}>
                            {ticket.schedule}
                          </div>
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          {ticket.seatNumber}
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          RWF {ticket.price}
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          <span className={`px-2 py-1 rounded-full text-xs ${
                            ticket.status === 'confirmed' 
                              ? 'bg-green-100 text-green-800' 
                              : ticket.status === 'pending'
                              ? 'bg-yellow-100 text-yellow-800'
                              : ticket.status === 'cancelled'
                              ? 'bg-red-100 text-red-800'
                              : 'bg-blue-100 text-blue-800'
                          }`}>
                            {ticket.status}
                          </span>
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          <span className={`px-2 py-1 rounded-full text-xs ${
                            ticket.paymentMethod === 'cash' ? 'bg-gray-100 text-gray-800' :
                            ticket.paymentMethod === 'mobile_money' ? 'bg-green-100 text-green-800' :
                            ticket.paymentMethod === 'card' ? 'bg-blue-100 text-blue-800' :
                            'bg-purple-100 text-purple-800'
                          }`}>
                            {ticket.paymentMethod.replace('_', ' ')}
                          </span>
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          <div className="flex space-x-2">
                            <button
                              onClick={() => handleEdit(ticket)}
                              className="px-3 py-1 bg-indigo-600 text-white rounded text-xs hover:bg-indigo-700 font-medium"
                            >
                              <i className="fas fa-edit mr-1"></i>
                              Edit
                            </button>
                            <button
                              onClick={() => handleDelete(ticket.id)}
                              className="px-3 py-1 bg-red-600 text-white rounded text-xs hover:bg-red-700 font-medium"
                            >
                              <i className="fas fa-trash mr-1"></i>
                              Delete
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              
              {/* Pagination */}
              {totalPages > 1 && (
                <div className="px-6 py-3 border-t border-gray-200">
                  <div className="flex items-center justify-between">
                    <div className="text-sm text-gray-700">
                      Showing {indexOfFirstItem + 1} to {Math.min(indexOfLastItem, filteredTicketList.length)} of {filteredTicketList.length} results
                    </div>
                    <div className="flex space-x-2">
                      <button
                        onClick={() => handlePageChange(currentPage - 1)}
                        disabled={currentPage === 1}
                        className={`px-3 py-1 rounded-md text-sm font-medium ${
                          currentPage === 1
                            ? 'bg-gray-200 text-gray-500 cursor-not-allowed'
                            : 'bg-indigo-600 text-white hover:bg-indigo-700'
                        }`}
                      >
                        <i className="fas fa-chevron-left mr-1"></i>
                        Previous
                      </button>
                      
                      {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => (
                        <button
                          key={page}
                          onClick={() => handlePageChange(page)}
                          className={`px-3 py-1 rounded-md text-sm font-medium ${
                            currentPage === page
                              ? 'bg-indigo-600 text-white'
                              : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                          }`}
                        >
                          {page}
                        </button>
                      ))}
                      
                      <button
                        onClick={() => handlePageChange(currentPage + 1)}
                        disabled={currentPage === totalPages}
                        className={`px-3 py-1 rounded-md text-sm font-medium ${
                          currentPage === totalPages
                            ? 'bg-gray-200 text-gray-500 cursor-not-allowed'
                            : 'bg-indigo-600 text-white hover:bg-indigo-700'
                        }`}
                      >
                        Next
                        <i className="fas fa-chevron-right ml-1"></i>
                      </button>
                    </div>
                  </div>
                </div>
              )}
            </CardTable>
          </div>
        </div>
      </div>
    </>
  );
}
