import React, { useState } from "react";

export default function ExpressAdd() {
  const API_URL = "http://localhost:5000/api/express/register";

  const [formData, setFormData] = useState({
    name: "",
    destination: "",
    departureTime: "",
  });

  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Handle form submit
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    try {
      const response = await fetch(API_URL, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        credentials: "include", // important if backend needs session/cookies
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        if (response.status === 401) {
          throw new Error("Unauthorized: Please log in first.");
        } else {
          throw new Error(`Error ${response.status}: Failed to save express.`);
        }
      }

      const data = await response.json();
      setSuccess("Express registered successfully!");
      setFormData({
        name: "",
        destination: "",
        departureTime: "",
      });

      console.log("Express registered:", data);
    } catch (err) {
      console.error("Error saving express:", err.message);
      setError(err.message);
    }
  };

  return (
    <div className="p-6 bg-white rounded-xl shadow-md w-96 mx-auto">
      <h2 className="text-xl font-bold mb-4">Add Express</h2>

      {error && <p className="text-red-600">{error}</p>}
      {success && <p className="text-green-600">{success}</p>}

      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          name="name"
          placeholder="Express Name"
          value={formData.name}
          onChange={handleChange}
          className="w-full border px-3 py-2 rounded"
          required
        />

        <input
          type="text"
          name="destination"
          placeholder="Destination"
          value={formData.destination}
          onChange={handleChange}
          className="w-full border px-3 py-2 rounded"
          required
        />

        <input
          type="time"
          name="departureTime"
          value={formData.departureTime}
          onChange={handleChange}
          className="w-full border px-3 py-2 rounded"
          required
        />

        <button
          type="submit"
          className="w-full bg-blue-600 text-white px-3 py-2 rounded hover:bg-blue-700"
        >
          Save Express
        </button>
      </form>
    </div>
  );
}
