// CardBarChart.jsx
import React, { useEffect } from "react";
import Chart from "chart.js";

export default function CardBarChart({ chartData, title, startDate, endDate }) {
  useEffect(() => {
    if (!chartData) return;

    const ctx = document.getElementById("bar-chart").getContext("2d");
    if (window.myBar) window.myBar.destroy();

    window.myBar = new Chart(ctx, {
      type: "bar",
      data: chartData,
      options: {
        maintainAspectRatio: false,
        responsive: true,
        legend: {
          position: "bottom",
          labels: {
            fontColor: "#4B5563"
          }
        },
        scales: {
          xAxes: [{
            ticks: {
              autoSkip: false,
              fontColor: "#4B5563"
            },
            gridLines: {
              display: false
            }
          }],
          yAxes: [{
            ticks: {
              beginAtZero: true,
              precision: 0,
              fontColor: "#4B5563"
            },
            gridLines: {
              color: "rgba(0,0,0,0.1)"
            }
          }],
        },
      },
    });

    return () => window.myBar && window.myBar.destroy();
  }, [chartData]);

  return (
    <div className="relative flex flex-col min-w-0 break-words bg-white w-full mb-6 shadow-lg rounded">
      <div className="rounded-t px-4 py-3 bg-transparent">
        <h2 className="text-blueGray-700 text-xl font-semibold">{title}</h2>
      </div>
      <div className="p-4 flex-auto">
        <div className="relative h-350-px">
          <canvas id="bar-chart"></canvas>
        </div>
      </div>
    </div>
  );
}
