import React, { useState, useEffect } from "react";
import CardStats from "components/Cards/CardStats.js";
import CardLineChart from "components/Cards/CardLineChart.js";
import CardBarChart from "components/Cards/CardBarChart.js";
import CardTable from "components/Cards/CardTable.js";

export default function Reports() {
  const [reportPeriod, setReportPeriod] = useState("weekly");
  const [reportType, setReportType] = useState("overview");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [destination, setDestination] = useState("");
  const [destinationsList, setDestinationsList] = useState([]);

  // Sample chart data
  const revenueData = {
    labels: ["Jan", "Feb", "Mar", "Apr", "May", "Jun"],
    datasets: [
      {
        label: "Revenue (RWF)",
        data: [1200000, 1900000, 3000000, 5000000, 2000000, 3000000],
        borderColor: "rgb(59, 130, 246)",
        backgroundColor: "rgba(59, 130, 246, 0.1)",
      },
    ],
  };

  const ticketSalesData = {
    labels: ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"],
    datasets: [
      {
        label: "Tickets Sold",
        data: [65, 59, 80, 81, 56, 55, 40],
        backgroundColor: "rgba(34, 197, 94, 0.8)",
      },
    ],
  };

  // Fetch destinations list on mount
  useEffect(() => {
    fetch("/api/reports/destinations", {
      headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
    })
      .then((res) => res.json())
      .then((data) => setDestinationsList(data))
      .catch((err) => console.error("Failed to fetch destinations", err));
  }, []);

  const handleGenerateReport = async () => {
    if (!startDate || !endDate) {
      alert("Please select both start and end dates");
      return;
    }

    let url = "";
    if (reportType === "destination") {
      if (!destination) {
        alert("Please select a destination");
        return;
      }
      url = `/api/reports/destination?destination=${destination}&start=${new Date(
        startDate
      ).getTime()}&end=${new Date(endDate).getTime()}`;
    } else {
      url = `/api/reports/date-range?start=${new Date(
        startDate
      ).getTime()}&end=${new Date(endDate).getTime()}`;
    }

    try {
      const response = await fetch(url, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      if (!response.ok) throw new Error("Failed to fetch report data");
      const data = await response.json();
      console.log("Report Data:", data);
      alert("Report generated successfully!");
    } catch (err) {
      console.error(err);
      alert("Error generating report: " + err.message);
    }
  };

  const handleExportReport = () => {
    alert("Report exported successfully!");
  };

  return (
    <>
      {/* Stats */}
      <div className="flex flex-wrap mt-4">
        <div className="w-full xl:w-8/12 mb-12 xl:mb-0 px-4">
          <CardStats
            statSubtitle="Total Revenue"
            statTitle="RWF 16,200,000"
            statArrow="up"
            statPercent="12"
            statPercentColor="text-emerald-500"
            statDescripiron="Since last month"
            statIconName="fas fa-dollar-sign"
            statIconColor="bg-emerald-500"
          />
        </div>
        <div className="w-full xl:w-4/12 px-4">
          <CardStats
            statSubtitle="Total Tickets"
            statTitle="2,300"
            statArrow="up"
            statPercent="3.48"
            statPercentColor="text-emerald-500"
            statDescripiron="Since last week"
            statIconName="fas fa-ticket-alt"
            statIconColor="bg-red-500"
          />
        </div>
      </div>

      {/* Report Configuration */}
      <div className="flex flex-wrap mt-4">
        <div className="w-full px-4">
          <div className="bg-white rounded-lg shadow-md p-6 mb-6">
            <h3 className="text-lg font-semibold text-gray-800 mb-6">
              Report Configuration
            </h3>

            <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-6">
              {/* Report Type */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Report Type
                </label>
                <select
                  value={reportType}
                  onChange={(e) => setReportType(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="overview">Overview</option>
                  <option value="revenue">Revenue</option>
                  <option value="tickets">Tickets</option>
                  <option value="destination">Destination</option>
                </select>
              </div>

              {/* Destination dropdown (shown only for destination report) */}
              {reportType === "destination" && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Destination
                  </label>
                  <select
                    value={destination}
                    onChange={(e) => setDestination(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="">Select Destination</option>
                    {destinationsList.map((d, idx) => (
                      <option key={idx} value={d}>
                        {d}
                      </option>
                    ))}
                  </select>
                </div>
              )}

              {/* Start Date */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Start Date
                </label>
                <input
                  type="date"
                  value={startDate}
                  onChange={(e) => setStartDate(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>

              {/* End Date */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  End Date
                </label>
                <input
                  type="date"
                  value={endDate}
                  onChange={(e) => setEndDate(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
            </div>

            {/* Buttons */}
            <div className="flex space-x-4">
              <button
                onClick={handleGenerateReport}
                className="px-6 py-2 bg-red-500 text-white rounded-md hover:bg-red-600 transition-colors font-medium"
              >
                <i className="fas fa-chart-line mr-2"></i>
                Generate Report
              </button>
              <button
                onClick={handleExportReport}
                className="px-6 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition-colors font-medium"
              >
                <i className="fas fa-download mr-2"></i>
                Export Report
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Charts */}
      <div className="flex flex-wrap mt-4">
        <div className="w-full xl:w-8/12 mb-12 xl:mb-0 px-4">
          <CardLineChart chartData={revenueData} />
        </div>
        <div className="w-full xl:w-4/12 px-4">
          <CardBarChart chartData={ticketSalesData} />
        </div>
      </div>

      {/* Detailed Report Table */}
      <div className="flex flex-wrap mt-4">
        <div className="w-full px-4">
          <CardTable>
            <div className="rounded-t mb-0 px-4 py-3 border-0">
              <div className="flex flex-wrap items-center justify-between">
                <div className="relative w-full px-4 max-w-full flex-grow flex-1">
                  <h3 className="font-semibold text-lg text-blueGray-700">
                    Detailed Report Data
                  </h3>
                </div>
                <div className="flex space-x-2">
                  <button className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition-colors">
                    <i className="fas fa-filter mr-2"></i> Filter
                  </button>
                  <button className="px-4 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition-colors">
                    <i className="fas fa-download mr-2"></i> Export
                  </button>
                </div>
              </div>
            </div>
            <div className="block w-full overflow-x-auto">
              <table className="items-center w-full bg-transparent border-collapse">
                <thead>
                  <tr>
                    <th>Date</th>
                    <th>Route</th>
                    <th>Tickets Sold</th>
                    <th>Revenue</th>
                    <th>Status</th>
                  </tr>
                </thead>
                <tbody>
                  {/* Example rows */}
                  <tr>
                    <td>2024-01-15</td>
                    <td>Kigali - Huye</td>
                    <td>45</td>
                    <td>RWF 112,500</td>
                    <td>
                      <i className="fas fa-circle text-emerald-500 mr-2"></i>
                      Completed
                    </td>
                  </tr>
                </tbody>
              </table>
            </div>
          </CardTable>
        </div>
      </div>
    </>
  );
}
