import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import jsPDF from "jspdf";
import "jspdf-autotable"; // âœ… Fix: just import, no assignment

export default function TicketAdd() {
  const history = useHistory();

  const [ticketData, setTicketData] = useState({
    names: "",
    email: "",
    phone: "",
    express: "",
    schedule: "",
    busPlateNo: "",
    seatNo: "",
    totalAmount: 0,
    payState: "PENDING",
    date: "",
  });

  const [expressList, setExpressList] = useState([]);
  const [scheduleList, setScheduleList] = useState([]);
  const [busList, setBusList] = useState([]);
  const [role, setRole] = useState("");
  const [existingTickets, setExistingTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [availableSeats, setAvailableSeats] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem("token");
        const expressId = localStorage.getItem("expressId");

        // Fetch user role
        let userRole = "";
        const userObj = localStorage.getItem("user");
        if (userObj) {
          const user = JSON.parse(userObj);
          userRole = user.role ? user.role.toUpperCase() : "";
        } else {
          userRole = localStorage.getItem("role")
            ? localStorage.getItem("role").toUpperCase()
            : "";
        }
        setRole(userRole);

        // Fetch express list with logos
        const expressResponse = await fetch(
          "http://localhost:5000/api/express/all",
          {
            method: "GET",
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        if (!expressResponse.ok) throw new Error("Cannot fetch express list");
        const expresses = await expressResponse.json();
        setExpressList(expresses);

        // Fetch schedules and filter
        const scheduleResponse = await fetch(
          "http://localhost:5000/api/tickets/schedule",
          {
            method: "GET",
            headers: {
              Authorization: `Bearer ${token}`,
              "X-Express-Id": expressId || "",
            },
          }
        );
        if (!scheduleResponse.ok) throw new Error("Cannot fetch schedule list");
        const schedules = await scheduleResponse.json();

        const today = new Date();
        const filteredSchedules = schedules.filter((sch) => {
          const scheduleDate = new Date(sch.date);
          const isNotExpired = scheduleDate >= today;
          const matchesExpress =
            userRole === "SUPER_ADMIN" ? true : sch.express.id === expressId;
          return isNotExpired && matchesExpress && sch.express;
        });

        const finalSchedules =
          userRole !== "SUPER_ADMIN" && expressId
            ? filteredSchedules.filter((sch) => sch.express.id === expressId)
            : filteredSchedules;
        setScheduleList(finalSchedules);

        // Fetch buses
        const busResponse = await fetch("http://localhost:5000/api/tickets/bus", {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
            "X-Express-Id": expressId || "",
          },
        });
        if (!busResponse.ok) throw new Error("Cannot fetch bus list");
        setBusList(await busResponse.json());

        // Fetch tickets
        const ticketsResponse = await fetch(
          "http://localhost:5000/api/tickets/all",
          {
            method: "GET",
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        if (!ticketsResponse.ok) throw new Error("Cannot fetch existing tickets");
        setExistingTickets(await ticketsResponse.json());

        if (userRole !== "SUPER_ADMIN" && expressId) {
          setTicketData((prev) => ({ ...prev, express: expressId }));
        }

        setLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
        setLoading(false);
        alert(error.message);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (!ticketData.schedule) return;

    const selectedSchedule = scheduleList.find(
      (s) => s.id === ticketData.schedule
    );
    if (!selectedSchedule || !selectedSchedule.bus) return;

    const bookedSeats = existingTickets
      .filter((t) => t.schedule.id === ticketData.schedule)
      .map((t) => t.seatNo);

    const remainingSeats = selectedSchedule.bus.busSize - bookedSeats.length;
    setAvailableSeats(remainingSeats);

    const nextSeat = [];
    for (let i = 1; i <= selectedSchedule.bus.busSize; i++) {
      if (!bookedSeats.includes(i)) {
        nextSeat.push(i);
      }
    }
    setTicketData((prev) => ({
      ...prev,
      seatNo: nextSeat.length > 0 ? nextSeat[0] : "",
      busPlateNo: selectedSchedule.bus.plateNo,
      totalAmount: selectedSchedule.destination.price || 0,
    }));
  }, [ticketData.schedule, scheduleList, existingTickets]);

  useEffect(() => {
    if (role !== "SUPER_ADMIN" || !ticketData.express) return;

    const fetchSchedulesForExpress = async () => {
      try {
        const token = localStorage.getItem("token");
        const scheduleResponse = await fetch(
          "http://localhost:5000/api/tickets/schedule",
          {
            method: "GET",
            headers: {
              Authorization: `Bearer ${token}`,
              "X-Express-Id": ticketData.express,
            },
          }
        );
        if (!scheduleResponse.ok)
          throw new Error("Cannot fetch schedules for express");
        const schedules = await scheduleResponse.json();

        const today = new Date();
        const filteredSchedules = schedules.filter((sch) => {
          const scheduleDate = new Date(sch.date);
          return (
            scheduleDate >= today &&
            sch.express.id === ticketData.express &&
            sch.express
          );
        });
        setScheduleList(filteredSchedules);
      } catch (error) {
        console.error("Error fetching schedules:", error);
        alert(error.message);
      }
    };

    fetchSchedulesForExpress();
  }, [ticketData.express, role]);

  // ðŸ”¹ Helper: Convert image URL to Base64
  const getBase64ImageFromUrl = async (imageUrl) => {
    const response = await fetch(imageUrl);
    const blob = await response.blob();
    return new Promise((resolve) => {
      const reader = new FileReader();
      reader.onloadend = () => resolve(reader.result);
      reader.readAsDataURL(blob);
    });
  };

  const generatePDF = async (ticket) => {
    const selectedSchedule = scheduleList.find(
      (s) => s.id === ticket.schedule.id
    );
    const selectedExpress = expressList.find((e) => e.id === ticket.express.id);
    const logoUrl = `http://localhost:5000/uploads/${selectedExpress.expressLogo}`;

    const doc = new jsPDF();
    doc.setFontSize(20);
    doc.text("TEGABUS TICKET", 105, 20, { align: "center" });

    // âœ… Add logo safely
    try {
      const logoBase64 = await getBase64ImageFromUrl(logoUrl);
      doc.addImage(logoBase64, "PNG", 80, 30, 50, 20);
    } catch (err) {
      console.warn("Logo could not be loaded:", err);
    }

    doc.setFontSize(16);
    doc.text(selectedExpress.expressName, 105, 60, { align: "center" });
    doc.line(20, 65, 190, 65);

    doc.setFontSize(12);
    doc.text("Passenger Details", 20, 80);
    doc.autoTable({
      startY: 90,
      head: [["Field", "Value"]],
      body: [
        ["Name", ticket.names],
        ["Phone", ticket.phone],
        ["Email", ticket.email || "-"],
      ],
      theme: "grid",
      styles: { fontSize: 10, cellPadding: 2 },
      columnStyles: { 0: { cellWidth: 40 }, 1: { cellWidth: 140 } },
    });

    doc.text("Ticket Details", 20, 130);
    doc.autoTable({
      startY: 140,
      head: [["Field", "Value"]],
      body: [
        ["Ticket Number", ticket.ticketNumber],
        [
          "Schedule",
          `${selectedSchedule.destination.fromLocation} to ${selectedSchedule.destination.toLocation}`,
        ],
        ["Date", new Date(ticket.date).toLocaleDateString()],
        ["Time", selectedSchedule.time],
        ["Bus Plate Number", ticket.busPlateNo],
        ["Seat Number", ticket.seatNo],
        ["Payment Status", ticket.payState],
        ["Total Amount", `$${ticket.totalAmount}`],
      ],
      theme: "grid",
      styles: { fontSize: 10, cellPadding: 2 },
      columnStyles: { 0: { cellWidth: 40 }, 1: { cellWidth: 140 } },
    });

    doc.text(
      "Thank you for choosing " + selectedExpress.expressName + "!",
      105,
      220,
      { align: "center" }
    );
    doc.setFontSize(8);
    doc.text(
      "Please present this ticket during boarding.",
      105,
      230,
      { align: "center" }
    );

    // Save PDF
    doc.save(`ticket_${ticket.ticketNumber}.pdf`);

    // Print
    const pdfOutput = doc.output("bloburl");
    const printWindow = window.open(pdfOutput);
    printWindow.onload = () => {
      printWindow.print();
      printWindow.close();
    };
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTicketData({ ...ticketData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!ticketData.seatNo) {
      return alert("No seats available for the selected schedule.");
    }

    try {
      const token = localStorage.getItem("token");

      const payload = {
        ...ticketData,
        express: { id: ticketData.express },
        schedule: { id: ticketData.schedule },
      };

      const response = await fetch(
        "http://localhost:5000/api/tickets/register",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify(payload),
        }
      );

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(errorText || "Failed to save ticket");
      }

      const savedTicket = await response.json();
      alert("Ticket added successfully!");

      // âœ… Generate and print PDF
      await generatePDF(savedTicket);

      history.push("/admin/tickets");
    } catch (error) {
      console.error(error);
      alert("Error saving ticket: " + error.message);
    }
  };

  const handleCancel = () => history.push("/admin/tickets");

  if (loading) return <p className="text-center p-6">Loading...</p>;

  return (
    <div className="flex flex-wrap">
      <div className="w-full xl:w-8/12 mb-12 xl:mb-0 px-4 mx-auto">
        <div className="relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded-lg bg-white border-0">
          <div className="rounded-t bg-white mb-0 px-6 py-6 flex justify-between items-center">
            <h6 className="text-blueGray-700 text-xl font-bold">Add New Ticket</h6>
            <button
              onClick={handleCancel}
              className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
            >
              Back
            </button>
          </div>

          <form onSubmit={handleSubmit} className="flex-auto px-4 lg:px-10 py-10 pt-0">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Passenger Name *</label>
                <input
                  type="text"
                  name="names"
                  value={ticketData.names}
                  onChange={handleInputChange}
                  required
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Email</label>
                <input
                  type="email"
                  name="email"
                  value={ticketData.email}
                  onChange={handleInputChange}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Phone *</label>
                <input
                  type="text"
                  name="phone"
                  value={ticketData.phone}
                  onChange={handleInputChange}
                  required
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg"
                />
              </div>

              {role === "SUPER_ADMIN" && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Express *</label>
                  <select
                    name="express"
                    value={ticketData.express}
                    onChange={handleInputChange}
                    required
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg"
                  >
                    <option value="">Select Express</option>
                    {expressList.map((exp) => (
                      <option key={exp.id} value={exp.id}>{exp.expressName}</option>
                    ))}
                  </select>
                </div>
              )}

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Schedule *</label>
                <select
                  name="schedule"
                  value={ticketData.schedule}
                  onChange={handleInputChange}
                  required
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg"
                >
                  <option value="">Select Schedule</option>
                  {scheduleList.map((sch) => (
                    <option key={sch.id} value={sch.id}>
                      {sch.destination.fromLocation} - {sch.destination.toLocation} ({sch.date} {sch.time})
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Bus Plate Number *</label>
                <input
                  type="text"
                  name="busPlateNo"
                  value={ticketData.busPlateNo}
                  readOnly
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg bg-gray-100"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Seat Number * (Available: {availableSeats})
                </label>
                <input
                  type="number"
                  name="seatNo"
                  value={ticketData.seatNo || ""}
                  readOnly
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg bg-gray-100"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Total Amount *</label>
                <input
                  type="number"
                  name="totalAmount"
                  value={ticketData.totalAmount}
                  readOnly
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg bg-gray-100"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Payment Status *</label>
                <select
                  name="payState"
                  value={ticketData.payState}
                  onChange={handleInputChange}
                  required
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg"
                >
                  <option value="PENDING">Pending</option>
                  <option value="PAID">Paid</option>
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Date *</label>
                <input
                  type="date"
                  name="date"
                  value={ticketData.date}
                  onChange={handleInputChange}
                  required
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg"
                />
              </div>
            </div>

            <div className="flex justify-end space-x-4 pt-6 border-t border-gray-200">
              <button
                type="button"
                onClick={handleCancel}
                className="px-6 py-3 border border-gray-300 rounded-lg hover:bg-gray-50"
              >
                Cancel
              </button>
              <button
                type="submit"
                className="px-6 py-3 bg-black text-white rounded-lg hover:bg-gray-700"
                disabled={availableSeats === 0}
              >
                <i className="fas fa-save mr-2"></i> Save Ticket
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
