import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import jsPDF from "jspdf";

export default function TicketAdd() {
  const history = useHistory();

  const [ticketData, setTicketData] = useState({
    names: "",
    email: "",
    phone: "",
    express: "",
    schedule: "",
    payState: "PENDING",
    date: "",
    seatNo: "",
  });

  const [expressList, setExpressList] = useState([]);
  const [scheduleList, setScheduleList] = useState([]);
  const [role, setRole] = useState("");
  const [existingTickets, setExistingTickets] = useState([]);
  const [availableSeats, setAvailableSeats] = useState(0);
  const [loading, setLoading] = useState(true);

  const token = localStorage.getItem("token");
  const userObj = JSON.parse(localStorage.getItem("user") || "{}");
  const userRole = userObj.role ? userObj.role.toUpperCase() : "";

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Role
        setRole(userRole);

        // Fetch Express List
        const expressResponse = await fetch("http://localhost:5000/api/express/all", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setExpressList(await expressResponse.json());

        // Fetch Schedules
        const expressId = userRole !== "SUPER_ADMIN" ? userObj.expressId : "";
        const scheduleResponse = await fetch(
          `http://localhost:5000/api/schedules/all${expressId ? `?expressId=${expressId}` : ""}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setScheduleList(await scheduleResponse.json());

        // Fetch Existing Tickets
        const ticketsResponse = await fetch("http://localhost:5000/api/tickets/all", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setExistingTickets(await ticketsResponse.json());

        // Auto-set express for non-admin
        if (userRole !== "SUPER_ADMIN") {
          setTicketData((prev) => ({ ...prev, express: userObj.expressId }));
        }

        setLoading(false);
      } catch (err) {
        console.error(err);
        alert("Error fetching data: " + err.message);
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  // Handle schedule selection & seat availability
  useEffect(() => {
    if (!ticketData.schedule) return;
    const selectedSchedule = scheduleList.find((s) => s.id === ticketData.schedule);
    if (!selectedSchedule) return;

    const bookedSeats = existingTickets
      .filter((t) => t.schedule.some((sch) => sch.id === ticketData.schedule))
      .map((t) => t.seatNo);

    const remainingSeats = selectedSchedule.bus.capacity - bookedSeats.length;
    setAvailableSeats(remainingSeats);

    const nextSeat = [];
    for (let i = 1; i <= selectedSchedule.bus.capacity; i++) {
      if (!bookedSeats.includes(i)) nextSeat.push(i);
    }

    setTicketData((prev) => ({
      ...prev,
      seatNo: nextSeat.length > 0 ? nextSeat[0] : "",
    }));
  }, [ticketData.schedule, scheduleList, existingTickets]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTicketData({ ...ticketData, [name]: value });
  };

  const generatePDF = (ticket) => {
    const doc = new jsPDF();
    doc.setFontSize(16);
    doc.text("Ticket Confirmation", 20, 20);
    doc.setFontSize(12);

    doc.text(`Ticket No: ${ticket.tickeNumber}`, 20, 40);
    doc.text(`Name: ${ticket.names}`, 20, 50);
    doc.text(`Email: ${ticket.email}`, 20, 60);
    doc.text(`Phone: ${ticket.phone}`, 20, 70);
    doc.text(`Express: ${ticket.express.expressName}`, 20, 80);
    doc.text(`Schedule: ${ticket.schedule[0].fromLocation} - ${ticket.schedule[0].toLocation}`, 20, 90);
    doc.text(`Date: ${new Date(ticket.date).toLocaleDateString()}`, 20, 100);
    doc.text(`Bus Plate No: ${ticket.busPlateNo}`, 20, 110);
    doc.text(`Seat No: ${ticket.seatNo}`, 20, 120);

    if (ticket.express.logo) {
      const img = new Image();
      img.src = ticket.express.logo; // logo URL
      doc.addImage(img, "PNG", 150, 20, 40, 40);
    }

    doc.save(`Ticket-${ticket.tickeNumber}.pdf`);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!ticketData.seatNo) return alert("No seats available for this schedule");

    try {
      const payload = {
        ...ticketData,
        express: role === "SUPER_ADMIN"
          ? expressList.find((exp) => exp.id === ticketData.express)
          : { id: ticketData.express },
        schedule: [{ id: ticketData.schedule }],
      };

      const response = await fetch("http://localhost:5000/api/tickets/register", {
        method: "POST",
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify(payload),
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(errorText || "Failed to save ticket");
      }

      const savedTicket = await response.json();
      generatePDF(savedTicket);

      alert("Ticket added successfully!");
      history.push("/admin/tickets");
    } catch (err) {
      console.error(err);
      alert("Error saving ticket: " + err.message);
    }
  };

  if (loading) return <p className="text-center p-6">Loading...</p>;

  return (
    <div className="flex justify-center p-4">
      <form onSubmit={handleSubmit} className="w-full max-w-3xl bg-white shadow-lg rounded-lg p-6">
        <h2 className="text-xl font-bold mb-4">Add New Ticket</h2>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <input placeholder="Name" name="names" value={ticketData.names} onChange={handleInputChange} required />
          <input placeholder="Email" name="email" value={ticketData.email} onChange={handleInputChange} />
          <input placeholder="Phone" name="phone" value={ticketData.phone} onChange={handleInputChange} required />

          {role === "SUPER_ADMIN" && (
            <select name="express" value={ticketData.express} onChange={handleInputChange} required>
              <option value="">Select Express</option>
              {expressList.map((exp) => (
                <option key={exp.id} value={exp.id}>{exp.expressName}</option>
              ))}
            </select>
          )}

          <select name="schedule" value={ticketData.schedule} onChange={handleInputChange} required>
            <option value="">Select Schedule</option>
            {scheduleList.map((sch) => (
              <option key={sch.id} value={sch.id}>
                {sch.fromLocation} - {sch.toLocation} ({sch.date} {sch.time})
              </option>
            ))}
          </select>

          <input
            type="number"
            name="seatNo"
            value={ticketData.seatNo || ""}
            readOnly
            placeholder={`Seat No (Available: ${availableSeats})`}
          />

          <select name="payState" value={ticketData.payState} onChange={handleInputChange}>
            <option value="PENDING">Pending</option>
            <option value="PAID">Paid</option>
          </select>

          <input type="date" name="date" value={ticketData.date} onChange={handleInputChange} required />
        </div>

        <div className="flex justify-end mt-4 space-x-2">
          <button type="button" onClick={() => history.push("/admin/tickets")} className="px-4 py-2 bg-gray-300 rounded">Cancel</button>
          <button type="submit" disabled={availableSeats === 0} className="px-4 py-2 bg-blue-600 text-white rounded">Save & Print</button>
        </div>
      </form>
    </div>
  );
}
