import React, { useState, useEffect, useRef } from "react";
import CardStats from "components/Cards/CardStats.js";
import CardTable from "components/Cards/CardTable.js";
import jsPDF from "jspdf";
import "jspdf-autotable";
import Chart from "chart.js";

export default function Reports() {
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [totalRevenue, setTotalRevenue] = useState(0);
  const [totalTickets, setTotalTickets] = useState(0);
  const [totalPaidTickets, setTotalPaidTickets] = useState(0);
  const [totalUnpaidTickets, setTotalUnpaidTickets] = useState(0);
  const [revenueData, setRevenueData] = useState({ labels: [], datasets: [] });
  const [ticketSalesData, setTicketSalesData] = useState({ labels: [], datasets: [] });
  const [tableData, setTableData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const revenueChartRef = useRef(null);
  const ticketSalesChartRef = useRef(null);
  const revenueChartInstance = useRef(null);
  const ticketSalesChartInstance = useRef(null);

  useEffect(() => {
    loadDefaultWeeklyData();
  }, []);

  const loadDefaultWeeklyData = async () => {
    try {
      setIsLoading(true);
      const end = new Date();
      const start = new Date();
      start.setDate(start.getDate() - 7);

      const startStr = start.toISOString().split('T')[0];
      const endStr = end.toISOString().split('T')[0];

      setStartDate(startStr);
      setEndDate(endStr);

      await fetchReportData(start.getTime(), end.getTime());
    } catch (error) {
      console.error("Error loading default weekly data:", error);
      alert("Error loading default weekly data: " + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const fetchReportData = async (startMillis, endMillis) => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        `http://localhost:5000/api/reports/date-range?start=${startMillis}&end=${endMillis}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`,
          },
        }
      );

      if (!response.ok) {
        throw new Error("Failed to fetch report data");
      }

      const data = await response.json();
      processReportData(data);
    } catch (error) {
      console.error(error);
      alert("Error fetching report data: " + error.message);
    }
  };

  const processReportData = (data) => {
    const groupedByDateAndDestination = {};
    data.forEach((t) => {
      const date = new Date(t.date).toLocaleDateString();
      const destination = t.schedule?.destination || 'Unknown';
      const key = `${date}-${destination}`;

      if (!groupedByDateAndDestination[key]) {
        groupedByDateAndDestination[key] = {
          date,
          destination,
          tickets: 0,
          revenue: 0,
          paidTickets: 0,
          unpaidTickets: 0,
        };
      }

      groupedByDateAndDestination[key].tickets += 1;
      groupedByDateAndDestination[key].revenue += t.totalAmount || 0;

      if (t.payState === "PAID") {
        groupedByDateAndDestination[key].paidTickets += 1;
      } else {
        groupedByDateAndDestination[key].unpaidTickets += 1;
      }
    });

    const tableGroupedData = Object.values(groupedByDateAndDestination);
    setTableData(tableGroupedData);

    const groupedByDateOnly = {};
    data.forEach((t) => {
      const date = new Date(t.date).toLocaleDateString();

      if (!groupedByDateOnly[date]) {
        groupedByDateOnly[date] = {
          date,
          tickets: 0,
          revenue: 0,
          paidTickets: 0,
          unpaidTickets: 0,
        };
      }

      groupedByDateOnly[date].tickets += 1;
      groupedByDateOnly[date].revenue += t.totalAmount || 0;

      if (t.payState === "PAID") {
        groupedByDateOnly[date].paidTickets += 1;
      } else {
        groupedByDateOnly[date].unpaidTickets += 1;
      }
    });

    const chartGroupedData = Object.values(groupedByDateOnly);

    const totalRev = chartGroupedData.reduce((sum, r) => sum + r.revenue, 0);
    const totalTix = chartGroupedData.reduce((sum, r) => sum + r.tickets, 0);
    const totalPaid = chartGroupedData.reduce((sum, r) => sum + r.paidTickets, 0);
    const totalUnpaid = chartGroupedData.reduce((sum, r) => sum + r.unpaidTickets, 0);

    setTotalRevenue(totalRev);
    setTotalTickets(totalTix);
    setTotalPaidTickets(totalPaid);
    setTotalUnpaidTickets(totalUnpaid);

    const sortedChartData = chartGroupedData.sort(
      (a, b) => new Date(a.date) - new Date(b.date)
    );
    const labels = sortedChartData.map((r) => r.date);

    const revenueChart = {
      labels,
      datasets: [
        {
          label: "Revenue (RWF)",
          data: sortedChartData.map((r) => r.revenue),
          borderColor: "rgb(59,130,246)",
          backgroundColor: "rgba(59,130,246,0.1)",
          tension: 0.4,
          fill: true,
        },
      ],
    };
    setRevenueData(revenueChart);

    const ticketChart = {
      labels,
      datasets: [
        {
          label: "Paid Tickets",
          data: sortedChartData.map((r) => r.paidTickets),
          backgroundColor: "rgba(34,197,94,0.8)",
          barThickness: 8,
        },
        {
          label: "Unpaid Tickets",
          data: sortedChartData.map((r) => r.unpaidTickets),
          backgroundColor: "rgba(239,68,68,0.8)",
          barThickness: 8,
        },
      ],
    };
    setTicketSalesData(ticketChart);
  };

  useEffect(() => {
    if (!revenueData.labels.length) return;

    if (revenueChartInstance.current) {
      revenueChartInstance.current.destroy();
    }

    const ctxRevenue = revenueChartRef.current.getContext("2d");
    revenueChartInstance.current = new Chart(ctxRevenue, {
      type: "line",
      data: revenueData,
      options: {
        maintainAspectRatio: false,
        responsive: true,
        plugins: {
          legend: {
            position: "bottom",
            labels: {
              color: "#4B5563"
            }
          }
        },
        scales: {
          x: {
            ticks: {
              color: "#4B5563"
            },
            grid: {
              display: false
            }
          },
          y: {
            ticks: {
              beginAtZero: true,
              color: "#4B5563"
            },
            grid: {
              color: "rgba(0,0,0,0.1)"
            }
          }
        }
      }
    });

    return () => {
      if (revenueChartInstance.current) {
        revenueChartInstance.current.destroy();
      }
    };
  }, [revenueData]);

  useEffect(() => {
    if (!ticketSalesData.labels.length) return;

    if (ticketSalesChartInstance.current) {
      ticketSalesChartInstance.current.destroy();
    }

    const ctxTicketSales = ticketSalesChartRef.current.getContext("2d");
    ticketSalesChartInstance.current = new Chart(ctxTicketSales, {
      type: "bar",
      data: ticketSalesData,
      options: {
        maintainAspectRatio: false,
        responsive: true,
        plugins: {
          legend: {
            position: "bottom",
            labels: {
              color: "#4B5563"
            }
          }
        },
        scales: {
          x: {
            ticks: {
              autoSkip: false,
              color: "#4B5563"
            },
            grid: {
              display: false
            }
          },
          y: {
            ticks: {
              beginAtZero: true,
              precision: 0,
              color: "#4B5563"
            },
            grid: {
              color: "rgba(0,0,0,0.1)"
            }
          }
        }
      }
    });

    return () => {
      if (ticketSalesChartInstance.current) {
        ticketSalesChartInstance.current.destroy();
      }
    };
  }, [ticketSalesData]);

  const handleGenerateReport = async () => {
    if (!startDate || !endDate) {
      alert("Please select both start and end dates");
      return;
    }

    try {
      setIsLoading(true);
      const startMillis = new Date(startDate).getTime();
      const endMillis = new Date(endDate).getTime();
      await fetchReportData(startMillis, endMillis);
      alert("Report generated successfully!");
    } catch (error) {
      console.error(error);
      alert("Error generating report: " + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleExportReport = () => {
    if (tableData.length === 0) {
      alert("No data to export. Please generate a report first.");
      return;
    }

    const doc = new jsPDF();

    doc.setFontSize(20);
    doc.text("Travel Report", 105, 15, { align: "center" });

    doc.setFontSize(12);
    doc.text(`Date Range: ${startDate} to ${endDate}`, 105, 25, { align: "center" });

    doc.setFontSize(14);
    doc.text("Summary", 14, 35);
    doc.setFontSize(10);
    doc.text(`Total Revenue: RWF ${totalRevenue.toLocaleString()}`, 14, 45);
    doc.text(`Total Tickets: ${totalTickets}`, 14, 55);
    doc.text(`Paid Tickets: ${totalPaidTickets}`, 14, 65);
    doc.text(`Unpaid Tickets: ${totalUnpaidTickets}`, 14, 75);

    doc.autoTable({
      startY: 85,
      head: [['Date', 'Destination', 'Tickets', 'Revenue', 'Paid', 'Unpaid']],
      body: tableData.map(row => [
        row.date,
        row.destination,
        row.tickets,
        `RWF ${row.revenue.toLocaleString()}`,
        row.paidTickets,
        row.unpaidTickets
      ]),
      theme: 'grid',
      headStyles: {
        fillColor: [59, 130, 246],
        textColor: 255
      }
    });

    doc.save(`travel-report-${startDate}-to-${endDate}.pdf`);
    alert("PDF exported successfully!");
  };

  return (
    <>
      <div className="flex flex-wrap mt-4">
        <div className="w-full xl:w-3/12 mb-12 xl:mb-0 px-4">
          <CardStats
            statSubtitle="Total Revenue"
            statTitle={`RWF ${totalRevenue.toLocaleString()}`}
            statArrow="up"
            statPercent="12"
            statPercentColor="text-emerald-500"
            statDescripiron="Since last month"
            statIconName="fas fa-dollar-sign"
            statIconColor="bg-emerald-500"
          />
        </div>
        <div className="w-full xl:w-3/12 px-4">
          <CardStats
            statSubtitle="Total Tickets"
            statTitle={totalTickets}
            statArrow="up"
            statPercent="3.48"
            statPercentColor="text-emerald-500"
            statDescripiron="Since last week"
            statIconName="fas fa-ticket-alt"
            statIconColor="bg-red-500"
          />
        </div>
        <div className="w-full xl:w-3/12 px-4">
          <CardStats
            statSubtitle="Paid Tickets"
            statTitle={totalPaidTickets}
            statArrow="up"
            statPercent="1.10"
            statPercentColor="text-emerald-500"
            statDescripiron="Since yesterday"
            statIconName="fas fa-check-circle"
            statIconColor="bg-blue-500"
          />
        </div>
        <div className="w-full xl:w-3/12 px-4">
          <CardStats
            statSubtitle="Unpaid Tickets"
            statTitle={totalUnpaidTickets}
            statArrow="down"
            statPercent="2.30"
            statPercentColor="text-red-500"
            statDescripiron="Since last week"
            statIconName="fas fa-exclamation-circle"
            statIconColor="bg-orange-500"
          />
        </div>
      </div>

      {/* Date Range Selection */}
      <div className="flex flex-wrap mt-4">
        <div className="w-full px-4">
          <div className="bg-white rounded-lg shadow-md p-6 mb-6">
            <h3 className="text-lg font-semibold text-gray-800 mb-6">
              Report Configuration
            </h3>

            <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Start Date
                </label>
                <input
                  type="date"
                  value={startDate}
                  onChange={(e) => setStartDate(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  End Date
                </label>
                <input
                  type="date"
                  value={endDate}
                  onChange={(e) => setEndDate(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>

              <div className="flex items-end">
                <button
                  onClick={loadDefaultWeeklyData}
                  className="px-4 py-2 bg-gray-500 text-white rounded-md hover:bg-gray-600 transition-colors font-medium"
                >
                  <i className="fas fa-calendar-week mr-2"></i>
                  This Week
                </button>
              </div>
            </div>

            <div className="flex space-x-4">
              <button
                onClick={handleGenerateReport}
                disabled={isLoading}
                className="px-6 py-2 bg-red-500 text-white rounded-md hover:bg-red-600 transition-colors font-medium disabled:opacity-50"
              >
                {isLoading ? (
                  <>
                    <i className="fas fa-spinner fa-spin mr-2"></i>
                    Generating...
                  </>
                ) : (
                  <>
                    <i className="fas fa-chart-line mr-2"></i>
                    Generate Report
                  </>
                )}
              </button>
              <button
                onClick={handleExportReport}
                disabled={tableData.length === 0}
                className="px-6 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition-colors font-medium disabled:opacity-50"
              >
                <i className="fas fa-download mr-2"></i>
                Export Report
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Charts */}
      <div className="flex flex-wrap mt-4">
        <div className="w-full xl:w-8/12 mb-12 xl:mb-0 px-4">
          {revenueData.labels.length > 0 ? (
            <div className="relative flex flex-col min-w-0 break-words bg-white w-full mb-6 shadow-lg rounded">
              <div className="rounded-t px-4 py-3 bg-transparent">
                <h2 className="text-blueGray-700 text-xl font-semibold">Revenue</h2>
              </div>
              <div className="p-4 flex-auto">
                <div className="relative h-350-px">
                  <canvas ref={revenueChartRef} id="revenue-chart"></canvas>
                </div>
              </div>
            </div>
          ) : (
            <div className="bg-white rounded-lg shadow-md p-6 text-center">
              <p className="text-gray-500">Generate a report to view revenue chart</p>
            </div>
          )}
        </div>
        <div className="w-full xl:w-4/12 px-4">
          {ticketSalesData.labels.length > 0 ? (
            <div className="relative flex flex-col min-w-0 break-words bg-white w-full mb-6 shadow-lg rounded">
              <div className="rounded-t px-4 py-3 bg-transparent">
                <h2 className="text-blueGray-700 text-xl font-semibold">Ticket Sales</h2>
              </div>
              <div className="p-4 flex-auto">
                <div className="relative h-350-px">
                  <canvas ref={ticketSalesChartRef} id="ticket-sales-chart"></canvas>
                </div>
              </div>
            </div>
          ) : (
            <div className="bg-white rounded-lg shadow-md p-6 text-center">
              <p className="text-gray-500">Generate a report to view ticket sales chart</p>
            </div>
          )}
        </div>
      </div>

      {/* Table */}
      <div className="flex flex-wrap mt-4">
        <div className="w-full px-4">
          <CardTable>
            <div className="rounded-t mb-0 px-4 py-3 border-0">
              <div className="flex flex-wrap items-center justify-between">
                <div className="relative w-full px-4 max-w-full flex-grow flex-1">
                  <h3 className="font-semibold text-lg text-blueGray-700">
                    Daily Summary by Destination
                  </h3>
                </div>
              </div>
            </div>
            <div className="block w-full overflow-x-auto">
              {tableData.length > 0 ? (
                <table className="items-center w-full bg-transparent border-collapse">
                  <thead>
                    <tr>
                      <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50">
                        Date
                      </th>
                      <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50">
                        Destination
                      </th>
                      <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50">
                        Tickets Sold
                      </th>
                      <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50">
                        Revenue
                      </th>
                      <th className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50">
                        Status
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    {tableData.map((row, index) => (
                      <tr key={index}>
                        <td className="px-6 py-4">{row.date}</td>
                        <td className="px-6 py-4">{row.destination}</td>
                        <td className="px-6 py-4">{row.tickets}</td>
                        <td className="px-6 py-4">{`RWF ${row.revenue.toLocaleString()}`}</td>
                        <td className="px-6 py-4">
                          <span className="mr-4">
                            <i className="fas fa-circle text-emerald-500 mr-1"></i>
                            Paid: {row.paidTickets}
                          </span>
                          <span>
                            <i className="fas fa-circle text-orange-500 mr-1"></i>
                            Unpaid: {row.unpaidTickets}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              ) : (
                <div className="text-center py-8">
                  <p className="text-gray-500">No data available. Generate a report to view details.</p>
                </div>
              )}
            </div>
          </CardTable>
        </div>
      </div>
    </>
  );
}
