import React, { useState, useEffect } from "react";
import { useParams, useHistory } from "react-router-dom";

export default function UserEdit() {
  const { id } = useParams(); // <-- this gets user id from route
  const history = useHistory();

  const [user, setUser] = useState({
    firstname: "",
    lastname: "",
    email: "",
    phone: "",
    express_id: "",
  });

  const [expressList, setExpressList] = useState([]);

  // Fetch user details
  const fetchUser = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/users/${id}`);
      if (!response.ok) throw new Error("Failed to fetch user");
      const data = await response.json();
      setUser(data);
    } catch (error) {
      console.error("Error fetching user:", error);
    }
  };

  // Fetch express list
  const fetchExpressList = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/express/all");
      if (!response.ok) throw new Error("Failed to fetch express list");
      const data = await response.json();
      setExpressList(data);
    } catch (error) {
      console.error("Error fetching express list:", error);
    }
  };

  useEffect(() => {
    if (id) {
      fetchUser();
      fetchExpressList();
    }
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser((prevUser) => ({
      ...prevUser,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:5000/api/users/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(user),
      });
      if (!response.ok) throw new Error("Failed to update user");
      history.push("/users");
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  return (
    <div className="container mx-auto px-4 py-6">
      <h2 className="text-xl font-bold mb-4">Edit User</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          name="firstname"
          value={user.firstname}
          onChange={handleChange}
          placeholder="First Name"
          className="border p-2 w-full"
        />
        <input
          type="text"
          name="lastname"
          value={user.lastname}
          onChange={handleChange}
          placeholder="Last Name"
          className="border p-2 w-full"
        />
        <input
          type="email"
          name="email"
          value={user.email}
          onChange={handleChange}
          placeholder="Email"
          className="border p-2 w-full"
        />
        <input
          type="text"
          name="phone"
          value={user.phone}
          onChange={handleChange}
          placeholder="Phone"
          className="border p-2 w-full"
        />

        {/* Dropdown for express */}
        <select
          name="express_id"
          value={user.express_id}
          onChange={handleChange}
          className="border p-2 w-full"
        >
          <option value="">Select Express</option>
          {expressList.map((exp) => (
            <option key={exp.id} value={exp.id}>
              {exp.name}
            </option>
          ))}
        </select>

        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded"
        >
          Update User
        </button>
      </form>
    </div>
  );
}
