import React from "react";
import Chart from "chart.js";

export default function CardBarChart({ chartData, filters }) {
  const [isLoading, setIsLoading] = React.useState(false);
  const [error, setError] = React.useState(null);

  React.useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);
      setError(null);

      try {
        let url = '/api/reports/date-range';
        const params = new URLSearchParams();

        if (filters?.startDate) {
          params.append('start', filters.startDate.getTime());
        }

        if (filters?.endDate) {
          params.append('end', filters.endDate.getTime());
        }

        if (filters?.destination) {
          params.append('destination', filters.destination);
        }

        if (params.toString()) {
          url += `?${params.toString()}`;
        }

        const response = await fetch(url, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`,
            'Content-Type': 'application/json'
          }
        });

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const tickets = await response.json();

        // Process data for bar chart (paid vs unpaid tickets)
        const ticketData = processTicketData(tickets);

        const dataToUse = {
          labels: ticketData.labels,
          datasets: [
            {
              label: "Paid Tickets",
              backgroundColor: "rgba(34,197,94,0.8)",
              borderColor: "rgba(34,197,94,1)",
              data: ticketData.paid,
              barThickness: 8,
            },
            {
              label: "Unpaid Tickets",
              backgroundColor: "rgba(239,68,68,0.8)",
              borderColor: "rgba(239,68,68,1)",
              data: ticketData.unpaid,
              barThickness: 8,
            },
          ],
        };

        renderChart(dataToUse);
      } catch (err) {
        console.error("Error fetching chart data:", err);
        setError(err.message);
        renderChart(getDefaultData());
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, [filters]);

  const processTicketData = (tickets) => {
    // Group tickets by day and count paid vs unpaid
    const paidByDay = {};
    const unpaidByDay = {};
    const labelsSet = new Set();

    tickets.forEach(ticket => {
      const date = new Date(ticket.createdAt).toLocaleDateString();
      labelsSet.add(date);

      if (ticket.paid) {
        paidByDay[date] = (paidByDay[date] || 0) + 1;
      } else {
        unpaidByDay[date] = (unpaidByDay[date] || 0) + 1;
      }
    });

    // Convert to arrays for chart
    const labels = Array.from(labelsSet);
    const paid = labels.map(date => paidByDay[date] || 0);
    const unpaid = labels.map(date => unpaidByDay[date] || 0);

    return { labels, paid, unpaid };
  };

  const getDefaultData = () => {
    return {
      labels: ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"],
      datasets: [
        {
          label: "Paid Tickets",
          backgroundColor: "rgba(34,197,94,0.8)",
          borderColor: "rgba(34,197,94,1)",
          data: [30, 45, 25, 50, 40, 60, 70],
          barThickness: 8,
        },
        {
          label: "Unpaid Tickets",
          backgroundColor: "rgba(239,68,68,0.8)",
          borderColor: "rgba(239,68,68,1)",
          data: [15, 20, 10, 25, 18, 30, 35],
          barThickness: 8,
        },
      ],
    };
  };

  const renderChart = (data) => {
    let config = {
      type: "bar",
      data: data,
      options: {
        maintainAspectRatio: false,
        responsive: true,
        title: {
          display: false,
          text: "Ticket Performance",
        },
        tooltips: {
          mode: "index",
          intersect: false,
        },
        hover: {
          mode: "nearest",
          intersect: true,
        },
        legend: {
          labels: {
            fontColor: "rgba(0,0,0,.4)",
          },
          align: "end",
          position: "bottom",
        },
        scales: {
          xAxes: [
            {
              display: true,
              scaleLabel: {
                display: false,
                labelString: "Date",
              },
              gridLines: {
                borderDash: [2],
                borderDashOffset: [2],
                color: "rgba(33, 37, 41, 0.3)",
                zeroLineColor: "rgba(33, 37, 41, 0.3)",
                zeroLineBorderDash: [2],
                zeroLineBorderDashOffset: [2],
              },
            },
          ],
          yAxes: [
            {
              display: true,
              scaleLabel: {
                display: false,
                labelString: "Tickets",
              },
              gridLines: {
                borderDash: [2],
                drawBorder: false,
                borderDashOffset: [2],
                color: "rgba(33, 37, 41, 0.2)",
                zeroLineColor: "rgba(33, 37, 41, 0.15)",
                zeroLineBorderDash: [2],
                zeroLineBorderDashOffset: [2],
              },
              ticks: {
                beginAtZero: true,
                precision: 0
              }
            },
          ],
        },
      },
    };

    const ctx = document.getElementById("bar-chart").getContext("2d");

    if (window.myBar) {
      window.myBar.destroy();
    }

    window.myBar = new Chart(ctx, config);
  };

  return (
    <>
      <div className="relative flex flex-col min-w-0 break-words bg-white w-full mb-6 shadow-lg rounded">
        <div className="rounded-t mb-0 px-4 py-3 bg-transparent">
          <div className="flex flex-wrap items-center">
            <div className="relative w-full max-w-full flex-grow flex-1">
              <h6 className="uppercase text-blueGray-400 mb-1 text-xs font-semibold">
                Performance
              </h6>
              <h2 className="text-blueGray-700 text-xl font-semibold">
                Ticket Sales
              </h2>
            </div>
          </div>
        </div>
        <div className="p-4 flex-auto">
          {isLoading && (
            <div className="text-center text-blueGray-700 py-4">Loading data...</div>
          )}
          {error && (
            <div className="text-center text-red-500 py-4">Error: {error}</div>
          )}
          <div className="relative h-350-px">
            <canvas id="bar-chart"></canvas>
          </div>
        </div>
      </div>
    </>
  );
}
