import React, { useEffect, useState } from "react";
import Chart from "chart.js/auto"; // Use Chart.js v3
import { useHistory } from "react-router-dom";

export default function CardBarChart() {
  const history = useHistory();
  const [chartData, setChartData] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchWeeklyTickets = async () => {
      try {
        const token = localStorage.getItem("token");
        const expressId = localStorage.getItem("expressId");

        if (!token) {
          setError("Please log in to view ticket data.");
          history.push("/login"); // Redirect to login if no token
          return;
        }

        const now = new Date();
        const startOfWeek = new Date(now.setDate(now.getDate() - now.getDay() + 1));
        startOfWeek.setHours(0, 0, 0, 0);
        const endOfWeek = new Date(startOfWeek);
        endOfWeek.setDate(endOfWeek.getDate() + 6);
        endOfWeek.setHours(23, 59, 59, 999);

        const response = await fetch(
          `http://localhost:5000/api/reports/date-range?start=${startOfWeek.getTime()}&end=${endOfWeek.getTime()}`,
          {
            method: "GET",
            headers: {
              "Content-Type": "application/json",
              "Authorization": `Bearer ${token}`,
              "X-Express-Id": expressId || "",
            },
          }
        );

        if (!response.ok) {
          if (response.status === 401) {
            setError("Unauthorized: Please log in again.");
            localStorage.removeItem("token"); // Clear invalid token
            history.push("/login");
            return;
          }
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const tickets = await response.json();
        console.log("Fetched tickets for sales:", tickets);

        const labels = ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"];
        const paid = Array(7).fill(0);
        const unpaid = Array(7).fill(0);

        tickets.forEach((ticket) => {
          const day = new Date(ticket.createdAt).getDay();
          const index = day === 0 ? 6 : day - 1;
          if (ticket.paid) {
            paid[index] += 1;
          } else {
            unpaid[index] += 1;
          }
        });

        setChartData({
          labels,
          datasets: [
            {
              label: "Paid Tickets",
              backgroundColor: "rgba(34,197,94,0.8)",
              data: paid,
              barThickness: 8,
            },
            {
              label: "Unpaid Tickets",
              backgroundColor: "rgba(239,68,68,0.8)",
              data: unpaid,
              barThickness: 8,
            },
          ],
        });
        setLoading(false);
      } catch (err) {
        console.error("Error fetching tickets:", err);
        setError("Failed to load ticket data: " + err.message);
        setLoading(false);
      }
    };

    fetchWeeklyTickets();
  }, [history]);

  useEffect(() => {
    if (!chartData) return;

    const ctx = document.getElementById("bar-chart")?.getContext("2d");
    if (!ctx) {
      console.error("Canvas element 'bar-chart' not found.");
      return;
    }

    if (window.myBar) window.myBar.destroy();

    window.myBar = new Chart(ctx, {
      type: "bar",
      data: chartData,
      options: {
        maintainAspectRatio: false,
        responsive: true,
        plugins: {
          legend: {
            position: "bottom",
            labels: { color: "black" },
          },
        },
        scales: {
          x: { ticks: { color: "black" } },
          y: { ticks: { color: "black", beginAtZero: true, precision: 0 } },
        },
      },
    });

    return () => window.myBar && window.myBar.destroy();
  }, [chartData]);

  if (loading) return <p className="text-center p-6">Loading ticket data...</p>;
  if (error) return <p className="text-center p-6 text-red-500">{error}</p>;

  return (
    <div className="relative flex flex-col min-w-0 break-words bg-white w-full mb-6 shadow-lg rounded">
      <div className="rounded-t px-4 py-3 bg-transparent">
        <h2 className="text-blueGray-700 text-xl font-semibold">Ticket Sales</h2>
      </div>
      <div className="p-4 flex-auto">
        <div className="relative h-350-px">
          <canvas id="bar-chart"></canvas>
        </div>
      </div>
    </div>
  );
}
