import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function ScheduleAdd() {
  const navigate = useNavigate();
  const API_URL = "http://localhost:5000/api/schedules";

  const [scheduleData, setScheduleData] = useState({
    express: "",
    bus: "",
    destination: "",
    departureTime: "",
    time: "",
    date: "",
    status: "active"
  });

  const [loading, setLoading] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setScheduleData({ ...scheduleData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const token = localStorage.getItem("token");
      const expressId = localStorage.getItem("expressId");

      const response = await fetch(`${API_URL}/add`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`,
          "X-Express-Id": expressId || "",
        },
        body: JSON.stringify(scheduleData),
      });

      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);

      alert("Schedule added successfully!");
      navigate("/admin/schedules");
    } catch (error) {
      console.error("Error adding schedule:", error);
      alert("Failed to add schedule: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex justify-center mt-8">
      <div className="w-full max-w-2xl bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-semibold mb-6">Add New Schedule</h2>
        <form className="grid grid-cols-1 md:grid-cols-2 gap-4" onSubmit={handleSubmit}>
          <select
            name="express"
            value={scheduleData.express}
            onChange={handleInputChange}
            className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          >
            <option value="">Select Express Route</option>
            <option value="Kigali - Huye">Kigali - Huye</option>
            <option value="Kigali - Musanze">Kigali - Musanze</option>
            <option value="Kigali - Kibuye">Kigali - Kibuye</option>
            <option value="Kigali - Rusizi">Kigali - Rusizi</option>
          </select>

          <select
            name="bus"
            value={scheduleData.bus}
            onChange={handleInputChange}
            className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          >
            <option value="">Select Bus</option>
            <option value="Bus 001">Bus 001</option>
            <option value="Bus 002">Bus 002</option>
            <option value="Bus 003">Bus 003</option>
            <option value="Bus 004">Bus 004</option>
          </select>

          <input
            type="text"
            name="destination"
            value={scheduleData.destination}
            onChange={handleInputChange}
            placeholder="Destination"
            className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />

          <input
            type="date"
            name="date"
            value={scheduleData.date}
            onChange={handleInputChange}
            className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />

          <input
            type="time"
            name="departureTime"
            value={scheduleData.departureTime}
            onChange={handleInputChange}
            className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />

          <input
            type="time"
            name="time"
            value={scheduleData.time}
            onChange={handleInputChange}
            className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />

          <select
            name="status"
            value={scheduleData.status}
            onChange={handleInputChange}
            className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <option value="active">Active</option>
            <option value="inactive">Inactive</option>
            <option value="cancelled">Cancelled</option>
          </select>

          <div className="md:col-span-2 flex space-x-4 mt-4">
            <button
              type="submit"
              disabled={loading}
              className="px-4 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition-colors"
            >
              {loading ? "Saving..." : "Add Schedule"}
            </button>
            <button
              type="button"
              onClick={() => navigate("/admin/schedules")}
              className="px-4 py-2 bg-gray-300 text-gray-700 rounded-md hover:bg-gray-400 transition-colors"
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
