import React, { useEffect, useState } from "react";
import Chart from "chart.js";

export default function CardLineChart() {
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    const fetchWeeklyRevenue = async () => {
      try {
        // Get start (Monday) & end (Sunday) of current week
        const now = new Date();
        const startOfWeek = new Date(now.setDate(now.getDate() - now.getDay() + 1));
        startOfWeek.setHours(0, 0, 0, 0);
        const endOfWeek = new Date(startOfWeek);
        endOfWeek.setDate(endOfWeek.getDate() + 6);
        endOfWeek.setHours(23, 59, 59, 999);

        const response = await fetch(
          `http://localhost:5000/api/reports/date-range?start=${startOfWeek.getTime()}&end=${endOfWeek.getTime()}`,
          { credentials: "include" }
        );
        const tickets = await response.json();

        // Group by day of week (Mon-Sun)
        const labels = ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"];
        const revenuePerDay = Array(7).fill(0);

        tickets.forEach(ticket => {
          const day = new Date(ticket.createdAt).getDay(); // Sunday=0
          const index = day === 0 ? 6 : day - 1; // shift Sunday to last
          revenuePerDay[index] += ticket.price || 0;
        });

        setChartData({
          labels,
          datasets: [
            {
              label: "Revenue (RWF)",
              backgroundColor: "rgba(59,130,246,0.1)",
              borderColor: "rgb(59,130,246)",
              data: revenuePerDay,
              fill: true,
              tension: 0.4,
            },
          ],
        });
      } catch (err) {
        console.error("Error fetching revenue:", err);
      }
    };

    fetchWeeklyRevenue();
  }, []);

  useEffect(() => {
    if (!chartData) return;

    const ctx = document.getElementById("line-chart").getContext("2d");
    if (window.myLine) window.myLine.destroy();

    window.myLine = new Chart(ctx, {
      type: "line",
      data: chartData,
      options: {
        maintainAspectRatio: false,
        responsive: true,
        legend: {
          labels: { fontColor: "white" },
          align: "end",
          position: "bottom",
        },
        scales: {
          xAxes: [{ ticks: { fontColor: "white" } }],
          yAxes: [{ ticks: { fontColor: "white", beginAtZero: true } }],
        },
      },
    });

    return () => window.myLine && window.myLine.destroy();
  }, [chartData]);

  return (
    <div className="relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded bg-blueGray-700">
      <div className="rounded-t px-4 py-3 bg-transparent">
        <h2 className="text-white text-xl font-semibold">Revenue Overview</h2>
      </div>
      <div className="p-4 flex-auto">
        <div className="relative h-350-px">
          <canvas id="line-chart"></canvas>
        </div>
      </div>
    </div>
  );
}
