import React, { useState, useEffect } from "react";
import Chart from "chart.js";

export default function CardLineChart() {
    const [chartData, setChartData] = useState(null);
    const [destinations, setDestinations] = useState([]);
    const [startDate, setStartDate] = useState("");
    const [endDate, setEndDate] = useState("");
    const [destination, setDestination] = useState("");

    // Fetch destinations on mount
    useEffect(() => {
        fetch("http://localhost:8080/api/reports/destinations", {
            headers: { Authorization: "Bearer your-token-here" }, // Replace with actual token
        })
            .then((response) => response.json())
            .then((data) => setDestinations(data))
            .catch((error) => console.error("Error fetching destinations:", error));
    }, []);

    // Fetch ticket data based on filters
    useEffect(() => {
        const fetchTickets = async () => {
            try {
                let url = "http://localhost:8080/api/reports/all";
                if (startDate && endDate) {
                    const startMillis = new Date(startDate).getTime();
                    const endMillis = new Date(endDate).getTime();
                    url = `http://localhost:8080/api/reports/date-range?start=${startMillis}&end=${endMillis}`;
                    if (destination) {
                        url += `&destination=${encodeURIComponent(destination)}`;
                    }
                } else if (destination) {
                    const startMillis = new Date().setDate(new Date().getDate() - 7);
                    const endMillis = Date.now();
                    url = `http://localhost:8080/api/reports/destination?destination=${encodeURIComponent(destination)}&start=${startMillis}&end=${endMillis}`;
                }

                const response = await fetch(url, {
                    headers: { Authorization: "Bearer your-token-here" }, // Replace with actual token
                });
                if (!response.ok) throw new Error("Failed to fetch tickets");
                const tickets = await response.json();

                // Process data for chart
                const days = 7;
                const today = new Date();
                const labels = Array.from({ length: days }, (_, i) => {
                    const date = new Date(today);
                    date.setDate(today.getDate() - (days - 1 - i));
                    return date.toLocaleDateString("en-US", { weekday: "short" });
                });

                const revenueData = Array(days).fill(0);
                tickets.forEach((ticket) => {
                    const ticketDate = new Date(ticket.createdAt);
                    const dayIndex = Math.floor((today - ticketDate) / (1000 * 60 * 60 * 24));
                    if (dayIndex >= 0 && dayIndex < days) {
                        const index = days - 1 - dayIndex;
                        revenueData[index] += ticket.price || 0;
                    }
                });

                setChartData({
                    labels,
                    datasets: [
                        {
                            label: "Revenue (RWF)",
                            backgroundColor: "rgba(59,130,246,0.1)",
                            borderColor: "rgb(59,130,246)",
                            data: revenueData,
                            fill: true,
                            tension: 0.4,
                        },
                    ],
                });
            } catch (error) {
                console.error("Error fetching tickets:", error);
                setChartData({
                    labels: ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"],
                    datasets: [
                        {
                            label: "Revenue (RWF)",
                            backgroundColor: "rgba(59,130,246,0.1)",
                            borderColor: "rgb(59,130,246)",
                            data: [3000, 4500, 2500, 5000, 4000, 6000, 7000],
                            fill: true,
                            tension: 0.4,
                        },
                    ],
                });
            }
        };

        fetchTickets();
    }, [startDate, endDate, destination]);

    // Chart rendering
    useEffect(() => {
        if (!chartData) return;

        const config = {
            type: "line",
            data: chartData,
            options: {
                maintainAspectRatio: false,
                responsive: true,
                plugins: {
                    title: { display: false },
                    legend: {
                        labels: { color: "white" },
                        align: "end",
                        position: "bottom",
                    },
                    tooltip: {
                        mode: "index",
                        intersect: false,
                    },
                },
                hover: {
                    mode: "nearest",
                    intersect: true,
                },
                scales: {
                    x: {
                        ticks: { color: "rgba(255,255,255,.7)" },
                        display: true,
                        grid: {
                            display: false,
                            borderDash: [2],
                            borderDashOffset: [2],
                            color: "rgba(33, 37, 41, 0.3)",
                            zeroLineColor: "rgba(0, 0, 0, 0)",
                        },
                    },
                    y: {
                        ticks: {
                            color: "rgba(255,255,255,.7)",
                            beginAtZero: true,
                            precision: 0,
                        },
                        display: true,
                        grid: {
                            borderDash: [3],
                            borderDashOffset: [3],
                            drawBorder: false,
                            color: "rgba(255, 255, 255, 0.15)",
                            zeroLineColor: "rgba(33, 37, 41, 0)",
                        },
                    },
                },
            },
        };

        const ctx = document.getElementById("line-chart").getContext("2d");
        if (window.myLine) {
            window.myLine.destroy();
        }
        window.myLine = new Chart(ctx, config);

        return () => {
            if (window.myLine) {
                window.myLine.destroy();
            }
        };
    }, [chartData]);

    return (
        <div className="container mx-auto p-4">
            <div className="bg-white p-6 rounded-lg shadow-lg mb-6">
                <h3 className="text-lg font-semibold mb-4">Filter Revenue Data</h3>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Start Date</label>
                        <input
                            type="date"
                            value={startDate}
                            onChange={(e) => setStartDate(e.target.value)}
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-500 focus:ring-opacity-50"
                        />
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">End Date</label>
                        <input
                            type="date"
                            value={endDate}
                            onChange={(e) => setEndDate(e.target.value)}
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-500 focus:ring-opacity-50"
                        />
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Destination</label>
                        <select
                            value={destination}
                            onChange={(e) => setDestination(e.target.value)}
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-500 focus:ring-opacity-50"
                        >
                            <option value="">All Destinations</option>
                            {destinations.map((dest) => (
                                <option key={dest} value={dest}>{dest}</option>
                            ))}
                        </select>
                    </div>
                </div>
            </div>
            <div className="relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded bg-gray-800">
                <div className="rounded-t mb-0 px-4 py-3 bg-transparent">
                    <div className="flex flex-wrap items-center">
                        <div className="relative w-full max-w-full flex-grow flex-1">
                            <h6 className="uppercase text-gray-200 mb-1 text-xs font-semibold">Overview</h6>
                            <h2 className="text-white text-xl font-semibold">Revenue Overview</h2>
                        </div>
                    </div>
                </div>
                <div className="p-4 flex-auto">
                    <div className="relative h-80">
                        <canvas id="line-chart"></canvas>
                    </div>
                </div>
            </div>
        </div>
    );
}
