import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";

export default function TicketAdd() {
  const history = useHistory();
  const API_URL = "http://localhost:5000/api/tickets";

  const [ticketData, setTicketData] = useState({
    names: "",
    email: "",
    phone: "",
    express: "",
    schedule: "",
    payState: "PENDING",
    date: "",
    seatNo: "",
  });

  const [expressList, setExpressList] = useState([]);
  const [scheduleList, setScheduleList] = useState([]);
  const [role, setRole] = useState("");
  const [existingTickets, setExistingTickets] = useState([]);
  const [availableSeats, setAvailableSeats] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem("token");
        const expressId = localStorage.getItem("expressId");

        // Get user role
        let userRole = "";
        const userObj = localStorage.getItem("user");
        if (userObj) {
          const user = JSON.parse(userObj);
          userRole = user.role?.toUpperCase() || "";
        } else {
          userRole = localStorage.getItem("role")?.toUpperCase() || "";
        }
        setRole(userRole);

        // Fetch express list
        const expressResponse = await fetch("http://localhost:5000/api/express/all", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setExpressList(await expressResponse.json());

        // Fetch schedule list
        const scheduleResponse = await fetch("http://localhost:5000/api/schedules/all", {
          headers: { Authorization: `Bearer ${token}`, "X-Express-Id": expressId || "" },
        });
        setScheduleList(await scheduleResponse.json());

        // Fetch existing tickets
        const ticketsResponse = await fetch(`${API_URL}/all`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setExistingTickets(await ticketsResponse.json());

        // Set express for non-admin
        if (userRole !== "SUPER_ADMIN") {
          setTicketData(prev => ({ ...prev, express: expressId }));
        }

        setLoading(false);
      } catch (error) {
        console.error(error);
        setLoading(false);
        alert("Error fetching data: " + error.message);
      }
    };

    fetchData();
  }, []);

  // Update available seats when schedule changes
  useEffect(() => {
    if (!ticketData.schedule) return;
    const selectedSchedule = scheduleList.find(s => s.id === ticketData.schedule);
    if (!selectedSchedule) return;

    const bookedSeats = existingTickets
      .filter(t => t.schedule?.some(s => s.id === ticketData.schedule))
      .map(t => t.seatNo);

    const seats = [];
    for (let i = 1; i <= selectedSchedule.bus.capacity; i++) {
      if (!bookedSeats.includes(i)) seats.push(i);
    }
    setAvailableSeats(seats);

    setTicketData(prev => ({
      ...prev,
      seatNo: seats.length > 0 ? seats[0] : "",
    }));
  }, [ticketData.schedule, scheduleList, existingTickets]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTicketData({ ...ticketData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!ticketData.seatNo) return alert("No seats available.");

    try {
      const token = localStorage.getItem("token");

      const payload = {
        ...ticketData,
        express: role === "SUPER_ADMIN"
          ? expressList.find(exp => exp.id === ticketData.express)
          : { id: ticketData.express },
        schedule: [{ id: ticketData.schedule }],
      };

      const response = await fetch(`${API_URL}/register`, {
        method: "POST",
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify(payload),
      });

      if (!response.ok) throw new Error(await response.text());

      const savedTicket = await response.json();
      alert("Ticket added successfully!");

      // Generate PDF ticket
      generatePDF(savedTicket);
      history.push("/admin/tickets");
    } catch (error) {
      console.error(error);
      alert("Error saving ticket: " + error.message);
    }
  };

  const generatePDF = (ticket) => {
    const doc = new jsPDF();
    doc.setFontSize(16);
    doc.text("Ticket Confirmation", 20, 20);

    doc.setFontSize(12);
    doc.text(`Name: ${ticket.names}`, 20, 40);
    doc.text(`Phone: ${ticket.phone}`, 20, 50);
    doc.text(`Email: ${ticket.email}`, 20, 60);
    doc.text(`Express: ${ticket.express?.expressName}`, 20, 70);
    doc.text(`Schedule: ${ticket.schedule?.map(s => `${s.id}`).join(", ")}`, 20, 80);
    doc.text(`Date: ${new Date(ticket.date).toLocaleDateString()}`, 20, 90);
    doc.text(`Bus Plate No: ${ticket.busPlateNo}`, 20, 100);
    doc.text(`Ticket No: ${ticket.tickeNumber}`, 20, 110);
    doc.text(`Seat No: ${ticket.seatNo}`, 20, 120);

    doc.save(`Ticket_${ticket.tickeNumber}.pdf`);
  };

  if (loading) return <p className="p-6 text-center">Loading...</p>;

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white shadow-md rounded-lg">
      <h2 className="text-xl font-bold mb-4">Add Ticket</h2>
      <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <input name="names" placeholder="Name" value={ticketData.names} onChange={handleInputChange} required className="border p-2 rounded"/>
        <input name="email" type="email" placeholder="Email" value={ticketData.email} onChange={handleInputChange} className="border p-2 rounded"/>
        <input name="phone" placeholder="Phone" value={ticketData.phone} onChange={handleInputChange} required className="border p-2 rounded"/>
        
        {role === "SUPER_ADMIN" && (
          <select name="express" value={ticketData.express} onChange={handleInputChange} required className="border p-2 rounded">
            <option value="">Select Express</option>
            {expressList.map(exp => <option key={exp.id} value={exp.id}>{exp.expressName}</option>)}
          </select>
        )}

        <select name="schedule" value={ticketData.schedule} onChange={handleInputChange} required className="border p-2 rounded">
          <option value="">Select Schedule</option>
          {scheduleList.map(sch => (
            <option key={sch.id} value={sch.id}>
              {sch.destination.fromLocation} - {sch.destination.toLocation} ({sch.date} {sch.time})
            </option>
          ))}
        </select>

        <select name="seatNo" value={ticketData.seatNo} onChange={handleInputChange} required className="border p-2 rounded" disabled>
          {availableSeats.map(seat => <option key={seat} value={seat}>{seat}</option>)}
        </select>

        <select name="payState" value={ticketData.payState} onChange={handleInputChange} required className="border p-2 rounded">
          <option value="PENDING">Pending</option>
          <option value="PAID">Paid</option>
        </select>

        <input name="date" type="date" value={ticketData.date} onChange={handleInputChange} required className="border p-2 rounded"/>
        
        <button type="submit" className="col-span-2 bg-black text-white p-2 rounded mt-2">Save Ticket</button>
      </form>
    </div>
  );
}
