import React, { useState, useEffect, useRef } from "react";
import Chart from "chart.js";

export default function CardLineChart({ chartData, title = "Revenue", startDate, endDate }) {
  const [isLoading, setIsLoading] = useState(false);
  const [data, setData] = useState(chartData || { labels: [], datasets: [] });
  const chartRef = useRef(null);
  const chartInstance = useRef(null);

  useEffect(() => {
    if (chartData) {
      setData(chartData);
    } else if (startDate && endDate) {
      fetchChartData();
    }
  }, [chartData, startDate, endDate]);

  const fetchChartData = async () => {
    try {
      setIsLoading(true);
      const startMillis = new Date(startDate).getTime();
      const endMillis = new Date(endDate).getTime();

      const token = localStorage.getItem("token");
      const response = await fetch(
        `http://localhost:5000/api/reports/date-range?start=${startMillis}&end=${endMillis}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`,
          },
        }
      );

      if (!response.ok) {
        throw new Error("Failed to fetch chart data");
      }

      const reportData = await response.json();
      processChartData(reportData);
    } catch (error) {
      console.error("Error fetching chart data:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const processChartData = (reportData) => {
    // Group tickets by Date only
    const groupedByDate = {};
    reportData.forEach((t) => {
      const date = new Date(t.date).toLocaleDateString();

      if (!groupedByDate[date]) {
        groupedByDate[date] = {
          date,
          revenue: 0,
        };
      }

      groupedByDate[date].revenue += t.totalAmount || 0;
    });

    const groupedData = Object.values(groupedByDate);
    const sortedData = groupedData.sort(
      (a, b) => new Date(a.date) - new Date(b.date)
    );

    const labels = sortedData.map((r) => r.date);

    // Revenue chart data
    const revenueChart = {
      labels,
      datasets: [
        {
          label: "Revenue (RWF)",
          data: sortedData.map((r) => r.revenue),
          borderColor: "rgb(59,130,246)",
          backgroundColor: "rgba(59,130,246,0.1)",
          tension: 0.4,
        },
      ],
    };

    setData(revenueChart);
  };

  useEffect(() => {
    if (data.labels.length === 0 || !chartRef.current) return;

    // Destroy previous chart if it exists
    if (chartInstance.current) {
      chartInstance.current.destroy();
    }

    var config = {
      type: "line",
      data: data,
      options: {
        maintainAspectRatio: false,
        responsive: true,
        title: {
          display: false,
          text: title,
          fontColor: "white",
        },
        legend: {
          labels: {
            fontColor: "white",
          },
          align: "end",
          position: "bottom",
        },
        tooltips: {
          mode: "index",
          intersect: false,
          callbacks: {
            label: function(tooltipItem, data) {
              return data.datasets[tooltipItem.datasetIndex].label + ': ' + 
                tooltipItem.yLabel.toLocaleString() + (title === "Revenue" ? ' RWF' : '');
            }
          }
        },
        hover: {
          mode: "nearest",
          intersect: true,
        },
        scales: {
          xAxes: [
            {
              ticks: {
                fontColor: "rgba(255,255,255,.7)",
              },
              display: true,
              scaleLabel: {
                display: false,
                labelString: "Date",
                fontColor: "white",
              },
              gridLines: {
                display: false,
                borderDash: [2],
                borderDashOffset: [2],
                color: "rgba(33, 37, 41, 0.3)",
                zeroLineColor: "rgba(0, 0, 0, 0)",
                zeroLineBorderDash: [2],
                zeroLineBorderDashOffset: [2],
              },
            },
          ],
          yAxes: [
            {
              ticks: {
                fontColor: "rgba(255,255,255,.7)",
                beginAtZero: true,
                callback: function(value) {
                  return value.toLocaleString() + (title === "Revenue" ? ' RWF' : '');
                }
              },
              display: true,
              scaleLabel: {
                display: false,
                labelString: title,
                fontColor: "white",
              },
              gridLines: {
                borderDash: [3],
                borderDashOffset: [3],
                drawBorder: false,
                color: "rgba(255, 255, 255, 0.15)",
                zeroLineColor: "rgba(33, 37, 41, 0)",
                zeroLineBorderDash: [2],
                zeroLineBorderDashOffset: [2],
              },
            },
          ],
        },
      },
    };

    const ctx = chartRef.current.getContext("2d");
    chartInstance.current = new Chart(ctx, config);

    // Cleanup function
    return () => {
      if (chartInstance.current) {
        chartInstance.current.destroy();
      }
    };
  }, [data, title]);

  return (
    <>
      <div className="relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded bg-blueGray-700">
        <div className="rounded-t mb-0 px-4 py-3 bg-transparent">
          <div className="flex flex-wrap items-center">
            <div className="relative w-full max-w-full flex-grow flex-1">
              <h6 className="uppercase text-blueGray-100 mb-1 text-xs font-semibold">
                Overview
              </h6>
              <h2 className="text-white text-xl font-semibold">{title}</h2>
            </div>
          </div>
        </div>
        <div className="p-4 flex-auto">
          {/* Chart */}
          <div className="relative h-350-px">
            {isLoading ? (
              <div className="flex items-center justify-center h-full">
                <div className="text-white">
                  <i className="fas fa-spinner fa-spin mr-2"></i>
                  Loading chart data...
                </div>
              </div>
            ) : data.labels.length > 0 ? (
              <canvas ref={chartRef} id="line-chart"></canvas>
            ) : (
              <div className="flex items-center justify-center h-full">
                <div className="text-white text-center">
                  <i className="fas fa-chart-line text-2xl mb-2"></i>
                  <p>No data available</p>
                  <p className="text-sm">Select a date range to generate a report</p>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </>
  );
}
