import React, { useState, useEffect } from "react";
import Chart from "chart.js";

export default function CardLineChart() {
  const [chartData, setChartData] = useState(null);
  const [loading, setLoading] = useState(true);
  const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";

  useEffect(() => {
    const fetchTicketData = async () => {
      try {
        const token = localStorage.getItem("token");
        const expressId = localStorage.getItem("expressId");

        const headers = {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
          "X-Express-Id": expressId || "",
        };

        // Fetch tickets for the current month
        const response = await fetch(`${API_URL}/api/dashboard/ticket/current-month`, {
          method: "GET",
          headers,
        });
        if (!response.ok) throw new Error("Failed to fetch tickets");
        const tickets = await response.json();

        // Aggregate revenue by day for August 2025
        const daysInMonth = new Date(2025, 8, 0).getDate(); // August 2025 has 31 days
        const revenueByDay = Array(daysInMonth).fill(0);
        const labels = Array.from({ length: daysInMonth }, (_, i) => `Aug ${i + 1}`);

        tickets.forEach((ticket) => {
          const ticketDate = new Date(ticket.createdAt);
          const day = ticketDate.getDate() - 1; // 0-based index for array
          if (ticketDate.getMonth() === 7 && ticketDate.getFullYear() === 2025) { // August is month 7 (0-based)
            revenueByDay[day] += ticket.price || 0;
          }
        });

        // Prepare chart data
        const data = {
          labels,
          datasets: [
            {
              label: "Revenue (RWF)",
              backgroundColor: "rgba(59,130,246,0.1)",
              borderColor: "rgb(59,130,246)",
              data: revenueByDay,
              fill: true,
              tension: 0.4,
            },
          ],
        };

        setChartData(data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching ticket data:", error);
        setLoading(false);
        // Use default data on error
        setChartData({
          labels: ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"],
          datasets: [
            {
              label: "Revenue (RWF)",
              backgroundColor: "rgba(59,130,246,0.1)",
              borderColor: "rgb(59,130,246)",
              data: [3000, 4500, 2500, 5000, 4000, 6000, 7000],
              fill: true,
              tension: 0.4,
            },
          ],
        });
      }
    };

    fetchTicketData();
  }, []);

  useEffect(() => {
    if (!chartData) return;

    const canvas = document.getElementById("line-chart");
    if (!canvas) {
      console.error("Canvas element with id 'line-chart' not found");
      return;
    }

    const ctx = canvas.getContext("2d");
    if (!ctx) {
      console.error("Failed to get 2D context for canvas");
      return;
    }

    const config = {
      type: "line",
      data: chartData,
      options: {
        maintainAspectRatio: false,
        responsive: true,
        title: {
          display: false,
          text: "Revenue Overview",
          fontColor: "white",
        },
        legend: {
          labels: {
            fontColor: "white",
          },
          align: "end",
          position: "bottom",
        },
        tooltips: {
          mode: "index",
          intersect: false,
        },
        hover: {
          mode: "nearest",
          intersect: true,
        },
        scales: {
          xAxes: [
            {
              ticks: {
                fontColor: "rgba(255,255,255,.7)",
              },
              display: true,
              scaleLabel: {
                display: false,
                labelString: "Date",
                fontColor: "white",
              },
              gridLines: {
                display: false,
                borderDash: [2],
                borderDashOffset: [2],
                color: "rgba(33, 37, 41, 0.3)",
                zeroLineColor: "rgba(0, 0, 0, 0)",
                zeroLineBorderDash: [2],
                zeroLineBorderDashOffset: [2],
              },
            },
          ],
          yAxes: [
            {
              ticks: {
                fontColor: "rgba(255,255,255,.7)",
                beginAtZero: true,
                precision: 0,
              },
              display: true,
              scaleLabel: {
                display: false,
                labelString: "Revenue (RWF)",
                fontColor: "white",
              },
              gridLines: {
                borderDash: [3],
                borderDashOffset: [3],
                drawBorder: false,
                color: "rgba(255, 255, 255, 0.15)",
                zeroLineColor: "rgba(33, 37, 41, 0)",
                zeroLineBorderDash: [2],
                zeroLineBorderDashOffset: [2],
              },
            },
          ],
        },
      },
    };

    if (window.myLine) {
      window.myLine.destroy();
    }

    window.myLine = new Chart(ctx, config);

    return () => {
      if (window.myLine) {
        window.myLine.destroy();
      }
    };
  }, [chartData]);

  if (loading) {
    return <div className="text-center p-6">Loading chart...</div>;
  }

  return (
    <div className="relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded bg-blueGray-700">
      <div className="rounded-t mb-0 px-4 py-3 bg-transparent">
        <div className="flex flex-wrap items-center">
          <div className="relative w-full max-w-full flex-grow flex-1">
            <h6 className="uppercase text-blueGray-100 mb-1 text-xs font-semibold">
              Overview
            </h6>
            <h2 className="text-white text-xl font-semibold">Revenue Overview</h2>
          </div>
        </div>
      </div>
      <div className="p-4 flex-auto">
        <div className="relative h-350-px">
          <canvas id="line-chart"></canvas>
        </div>
      </div>
    </div>
  );
}
