import React, { useState, useEffect } from "react";
import CardStats from "components/Cards/CardStats.js";
import CardLineChart from "components/Cards/CardLineChart.js";
import CardBarChart from "components/Cards/CardBarChart.js";
import CardTable from "components/Cards/CardTable.js";

export default function Reports() {
  const [tickets, setTickets] = useState([]);
  const [reportType, setReportType] = useState("overview");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  // Fetch all tickets when component mounts
  useEffect(() => {
    fetchTickets();
  }, []);

  const fetchTickets = async () => {
    try {
      const token = JSON.parse(localStorage.getItem("user"))?.token;
      const response = await fetch("http://localhost:5000/api/reports/all", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) throw new Error("Failed to fetch tickets");
      const data = await response.json();
      setTickets(data);
    } catch (error) {
      console.error(error);
      alert("Error fetching tickets");
    }
  };

  const fetchTicketsByDate = async () => {
    if (!startDate || !endDate) {
      alert("Please select both start and end dates");
      return;
    }

    try {
      const token = JSON.parse(localStorage.getItem("user"))?.token;
      const response = await fetch(
        `http://localhost:5000/api/reports/date-range?start=${new Date(
          startDate
        ).getTime()}&end=${new Date(endDate).getTime()}`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (!response.ok) throw new Error("Failed to fetch tickets by date");
      const data = await response.json();
      setTickets(data);
    } catch (error) {
      console.error(error);
      alert("Error fetching date range report");
    }
  };

  // Example stats
  const totalRevenue = tickets.reduce((sum, t) => sum + (t.price || 0), 0);
  const totalTickets = tickets.length;

  const revenueData = {
    labels: tickets.map((t) => new Date(t.createdAt).toLocaleDateString()),
    datasets: [
      {
        label: "Revenue (RWF)",
        data: tickets.map((t) => t.price || 0),
        borderColor: "rgb(59, 130, 246)",
        backgroundColor: "rgba(59, 130, 246, 0.1)",
      },
    ],
  };

  const ticketSalesData = {
    labels: tickets.map((t) => new Date(t.createdAt).toLocaleDateString()),
    datasets: [
      {
        label: "Tickets Sold",
        data: tickets.map(() => 1), // each ticket = 1
        backgroundColor: "rgba(34, 197, 94, 0.8)",
      },
    ],
  };

  return (
    <>
      <div className="flex flex-wrap mt-4">
        <div className="w-full xl:w-8/12 mb-12 xl:mb-0 px-4">
          <CardStats
            statSubtitle="Total Revenue"
            statTitle={`RWF ${totalRevenue.toLocaleString()}`}
            statArrow="up"
            statPercent="12"
            statPercentColor="text-emerald-500"
            statDescripiron="Since last month"
            statIconName="fas fa-dollar-sign"
            statIconColor="bg-emerald-500"
          />
        </div>
        <div className="w-full xl:w-4/12 px-4">
          <CardStats
            statSubtitle="Total Tickets"
            statTitle={totalTickets.toString()}
            statArrow="up"
            statPercent="3.48"
            statPercentColor="text-emerald-500"
            statDescripiron="Since last week"
            statIconName="fas fa-ticket-alt"
            statIconColor="bg-red-500"
          />
        </div>
      </div>

      {/* Date Range Selection */}
      <div className="flex flex-wrap mt-4">
        <div className="w-full px-4">
          <div className="bg-white rounded-lg shadow-md p-6 mb-6">
            <h3 className="text-lg font-semibold text-gray-800 mb-6">
              Report Configuration
            </h3>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Start Date
                </label>
                <input
                  type="date"
                  value={startDate}
                  onChange={(e) => setStartDate(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  End Date
                </label>
                <input
                  type="date"
                  value={endDate}
                  onChange={(e) => setEndDate(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
            </div>

            <div className="flex space-x-4">
              <button
                onClick={fetchTicketsByDate}
                className="px-6 py-2 bg-red-500 text-white rounded-md hover:bg-red-600 transition-colors font-medium"
              >
                <i className="fas fa-chart-line mr-2"></i>
                Generate Report
              </button>
              <button
                onClick={() => alert("Export not implemented yet")}
                className="px-6 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition-colors font-medium"
              >
                <i className="fas fa-download mr-2"></i>
                Export Report
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Charts */}
      <div className="flex flex-wrap mt-4">
        <div className="w-full xl:w-8/12 mb-12 xl:mb-0 px-4">
          <CardLineChart chartData={revenueData} />
        </div>
        <div className="w-full xl:w-4/12 px-4">
          <CardBarChart chartData={ticketSalesData} />
        </div>
      </div>

      {/* Table */}
      <div className="flex flex-wrap mt-4">
        <div className="w-full px-4">
          <CardTable>
            <div className="block w-full overflow-x-auto">
              <table className="items-center w-full bg-transparent border-collapse">
                <thead>
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">
                      Date
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">
                      Route
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">
                      Price
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">
                      Status
                    </th>
                  </tr>
                </thead>
                <tbody>
                  {tickets.map((t) => (
                    <tr key={t.id}>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">
                        {new Date(t.date).toLocaleDateString()}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">
                        {t.destination.fromLocation || "N/A"}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">
                        RWF {t.price?.toLocaleString()}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">
                        {t.state ? "Completed" : "Pending"}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </CardTable>
        </div>
      </div>
    </>
  );
}
