import React, { useState, useEffect } from "react";
import Chart from "chart.js";

export default function CardBarChart({ chartData, title = "Ticket Sales", startDate, endDate }) {
  const [isLoading, setIsLoading] = useState(false);
  const [data, setData] = useState(chartData || { labels: [], datasets: [] });

  useEffect(() => {
    if (chartData) {
      setData(chartData);
    } else if (startDate && endDate) {
      fetchChartData();
    }
  }, [chartData, startDate, endDate]);

  const fetchChartData = async () => {
    try {
      setIsLoading(true);
      const startMillis = new Date(startDate).getTime();
      const endMillis = new Date(endDate).getTime();

      const token = localStorage.getItem("token");
      const response = await fetch(
        `http://localhost:5000/api/reports/date-range?start=${startMillis}&end=${endMillis}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`,
          },
        }
      );

      if (!response.ok) {
        throw new Error("Failed to fetch chart data");
      }

      const reportData = await response.json();
      processChartData(reportData);
    } catch (error) {
      console.error("Error fetching chart data:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const processChartData = (reportData) => {
    // Group tickets by Date only
    const groupedByDate = {};
    reportData.forEach((t) => {
      const date = new Date(t.date).toLocaleDateString();

      if (!groupedByDate[date]) {
        groupedByDate[date] = {
          date,
          paidTickets: 0,
          unpaidTickets: 0,
        };
      }

      if (t.payState === "PAID") {
        groupedByDate[date].paidTickets += 1;
      } else {
        groupedByDate[date].unpaidTickets += 1;
      }
    });

    const groupedData = Object.values(groupedByDate);
    const sortedData = groupedData.sort(
      (a, b) => new Date(a.date) - new Date(b.date)
    );

    const labels = sortedData.map((r) => r.date);

    // Ticket sales chart data
    const ticketChart = {
      labels,
      datasets: [
        {
          label: "Paid Tickets",
          data: sortedData.map((r) => r.paidTickets),
          backgroundColor: "rgba(34,197,94,0.8)",
        },
        {
          label: "Unpaid Tickets",
          data: sortedData.map((r) => r.unpaidTickets),
          backgroundColor: "rgba(239,68,68,0.8)",
        },
      ],
    };

    setData(ticketChart);
  };

  useEffect(() => {
    if (data.labels.length === 0) return;

    let config = {
      type: "bar",
      data: data,
      options: {
        maintainAspectRatio: false,
        responsive: true,
        title: {
          display: false,
          text: title,
        },
        tooltips: {
          mode: "index",
          intersect: false,
        },
        hover: {
          mode: "nearest",
          intersect: true,
        },
        legend: {
          labels: {
            fontColor: "rgba(0,0,0,.4)",
          },
          align: "end",
          position: "bottom",
        },
        scales: {
          xAxes: [
            {
              display: true,
              scaleLabel: {
                display: false,
                labelString: "Date",
              },
              gridLines: {
                borderDash: [2],
                borderDashOffset: [2],
                color: "rgba(33, 37, 41, 0.3)",
                zeroLineColor: "rgba(33, 37, 41, 0.3)",
                zeroLineBorderDash: [2],
                zeroLineBorderDashOffset: [2],
              },
            },
          ],
          yAxes: [
            {
              display: true,
              scaleLabel: {
                display: false,
                labelString: "Value",
              },
              gridLines: {
                borderDash: [2],
                drawBorder: false,
                borderDashOffset: [2],
                color: "rgba(33, 37, 41, 0.2)",
                zeroLineColor: "rgba(33, 37, 41, 0.15)",
                zeroLineBorderDash: [2],
                zeroLineBorderDashOffset: [2],
              },
              ticks: {
                beginAtZero: true,
                precision: 0
              }
            },
          ],
        },
      },
    };

    const ctx = document.getElementById("bar-chart").getContext("2d");

    // Destroy previous chart if it exists
    if (window.myBar) {
      window.myBar.destroy();
    }

    window.myBar = new Chart(ctx, config);

    // Cleanup function
    return () => {
      if (window.myBar) {
        window.myBar.destroy();
      }
    };
  }, [data, title]);

  return (
    <>
      {/* Card chart */}
      <div className="relative flex flex-col min-w-0 break-words bg-white w-full mb-6 shadow-lg rounded">
        <div className="rounded-t mb-0 px-4 py-3 bg-transparent">
          <div className="flex flex-wrap items-center">
            <div className="relative w-full max-w-full flex-grow flex-1">
              <h6 className="uppercase text-blueGray-400 mb-1 text-xs font-semibold">
                Performance
              </h6>
              <h2 className="text-blueGray-700 text-xl font-semibold">
                {title}
              </h2>
            </div>
          </div>
        </div>
        <div className="p-4 flex-auto">
          {/* Chart */}
          <div className="relative h-350-px">
            {isLoading ? (
              <div className="flex items-center justify-center h-full">
                <div className="text-blueGray-700">
                  <i className="fas fa-spinner fa-spin mr-2"></i>
                  Loading chart data...
                </div>
              </div>
            ) : data.labels.length > 0 ? (
              <canvas id="bar-chart"></canvas>
            ) : (
              <div className="flex items-center justify-center h-full">
                <div className="text-blueGray-700 text-center">
                  <i className="fas fa-chart-bar text-2xl mb-2"></i>
                  <p>No data available</p>
                  <p className="text-sm">Select a date range to generate a report</p>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </>
  );
}
