import React, { useState, useEffect } from "react";
import { useHistory, useParams } from "react-router-dom";

export default function ExpressAdd() {
  const history = useHistory();
  const { expressId } = useParams(); // get expressId from URL if editing
  const isEditMode = Boolean(expressId);

  const [expressData, setExpressData] = useState({
    expressName: "",
    expressDescription: "",
    expressLogo: null,
    expressProfile: null,
  });

  // Fetch existing express if editing
  useEffect(() => {
    if (isEditMode) {
      fetch(`http://localhost:5000/api/express/${expressId}`)
        .then((res) => res.json())
        .then((data) => {
          setExpressData({
            expressName: data.expressName || "",
            expressDescription: data.expressDescription || "",
            expressLogo: null,   // keep null; user can upload new
            expressProfile: null,
          });
        })
        .catch((err) => console.error("Error fetching express:", err));
    }
  }, [expressId, isEditMode]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setExpressData({ ...expressData, [name]: value });
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    setExpressData({ ...expressData, [name]: files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("expressName", expressData.expressName);
    formData.append("expressDescription", expressData.expressDescription);
    if (expressData.expressLogo) formData.append("expressLogo", expressData.expressLogo);
    if (expressData.expressProfile) formData.append("expressProfile", expressData.expressProfile);

    try {
      const token = localStorage.getItem("token");
      const url = isEditMode
        ? `http://localhost:5000/api/express/update/${expressId}`
        : "http://localhost:5000/api/express/register";

      const method = isEditMode ? "PUT" : "POST";

      const response = await fetch(url, {
        method,
        headers: {
          Authorization: `Bearer ${token}`,
        },
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.text();
        throw new Error(errorData || "Failed to save express");
      }

      //const savedExpress = await response.json();
      alert(isEditMode ? "Express updated successfully!" : "Express added successfully!");
      history.push("/admin/express");
    } catch (error) {
      console.error("Error saving express:", error);
      alert("Failed to save express: " + error.message);
    }
  };

  const handleCancel = () => {
    history.push("/admin/express");
  };

  return (
    <div className="flex flex-wrap">
      <div className="w-full xl:w-8/12 mb-12 xl:mb-0 px-4 mx-auto">
        <div className="relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded-lg bg-white border-0">
          <div className="rounded-t bg-white mb-0 px-6 py-6">
            <div className="text-center flex justify-between">
              <h6 className="text-blueGray-700 text-xl font-bold">
                {isEditMode ? "Edit Express" : "Add Express"}
              </h6>
              <button
                onClick={handleCancel}
                className="bg-red-500 text-white active:bg-red-600 font-bold uppercase text-xs px-4 py-2 rounded shadow hover:shadow-md outline-none focus:outline-none mr-1 ease-linear transition-all duration-150"
                type="button"
              >
                Back
              </button>
            </div>
          </div>

          <form onSubmit={handleSubmit} className="flex-auto px-4 lg:px-10 py-10 pt-0">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Express Name *
                </label>
                <input
                  type="text"
                  name="expressName"
                  value={expressData.expressName}
                  onChange={handleInputChange}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="Enter express name"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Logo {isEditMode ? "(Leave empty to keep existing)" : "*"}
                </label>
                <input
                  type="file"
                  name="expressLogo"
                  onChange={handleFileChange}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  required={!isEditMode}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Bus Profile {isEditMode ? "(Leave empty to keep existing)" : "*"}
                </label>
                <input
                  type="file"
                  name="expressProfile"
                  onChange={handleFileChange}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  required={!isEditMode}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Description *
                </label>
                <textarea
                  name="expressDescription"
                  value={expressData.expressDescription}
                  onChange={handleInputChange}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="Enter express description"
                  required
                />
              </div>
            </div>

            <div className="flex justify-end space-x-4 pt-6 border-t border-gray-200">
              <button
                type="button"
                onClick={handleCancel}
                className="px-6 py-3 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-50 transition-colors"
              >
                Cancel
              </button>
              <button
                type="submit"
                className="px-6 py-3 bg-black text-white rounded-lg hover:bg-gray-700 transition-colors"
              >
                <i className="fas fa-save mr-2"></i>
                {isEditMode ? "Update Express" : "Save Express"}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
