import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import CardTable from "components/Cards/CardTable.js";

export default function Schedules() {
  const history = useHistory();
  const [showForm, setShowForm] = useState(false);
  const [editingSchedule, setEditingSchedule] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [scheduleData, setScheduleData] = useState({
    express: "",
    bus: "",
    destination: "",
    departureTime: "",
    time: "", 
    date: ""
  });
  const [scheduleList, setScheduleList] = useState([
    {
      id: 1,
      express: "Kigali - Huye",
      bus: "Bus 001",
      date: "2024-01-15",
      departureTime: "08:00",
      arrivalTime: "10:30",
      availableSeats: "45",
      price: "2500",
      status: "active"
    },
    {
      id: 2,
      express: "Kigali - Musanze",
      bus: "Bus 002",
      date: "2024-01-15",
      departureTime: "10:00",
      arrivalTime: "12:00",
      availableSeats: "38",
      price: "3000",
      status: "active"
    },
    {
      id: 3,
      express: "Kigali - Kibuye",
      bus: "Bus 003",
      date: "2024-01-15",
      departureTime: "12:00",
      arrivalTime: "14:30",
      availableSeats: "42",
      price: "2800",
      status: "active"
    },
    {
      id: 4,
      express: "Kigali - Rusizi",
      bus: "Bus 004",
      date: "2024-01-15",
      departureTime: "14:00",
      arrivalTime: "16:30",
      availableSeats: "35",
      price: "3200",
      status: "active"
    },
    {
      id: 5,
      express: "Kigali - Huye",
      bus: "Bus 005",
      date: "2024-01-16",
      departureTime: "08:00",
      arrivalTime: "10:30",
      availableSeats: "50",
      price: "2500",
      status: "active"
    },
    {
      id: 6,
      express: "Kigali - Musanze",
      bus: "Bus 006",
      date: "2024-01-16",
      departureTime: "10:00",
      arrivalTime: "12:00",
      availableSeats: "45",
      price: "3000",
      status: "active"
    }
  ]);

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5;

  // Calculate pagination
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  
  // Filter schedule list based on search term
  const filteredScheduleList = scheduleList.filter(schedule =>
    schedule.express.toLowerCase().includes(searchTerm.toLowerCase()) ||
    schedule.bus.toLowerCase().includes(searchTerm.toLowerCase()) ||
    schedule.status.toLowerCase().includes(searchTerm.toLowerCase())
  );
  
  const currentItems = filteredScheduleList.slice(indexOfFirstItem, indexOfLastItem);
  const totalPages = Math.ceil(filteredScheduleList.length / itemsPerPage);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setScheduleData({
      ...scheduleData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editingSchedule) {
      setScheduleList(scheduleList.map(item => 
        item.id === editingSchedule.id ? { ...scheduleData, id: item.id } : item
      ));
      setEditingSchedule(null);
    } else {
      setScheduleList([...scheduleList, { ...scheduleData, id: Date.now() }]);
    }
    setScheduleData({
      express: "",
      bus: "",
      date: "",
      departureTime: "",
      arrivalTime: "",
      availableSeats: "",
      price: "",
      status: "active"
    });
    setShowForm(false);
  };

  const handleEdit = (schedule) => {
    setEditingSchedule(schedule);
    setScheduleData(schedule);
    setShowForm(true);
  };

  const handleDelete = (id) => {
    setScheduleList(scheduleList.filter(item => item.id !== id));
  };

  const handleCancel = () => {
    setShowForm(false);
    setEditingSchedule(null);
    setScheduleData({
      express: "",
      bus: "",
      date: "",
      departureTime: "",
      arrivalTime: "",
      availableSeats: "",
      price: "",
      status: "active"
    });
  };

  const handleAddNew = () => {
    history.push("/admin/schedules/add");
  };

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <>
      {/* Schedules Management */}
      <div className="flex flex-wrap mt-4">
        <div className="w-full xl:w-8/12 mb-12 xl:mb-0 px-4">
          <div className="bg-white rounded-lg shadow-md p-6">
            <div className="flex items-center justify-between mb-6">
              <h1 className="text-3xl font-bold text-gray-800">Schedules Management</h1>
              <div className="flex space-x-3">
                <button
                  onClick={handleAddNew}
                  className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition-colors"
                >
                  <i className="fas fa-plus mr-2"></i>
                  Add New Schedule
                </button>
                <button
                  onClick={() => setShowForm(!showForm)}
                  className="px-4 py-2 border border-gray-300 text-gray-700 rounded-md hover:bg-gray-50 transition-colors"
                >
                  <i className="fas fa-edit mr-2"></i>
                  Quick Add
                </button>
              </div>
            </div>

            {showForm && (
              <div className="mb-6 p-4 border border-gray-200 rounded-lg bg-gray-50">
                <h3 className="text-lg font-semibold mb-4">
                  {editingSchedule ? "Edit Schedule" : "Add New Schedule"}
                </h3>
                <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <select
                    name="express"
                    value={scheduleData.express}
                    onChange={handleInputChange}
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  >
                    <option value="">Select Express Route</option>
                    <option value="Kigali - Huye">Kigali - Huye</option>
                    <option value="Kigali - Musanze">Kigali - Musanze</option>
                    <option value="Kigali - Kibuye">Kigali - Kibuye</option>
                    <option value="Kigali - Rusizi">Kigali - Rusizi</option>
                  </select>
                  <select
                    name="bus"
                    value={scheduleData.bus}
                    onChange={handleInputChange}
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  >
                    <option value="">Select Bus</option>
                    <option value="Bus 001">Bus 001</option>
                    <option value="Bus 002">Bus 002</option>
                    <option value="Bus 003">Bus 003</option>
                    <option value="Bus 004">Bus 004</option>
                  </select>
                  <input
                    type="date"
                    name="date"
                    value={scheduleData.date}
                    onChange={handleInputChange}
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                  <input
                    type="time"
                    name="departureTime"
                    value={scheduleData.departureTime}
                    onChange={handleInputChange}
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                  <input
                    type="time"
                    name="arrivalTime"
                    value={scheduleData.arrivalTime}
                    onChange={handleInputChange}
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                  <input
                    type="number"
                    name="availableSeats"
                    value={scheduleData.availableSeats}
                    onChange={handleInputChange}
                    placeholder="Available Seats"
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                  <input
                    type="number"
                    name="price"
                    value={scheduleData.price}
                    onChange={handleInputChange}
                    placeholder="Price (RWF)"
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                  <select
                    name="status"
                    value={scheduleData.status}
                    onChange={handleInputChange}
                    className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="active">Active</option>
                    <option value="inactive">Inactive</option>
                    <option value="cancelled">Cancelled</option>
                  </select>
                  <div className="md:col-span-3 flex space-x-3">
                    <button
                      type="submit"
                      className="px-4 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition-colors"
                    >
                      {editingSchedule ? "Update" : "Add"}
                    </button>
                    <button
                      type="button"
                      onClick={handleCancel}
                      className="px-4 py-2 border border-gray-300 text-gray-700 rounded-md hover:bg-gray-50 transition-colors"
                    >
                      Cancel
                    </button>
                  </div>
                </form>
              </div>
            )}

            <CardTable>
              <div className="rounded-t mb-0 px-4 py-3 border-0">
                <div className="flex flex-wrap items-center justify-between">
                  <div className="relative w-full px-4 max-w-full flex-grow flex-1">
                    <h3 className="font-semibold text-lg text-blueGray-700">
                      Schedules List
                    </h3>
                  </div>
                  <div className="flex space-x-2">
                    <div className="relative">
                      <input
                        type="text"
                        placeholder="Search schedules..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="px-4 py-2 pl-10 pr-4 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      />
                      <i className="fas fa-search absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400"></i>
                    </div>
                    <button className="px-4 py-2 bg-amber-500 text-white rounded-md hover:bg-amber-600 transition-colors font-medium">
                      <i className="fas fa-filter mr-2"></i>
                      Filter
                    </button>
                  </div>
                </div>
              </div>
              <div className="block w-full overflow-x-auto">
                <table className="items-center w-full bg-transparent border-collapse">
                  <thead>
                    <tr>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Express
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Bus
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Date
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Departure
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Arrival
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Seats
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Price
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Status
                      </th>
                      <th className="px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                        Actions
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    {currentItems.map((schedule) => (
                      <tr key={schedule.id}>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          {schedule.express}
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          {schedule.bus}
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          {schedule.date}
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          {schedule.departureTime}
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          {schedule.arrivalTime}
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          {schedule.availableSeats}
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          RWF {schedule.price}
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          <span className={`px-2 py-1 rounded-full text-xs ${
                            schedule.status === 'active' 
                              ? 'bg-green-100 text-green-800' 
                              : schedule.status === 'inactive'
                              ? 'bg-gray-100 text-gray-800'
                              : 'bg-red-100 text-red-800'
                          }`}>
                            {schedule.status}
                          </span>
                        </td>
                        <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                          <div className="flex space-x-2">
                            <button
                              onClick={() => handleEdit(schedule)}
                              className="px-3 py-1 bg-indigo-600 text-white rounded text-xs hover:bg-indigo-700 font-medium"
                            >
                              <i className="fas fa-edit mr-1"></i>
                              Edit
                            </button>
                            <button
                              onClick={() => handleDelete(schedule.id)}
                              className="px-3 py-1 bg-red-600 text-white rounded text-xs hover:bg-red-700 font-medium"
                            >
                              <i className="fas fa-trash mr-1"></i>
                              Delete
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              
              {/* Pagination */}
              {totalPages > 1 && (
                <div className="px-6 py-3 border-t border-gray-200">
                  <div className="flex items-center justify-between">
                    <div className="text-sm text-gray-700">
                      Showing {indexOfFirstItem + 1} to {Math.min(indexOfLastItem, filteredScheduleList.length)} of {filteredScheduleList.length} results
                    </div>
                    <div className="flex space-x-2">
                      <button
                        onClick={() => handlePageChange(currentPage - 1)}
                        disabled={currentPage === 1}
                        className={`px-3 py-1 rounded-md text-sm font-medium ${
                          currentPage === 1
                            ? 'bg-gray-200 text-gray-500 cursor-not-allowed'
                            : 'bg-indigo-600 text-white hover:bg-indigo-700'
                        }`}
                      >
                        <i className="fas fa-chevron-left mr-1"></i>
                        Previous
                      </button>
                      
                      {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => (
                        <button
                          key={page}
                          onClick={() => handlePageChange(page)}
                          className={`px-3 py-1 rounded-md text-sm font-medium ${
                            currentPage === page
                              ? 'bg-indigo-600 text-white'
                              : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                          }`}
                        >
                          {page}
                        </button>
                      ))}
                      
                      <button
                        onClick={() => handlePageChange(currentPage + 1)}
                        disabled={currentPage === totalPages}
                        className={`px-3 py-1 rounded-md text-sm font-medium ${
                          currentPage === totalPages
                            ? 'bg-gray-200 text-gray-500 cursor-not-allowed'
                            : 'bg-indigo-600 text-white hover:bg-indigo-700'
                        }`}
                      >
                        Next
                        <i className="fas fa-chevron-right ml-1"></i>
                      </button>
                    </div>
                  </div>
                </div>
              )}
            </CardTable>
          </div>
        </div>
      </div>
    </>
  );
}
