import React from "react";
import Chart from "chart.js";

export default function CardLineChart({ chartData, title = "Revenue" }) {
  React.useEffect(() => {
    const dataToUse = chartData && chartData.labels && chartData.labels.length > 0 
      ? chartData 
      : { labels: [], datasets: [] };

    var config = {
      type: "line",
      data: dataToUse,
      options: {
        maintainAspectRatio: false,
        responsive: true,
        title: {
          display: false,
          text: title,
          fontColor: "white",
        },
        legend: {
          labels: {
            fontColor: "white",
          },
          align: "end",
          position: "bottom",
        },
        tooltips: {
          mode: "index",
          intersect: false,
          callbacks: {
            label: function(tooltipItem, data) {
              return data.datasets[tooltipItem.datasetIndex].label + ': ' + 
                tooltipItem.yLabel.toLocaleString() + (title === "Revenue" ? ' RWF' : '');
            }
          }
        },
        hover: {
          mode: "nearest",
          intersect: true,
        },
        scales: {
          xAxes: [
            {
              ticks: {
                fontColor: "rgba(255,255,255,.7)",
              },
              display: true,
              scaleLabel: {
                display: false,
                labelString: "Date",
                fontColor: "white",
              },
              gridLines: {
                display: false,
                borderDash: [2],
                borderDashOffset: [2],
                color: "rgba(33, 37, 41, 0.3)",
                zeroLineColor: "rgba(0, 0, 0, 0)",
                zeroLineBorderDash: [2],
                zeroLineBorderDashOffset: [2],
              },
            },
          ],
          yAxes: [
            {
              ticks: {
                fontColor: "rgba(255,255,255,.7)",
                beginAtZero: true,
                callback: function(value) {
                  return value.toLocaleString() + (title === "Revenue" ? ' RWF' : '');
                }
              },
              display: true,
              scaleLabel: {
                display: false,
                labelString: title,
                fontColor: "white",
              },
              gridLines: {
                borderDash: [3],
                borderDashOffset: [3],
                drawBorder: false,
                color: "rgba(255, 255, 255, 0.15)",
                zeroLineColor: "rgba(33, 37, 41, 0)",
                zeroLineBorderDash: [2],
                zeroLineBorderDashOffset: [2],
              },
            },
          ],
        },
      },
    };

    var ctx = document.getElementById("line-chart").getContext("2d");

    // Destroy previous chart if it exists
    if (window.myLine) {
      window.myLine.destroy();
    }

    window.myLine = new Chart(ctx, config);

    // Cleanup function
    return () => {
      if (window.myLine) {
        window.myLine.destroy();
      }
    };
  }, [chartData, title]);

  return (
    <>
      <div className="relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded bg-blueGray-700">
        <div className="rounded-t mb-0 px-4 py-3 bg-transparent">
          <div className="flex flex-wrap items-center">
            <div className="relative w-full max-w-full flex-grow flex-1">
              <h6 className="uppercase text-blueGray-100 mb-1 text-xs font-semibold">
                Overview
              </h6>
              <h2 className="text-white text-xl font-semibold">{title}</h2>
            </div>
          </div>
        </div>
        <div className="p-4 flex-auto">
          {/* Chart */}
          <div className="relative h-350-px">
            <canvas id="line-chart"></canvas>
          </div>
        </div>
      </div>
    </>
  );
}
